css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr") {

/*Zero Familiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/0ad117ae07cb25a92429e7f354c96624/tumblr_omxh6pSeKe1trxrtqo3_500.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/e5af6c5ad44e90374867f07ed032c7a7/tumblr_omxh6pSeKe1trxrtqo2_500.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Zero Kiryuu" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Deja de provocarle, ¡te comera entera!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Vampiro" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/5a3bed0ab88a027cced77c5137095ea5/tumblr_omxh6pSeKe1trxrtqo1_75sq.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Tabletas de Sangre" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("https://68.media.tumblr.com/1e58d566da9ee673ae4226ac749fb25a/tumblr_omxmhdCOH21trxrtqo1_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 560px !important ;
    width : 250px !important ;
  }
.background-element {
  	  background: url('https://68.media.tumblr.com/9e5416d337a8e854ebea58cc929e10bb/tumblr_omxjruRRc91trxrtqo1_1280.jpg') !important;
  }

#index-player-display {
   	  background: url('https://68.media.tumblr.com/1e58d566da9ee673ae4226ac749fb25a/tumblr_omxmhdCOH21trxrtqo1_1280.png') !important;
    
}
  #player-display-pet > img {display : none !important ;}
}
