    color: #8c8f8c !important;
	}
	.ac-title[selected]	{
    font-size: 13px;
    font-weight: 400;
    color: #fff !important;
	}
	/*URL Color*/
	.ac-url-text	{
   color: #40769d !important;
	}
  .ac-url-text[selected]{
    color: #6090b2 !important;
  }
	/*From bookmark item*/
	.autocomplete-richlistitem[type="bookmark"]	{
	font-weight: bold !important;
	background: #232F3C !important;
	}
	/*Found text what you typed*/
	.ac-emphasize-text	{
  border-bottom: 1px solid;
  font-weight: 600 !important;
	}

/* TABS */

.theme-fg-color1{
  color: var(--theme-highlight-green)!important;
}
.theme-fg-color2{
  color: var(--theme-highlight-blue)!important;
}
.theme-fg-color3{
  color: var(--theme-highlight-purple)!important;
}
.theme-fg-color5{
  color: var(--theme-highlight-lightorange)!important;
}
.theme-fg-color6{
  color: var(--theme-highlight-orange)!important;
}

/* DEFAULT MOZILLA STUFF */

:root, :root.theme-dark {
  --main-tab-active-bg-color: #1A4666;
  --main-tab-active-hover-bg-color: #1A4666;
  --main-tab-active-text-color: #FFFFFF;
  --main-tab-inactive-bg-color: #1C2126;
  --main-tab-inactive-hover-bg-color: #07090a;
  --main-tab-inactive-text-color: #FFFFFF;

  --theme-body-background: #14171a;
  --theme-sidebar-background: #181d20;
  --theme-contrast-background: #b28025;

  --theme-tab-toolbar-background: #272b35;
  --theme-toolbar-background: #252c33;
  --theme-selection-background: rgba(42, 80, 112, 0.6);
  --theme-selection-background-semitransparent: rgba(29, 50, 68, 0.5);
  --theme-selection-color: #f5f7fa;
  --theme-splitter-color: #222222;
  --theme-comment: #5c6773;

  --theme-body-color: #8fa1b2;
  --theme-body-color-alt: #D6D6D6;
  --theme-body-color-inactive: #8fa1b2;
  --theme-content-color1: #a9bacb;
  --theme-content-color2: #8fa1b2;
  --theme-content-color3: #667380;

  --theme-highlight-green: #6BC14C;
  --theme-highlight-blue: #46afe3;
  --theme-highlight-bluegrey: #5e88b0;
  --theme-highlight-purple: #D87AFF;
  --theme-highlight-lightorange: #d99b28;
  --theme-highlight-orange: #d96629;
  --theme-highlight-red: #eb5368;
  --theme-highlight-pink: #df80ff;
  --theme-highlight-gray: #e9f4fe;

  /* For accessibility purposes we want to enhance the focus styling. This
  * should improve keyboard navigation usability. */
  --theme-focus-outline-color: #ced3d9;

  /* Colors used in Graphs, like performance tools. Mostly similar to some "highlight-*" colors. */
  --theme-graphs-green: #70bf53;
  --theme-graphs-blue: #46afe3;
  --theme-graphs-bluegrey: #5e88b0;
  --theme-graphs-purple: #df80ff;
  --theme-graphs-yellow: #d99b28;
  --theme-graphs-orange: #d96629;
  --theme-graphs-red: #eb5368;
  --theme-graphs-grey: #757873;
  --theme-graphs-full-red: #f00;
  --theme-graphs-full-blue: #00f;

  /* Images */
  /*--theme-pane-collapse-image: url(chrome://devtools/skin/images/pane-collapse.svg);
  --theme-pane-expand-image: url(chrome://devtools/skin/images/pane-expand.svg);*/

  /* Tooltips */
  --theme-tooltip-border: #434850;
  --theme-tooltip-background: rgba(19, 28, 38, .9);
  --theme-tooltip-shadow: rgba(25, 25, 25, 0.76);

  /* Command line */
  /*--theme-command-line-image: url(chrome://devtools/skin/images/commandline-icon.svg#dark-theme);
  --theme-command-line-image-focus: url(chrome://devtools/skin/images/commandline-icon.svg#dark-theme-focus);*/
}

/* TABS */


:root.theme-dark {
  --theme-body-background: #14171a !important;
  --theme-sidebar-background: #181d20 !important;
  --theme-contrast-background: #b28025 !important;

  --theme-tab-toolbar-background: #252c33 !important;
  --theme-toolbar-background: #343c45 !important;
  --theme-selection-background: #1d4f73 !important;
  --theme-selection-background-semitransparent: rgba(29, 79, 115, .5) !important;
  --theme-selection-color: #f5f7fa !important;
  --theme-splitter-color: black !important;
  --theme-comment: #757873 !important;

  --theme-body-color: #8fa1b2 !important;
  --theme-body-color-alt: #b6babf !important;
  --theme-body-color-inactive: #8fa1b2 !important;
  --theme-content-color1: #a9bacb !important;
  --theme-content-color2: #8fa1b2 !important;
  --theme-content-color3: #5f7387 !important;

  --theme-highlight-green: #70bf53 !important;
  --theme-highlight-blue: #46afe3 !important;
  --theme-highlight-bluegrey: #5e88b0 !important;
  --theme-highlight-purple: #6b7abb !important;
  --theme-highlight-lightorange: #d99b28 !important;
  --theme-highlight-orange: #d96629 !important;
  --theme-highlight-red: #eb5368 !important;
  --theme-highlight-pink: #df80ff !important;

  /* Colors used in Graphs, like performance tools. Mostly similar to some "highlight-*" colors. */
  --theme-graphs-green: #70bf53 !important;
  --theme-graphs-blue: #46afe3 !important;
  --theme-graphs-bluegrey: #5e88b0 !important;
  --theme-graphs-purple: #df80ff !important;
  --theme-graphs-yellow: #d99b28 !important;
  --theme-graphs-orange: #d96629 !important;
  --theme-graphs-red: #eb5368 !important;
  --theme-graphs-grey: #757873 !important;
  --theme-graphs-full-red: #f00 !important;
  --theme-graphs-full-blue: #00f !important;

  /* Images */
  --theme-pane-collapse-image: url(chrome://devtools/skin/images/pane-collapse.svg);
  --theme-pane-expand-image: url(chrome://devtools/skin/images/pane-expand.svg);
}


