body {
  margin: 0 !important;
}

.theme-body {
  background: var(--theme-body-background) !important;
  color: var(--theme-body-color) !important;
}

.theme-sidebar {
  background: var(--theme-sidebar-background) !important;
  color: var(--theme-content-color1) !important;
}

::-moz-selection {
  background-color: var(--theme-selection-background) !important;
  color: var(--theme-selection-color) !important;
}

.theme-bg-darker {
  background-color: var(--theme-selection-background-semitransparent) !important;
}

.theme-selected,
.CodeMirror-hint-active {
  background-color: var(--theme-selection-background) !important;
  color: var(--theme-selection-color) !important;
}

.theme-bg-contrast,
.variable-or-property:not([overridden])[changed] {
  background: var(--theme-contrast-background) !important;
}

.theme-link,
.cm-s-mozilla .cm-link {
  color: var(--theme-highlight-blue) !important;
}

/*
 * FIXME: http://bugzil.la/575675 CSS links without :visited set cause assertion
 * failures in debug builds.
 */
.theme-link:visited,
.cm-s-mozilla .cm-link:visited,
.CodeMirror-Tern-type {
  color: var(--theme-highlight-blue) !important;
}


.theme-comment,
.cm-s-mozilla .cm-meta,
.cm-s-mozilla .cm-hr,
.cm-s-mozilla .cm-comment,
.variable-or-property .token-undefined,
.variable-or-property .token-null,
.CodeMirror-Tern-completion-unknown:before {
  color: var(--theme-comment) !important;
}

.theme-gutter {
  background-color: var(--theme-tab-toolbar-background) !important;
  color: var(--theme-content-color3) !important;
  border-color: var(--theme-splitter-color) !important;
}

.theme-separator {
  border-color: var(--theme-splitter-color) !important;
}

.theme-fg-color1,
.cm-s-mozilla .cm-number,
.variable-or-property .token-number,
.variable-or-property[return] > .title > .name,
.variable-or-property[scope] > .title > .name {
  color: var(--theme-highlight-green) !important;
}

.CodeMirror-Tern-completion-number:before {
  background-color: #5c9966 !important;
}

.theme-fg-color2,
.cm-s-mozilla .cm-attribute,
.cm-s-mozilla .cm-variable,
.cm-s-mozilla .cm-def,
.cm-s-mozilla .cm-property,
.cm-s-mozilla .cm-qualifier,
.variables-view-variable > .title > .name {
  color: var(--theme-highlight-blue) !important;
}

.CodeMirror-Tern-completion-object:before {
  background-color: #3689b2 !important;
}

.cm-s-mozilla .cm-unused-line {
  text-decoration: line-through !important;
  text-decoration-color: #0072ab !important;
}

.cm-s-mozilla .cm-executed-line {
  background-color: #133c26 !important;
}

.theme-fg-color3,
.cm-s-mozilla .cm-builtin,
.cm-s-mozilla .cm-tag,
.cm-s-mozilla .cm-header,
.cm-s-mozilla .cm-bracket,
.variables-view-property > .title > .name {
  color: var(--theme-highlight-pink) !important;
}

.CodeMirror-Tern-completion-array:before {
  background-color: var(--theme-highlight-bluegrey) !important;
}

.theme-fg-color4 {
  color: var(--theme-highlight-purple) !important;
}

.theme-fg-color5,
.cm-s-mozilla .cm-keyword {
  color: var(--theme-highlight-lightorange) !important;
}

.theme-fg-color6,
.cm-s-mozilla .cm-string,
.cm-s-mozilla .cm-string-2,
.variable-or-property .token-string,
.CodeMirror-Tern-farg {
  color: var(--theme-highlight-orange) !important;
}

.CodeMirror-Tern-completion-string:before,
.CodeMirror-Tern-completion-fn:before {
  background-color: #b26b47 !important;
}

.theme-fg-color7,
.cm-s-mozilla .cm-atom,
.cm-s-mozilla .cm-quote,
.cm-s-mozilla .cm-error,
.variable-or-property .token-boolean,
.variable-or-property .token-domnode,
.variable-or-property[exception] > .title > .name {
  color: var(--theme-highlight-red) !important;
}

.CodeMirror-Tern-completion-bool:before {
  background-color: #bf5656 !important;
}

.variable-or-property .token-domnode {
  font-weight: bold !important;
}

.theme-toolbar,
.devtools-toolbar,
.devtools-sidebar-tabs tabs,
.devtools-sidebar-alltabs,
.cm-s-mozilla .CodeMirror-dialog { /* General toolbar styling */
  color: var(--theme-body-color-alt) !important;
  background-color: var(--theme-toolbar-background) !important;
  border-color: hsla(210,8%,5%,.6) !important;
}

.theme-fg-contrast { /* To be used for text on theme-bg-contrast */
  color: black !important;
}

.ruleview-swatch,
.computedview-colorswatch {
  box-shadow: 0 0 0 1px #818181 !important;
}

/* CodeMirror specific styles.
 * Best effort to match the existing theme, some of the colors
 * are duplicated here to prevent weirdness in the main theme. */

.CodeMirror.cm-s-mozilla  { /* Inherit platform specific font sizing and styles */
  font-family: inherit !important;
  font-size: inherit !important;
  background: transparent !important;
}

.CodeMirror.cm-s-mozilla  pre,
.cm-s-mozilla .cm-variable-2,
.cm-s-mozilla .cm-variable-3,
.cm-s-mozilla .cm-operator,
.cm-s-mozilla .cm-special {
  color: var(--theme-content-color1) !important;
}

.cm-s-mozilla .CodeMirror-lines .CodeMirror-cursor {
  border-left: solid 1px #fff !important;
}

.cm-s-mozilla.CodeMirror-focused .CodeMirror-selected { /* selected text (focused) */
  background: rgb(185, 215, 253) !important;
}

.cm-s-mozilla .CodeMirror-selected { /* selected text (unfocused) */
  background: rgb(176, 176, 176) !important;
}

.cm-s-mozilla .CodeMirror-activeline-background { /* selected color with alpha */
  background: rgba(185, 215, 253, .15) !important;
}

div.cm-s-mozilla span.CodeMirror-matchingbracket { /* highlight brackets */
  outline: solid 1px rgba(255, 255, 255, .25) !important;
  color: white !important;
}

/* Highlight for a line that contains an error. */
div.CodeMirror div.error-line {
  background: rgba(255,0,0,0.2) !important;
}

/* Generic highlighted text */
div.CodeMirror span.marked-text {
  background: rgba(255,255,0,0.2) !important;
  border: 1px dashed rgba(192,192,0,0.6) !important;
  -moz-margin-start: -1px !important;
  -moz-margin-end: -1px !important;
}

/* Highlight for evaluating current statement. */
div.CodeMirror span.eval-text {
  background-color: #556 !important;
}

.cm-s-mozilla .CodeMirror-linenumber { /* line number text */
  color: var(--theme-content-color3) !important;
}

.cm-s-mozilla .CodeMirror-gutters { /* vertical line next to line numbers */
  border-right-color: var(--theme-toolbar-background) !important;
  background-color: var(--theme-sidebar-background) !important;
}

.cm-s-markup-view pre {
  line-height: 1.4em !important;
  min-height: 1.4em !important;
}

/* Twisty and checkbox controls */
.theme-twisty, .theme-checkbox {
  width: 14px !important;
  height: 14px !important;
  background-repeat: no-repeat !important;
  background-image: url("chrome://devtools/skin/images/controls.png") !important;
  background-size: 56px 28px !important;
}

.theme-twisty {
  cursor: pointer !important;
  background-position: -28px -14px !important;
}

.theme-twisty:-moz-focusring {
  outline-style: none !important;
}

.theme-twisty[open], .theme-twisty.open  {
  background-position: -42px -14px !important;
}

.theme-twisty[invisible] {
  visibility: hidden !important;
}

.theme-checkbox {
  display: inline-block !important;
  border: 0 !important;
  padding: 0 !important;
  outline: none !important;
  background-position: -28px 0 !important;
}

.theme-checkbox[checked] {
  background-position: -42px 0 !important;
}

@media (min-resolution: 1.1dppx) {
  .theme-twisty, .theme-checkbox {
    background-image: url("chrome://devtools/skin/images/controls@2x.png") !important;
  }
}

/* XUL panel styling (see devtools/client/shared/widgets/Tooltip.js) */

.theme-tooltip-panel .panel-arrowcontent {
  padding: 5px !important;
  background: rgba(19, 28, 38, .9) !important;
  border-radius: 5px !important;
  box-shadow: none !important;
  border: 3px solid #434850 !important;
}

/* Overring panel arrow images to fit with our light and dark themes */

.theme-tooltip-panel .panel-arrow {
  --arrow-margin: -4px !important;
}

:root[platform="win"] .theme-tooltip-panel .panel-arrow {
  --arrow-margin: -7px !important;
}

.theme-tooltip-panel .panel-arrow[side="top"],
.theme-tooltip-panel .panel-arrow[side="bottom"] {
  list-style-image: url("chrome://devtools/skin/tooltip/arrow-vertical-dark.png") !important;
  /* !important is needed to override the popup.css rules in toolkit/themes */
  width: 39px !important;
  height: 16px !important;
}

.theme-tooltip-panel .panel-arrow[side="left"],
.theme-tooltip-panel .panel-arrow[side="right"] {
  list-style-image: url("chrome://devtools/skin/tooltip/arrow-horizontal-dark.png") !important;
  /* !important is needed to override the popup.css rules in toolkit/themes */
  width: 16px !important;
  height: 39px !important;
}

.theme-tooltip-panel .panel-arrow[side="top"] {
  margin-bottom: var(--arrow-margin) !important;
}

.theme-tooltip-panel .panel-arrow[side="bottom"] {
  margin-top: var(--arrow-margin) !important;
}

.theme-tooltip-panel .panel-arrow[side="left"] {
  margin-right: var(--arrow-margin) !important;
}

.theme-tooltip-panel .panel-arrow[side="right"] {
  margin-left: var(--arrow-margin) !important;
}

@media (min-resolution: 1.1dppx) {
  .theme-tooltip-panel .panel-arrow[side="top"],
  .theme-tooltip-panel .panel-arrow[side="bottom"] {
    list-style-image: url("chrome://devtools/skin/tooltip/arrow-vertical-dark@2x.png") !important;
  }

  .theme-tooltip-panel .panel-arrow[side="left"],
  .theme-tooltip-panel .panel-arrow[side="right"] {
    list-style-image: url("chrome://devtools/skin/tooltip/arrow-horizontal-dark@2x.png") !important;
  }
}

.theme-tooltip-panel .devtools-tooltip-simple-text {
  color: white !important;
  border-bottom: 1px solid #434850 !important;
}

.theme-tooltip-panel .devtools-tooltip-simple-text:last-child {
  border-bottom: 0 !important;
}

.devtools-textinput,
.devtools-searchinput {
  background-color: rgba(24, 29, 32, 1) !important;
  color: rgba(184, 200, 217, 1) !important;
}

.CodeMirror-Tern-fname {
  color: #f7f7f7 !important;
}

.CodeMirror-hints,
.CodeMirror-Tern-tooltip {
  box-shadow: 0 0 4px rgba(255, 255, 255, .3) !important;
  background-color: #0f171f !important;
  color: var(--theme-body-color) !important;
}

#inspector-breadcrumbs {
  display: flex;
  background-color: var(--theme-body-background) !important;
}
