@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("elearning.tgm.ac.at") {

    
  /* BACKGROUND */

  body:after {
      content: "";
      width: 100%;
    height: 100%;
      background:red url("http://gencept.com/wp-content/uploads/2016/04/Wallpaper-of-the-Week-25-Material-Design-Inspired-Wallpapers_24-@-GenCept.png");
      background-size: cover;
      top: 0px;
    left: 0px;
      position: absolute;
      display: inline-block;
      opacity:0.0;
      z-index: -10;
  }


  /* TOP NAV BAR */

  .navbar {
      border: 0px;
      border-radius: 0px;
      box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  }


  /* BLOCKS */

  .block {
      border: 0px;
      border-radius: 0px;
      box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
      /*box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);*/
  }
  .coursebox {
      border-bottom: 1px solid #d3d3d3;
      border-top: 0px;
      border-left: 0px;
      border-right: 0px;
      border-radius: 0px;
  }
  .coursebox:last-child {
      border-bottom: 0px;
  }
  .block_course_overview {
      border: 0px;
  }
  .header {
      border: 0px;
  }
  .row-fluid section {
      border-radius: 0px;
  }
  section {
      border-radius: 0px;
  }
  #page #page-content #region-main {
      border: 0px;
      border-radius: 0px;
      box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  }
  .block_course_overview {
      box-shadow: 0 0px 0px rgba(0,0,0,0.0), 0 0px 0px rgba(0,0,0,0.0);
  }
  .course-content ul li.section.main.current {
      border: 0px;
      border-radius: 0px;
      background-color: #eaeaea;
      box-shadow: 0px 0px 0px 0px #1EA4C9
  }
    
  /* NAV MENU */
  /*
  #block-region-side-pre,
  .span4,
  .desktop-first-column {
    position: fixed;
    width: 22%;
  }
  .breadcrumb,
  .style1 {
    display: none !important;
  }
  #block-region-side-post {
    position: fixed;
    right: 5px;
  }
  .breadcrumb-button {
    display: none !important;
  }
  */  
    /* LOGO */
    a.logo {
        visibility: hidden;
    }
    #page {
      position: relative;
      top: -55px;
    }
    
  /* DROPDOWN */
    .dropdown-menu {
        border: 0px;
        border-radius: 0px;
        box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
    }
    
    /* LOGIN */
    .loginbox .loginform .form-input input {
        border-radius: 0px;
    }
    input[id=loginbtn]:hover {
      -webkit-animation-name: hvr-wobble-top;
      animation-name: hvr-wobble-top;
      -webkit-animation-duration: 1s;
      animation-duration: 1s;
      -webkit-animation-timing-function: ease-in-out;
      animation-timing-function: ease-in-out;
      -webkit-animation-iteration-count: 1;
      animation-iteration-count: 1;
    }
    @keyframes example {
        0%   {margin-left: 0%;}
        100% {margin-left: 100%;}
    }
    
    /* Wobble Top */
@-webkit-keyframes hvr-wobble-top {
  16.65% {
    -webkit-transform: skew(-12deg);
    transform: skew(-12deg);
  }

  33.3% {
    -webkit-transform: skew(10deg);
    transform: skew(10deg);
  }

  49.95% {
    -webkit-transform: skew(-6deg);
    transform: skew(-6deg);
  }

  66.6% {
    -webkit-transform: skew(4deg);
    transform: skew(4deg);
  }

  83.25% {
    -webkit-transform: skew(-2deg);
    transform: skew(-2deg);
  }

  100% {
    -webkit-transform: skew(0);
    transform: skew(0);
  }
}

@keyframes hvr-wobble-top {
  16.65% {
    -webkit-transform: skew(-12deg);
    transform: skew(-12deg);
  }

  33.3% {
    -webkit-transform: skew(10deg);
    transform: skew(10deg);
  }

  49.95% {
    -webkit-transform: skew(-6deg);
    transform: skew(-6deg);
  }

  66.6% {
    -webkit-transform: skew(4deg);
    transform: skew(4deg);
  }

  83.25% {
    -webkit-transform: skew(-2deg);
    transform: skew(-2deg);
  }

  100% {
    -webkit-transform: skew(0);
    transform: skew(0);
  }
}
}
