css:
@-moz-document regexp(".*\\.atlassian\\.net.*RapidBoard.*") {
/* JIRA BACKLOG VIEW */
.ghx-backlog-container {
	background-color: #f5f5f5;
}

.ghx-backlog-container .ghx-marker {
	background-color: #f5f5f5;
}

.ghx-backlog-container .ghx-backlog-card {
	width: 250px;
	display: inline-block;
	margin-right: 0.5rem;
	margin-bottom: 0.5rem;
	font-size: 0.8rem;
	border-radius: 0.2rem;
}

.ghx-backlog-container .ghx-backlog-card .ghx-plan-extra-fields {
	display: none;
}

.ghx-backlog-container .ghx-backlog-card .ghx-plan-main-fields {
	height: 3.5rem;
	margin-top: 1.3rem;
	margin-right: 0.3rem;
}

.ghx-backlog-container .ghx-backlog-card .ghx-items-container {
	position: static !important;
	margin: 2px !important;
}

.ghx-backlog-container .ghx-backlog-card .ghx-type.items-spacer {
	float: left;
	margin-top: -1.5rem;
}

.ghx-backlog-container .ghx-backlog-card .ghx-backlog-card-expander-spacer {
	width: 10px;	
}

.ghx-backlog-container .ghx-backlog-card .ghx-avatar-img {
	width: 16px;
	height: 16px;
}

.ghx-backlog-container .ghx-backlog-card .ghx-summary {
	white-space: normal;
	height: 3.5rem;
	width: 230px;
	text-transform: lowercase;
}

.ghx-backlog-container .ghx-backlog-card .aui-lozenge {
	position: absolute;
	top: 0rem;
	left: 1rem;
	right: 0;
	height: 1rem;
	font-size: 0.8rem;
	min-width: 100%;
	border-radius: 0;
	opacity: 0.8;
}

.ghx-backlog-container .ghx-backlog-card .ghx-label-version {
	position: absolute;
	bottom: 0rem;
	right: 0rem;
	margin: 0;
	border-radius: 0;
	border-right: none;
	border-bottom: none;
}

.ghx-backlog-container .ghx-backlog-card .ghx-extra-field {
	flex: none;	
}

/* JIRA BOARD VIEW */
.ghx-column-headers .ghx-column h2 {
	font-size: 0.8rem;
	color: #666;
}
.ghx-column-headers,
.ghx-columns {
	border-spacing: 0;
	padding-left: 1rem;
}
.ghx-parent-group {
	margin: 0;
}
.ghx-issue,
.ghx-parent-group {
	margin: 0 0 0.5rem 0 !important;
	border-radius: 0;
	padding-bottom: 0.1rem;
}
.ghx-row.ghx-highlighted-field .aui-lozenge {
	position: absolute;
	top: 0rem;
	left: 0;
	right: 0;
	height: 0.1rem;
	padding: 0;
	border-radius: 0;
}
.ghx-issue .aui-label {
	border-radius: 0;
	width: 100%;
	color: transparent !important;
}
.ghx-issue .ghx-summary {
	padding-top: 0rem;
	max-height: 10rem;
	font-size: 0.8rem !important;
	line-height: 1rem !important;
	text-transform: lowercase;
}
.ghx-xtra-narrow-card .ghx-issue .ghx-summary {
	padding-top: 0rem;
}
.ghx-issue .ghx-estimate {
 	position: absolute;
	background-color: #f5f5f5;
	left: 0rem;
	border-top: 1px solid #ddd;
	margin-top: .25rem;
	border-radius: 0;
	border-right: 1px solid #ddd;
}
.ghx-narrow-card .ghx-issue .ghx-estimate {
	right: 0rem;
	left: inherit;
	border-left: 1px solid #ddd;
	border-right: none;
	margin-top: 0rem;
}
.ghx-xtra-narrow-card .ghx-issue .ghx-estimate {
	display: none
}
.ghx-stat-fields .ghx-field-icon {
	display: none;
}
.ghx-field-icon[data-tooltip="High priority"],
.ghx-field-icon[data-tooltip="Critical priority"],
.ghx-field-icon[data-tooltip="Blocker priority"] {
	display: none;
}
.ghx-row.ghx-stat-1 {
	height: 0;
	margin: 0 !important;
}
.ghx-row.ghx-stat-2 {
	margin-top: 0 !important;
}
.ghx-xtra-narrow-card .ghx-row.ghx-stat-2 {
	margin-top: 8px !important;
}
.ghx-issue .ghx-avatar-img {
	height: 18px;
	width: 18px;
	line-height: 18px;
}
.ghx-xtra-narrow-card .ghx-row.ghx-stat-1 {
	height: 1.7rem;
	float: right;
	width: 25% !important;
	margin-right: 0;
}
.ghx-xtra-narrow-card .ghx-row.ghx-stat-2 {
	width: 70% !important;
}
.ghx-xtra-narrow-card .ghx-key {
	margin-top: 0.2rem;
}
.ghx-narrow-card .ghx-key {
	margin-top: -0.2rem;
}
.ghx-issue .ghx-key {
	font-size: 0.7rem;
}
}
