css:
@-moz-document domain("v3rmillion.net") {
/* ===Logo=== */
#header {
    background: url(http://i.imgur.com/29dJA8D.png); /* You can change the URL here to an image of your choosing. */
    margin: 0px auto 0 auto;
    text-align: left;
    background-repeat: no-repeat;
    background-position: center;
    background-color: #1a1a1a
}
#logo img {
    max-height: 140px;
    visibility: hidden;
}

/* ===Navigation bar=== */
#bridge {
    background: #202020;
    height: 45px;
    border-bottom: 0px solid #000;
}
ul.menu li a.active {
    background: #151515 !important;
    color: #fff !important;
}
ul.menu li ul {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #2a2a2a;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 0px 0px;
    border-top: 0px solid #cd1818;
}
.panel_buttons {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #2a2a2a;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 0px 0px;
    border-top: 0px solid #cd1818;
    text-align: left;
}
.pm_new, .alert_new {
    background: rgba(99, 99, 99, 0.78) !important;
    display: inline !important;
}
				/* -Forum Navigation- */
.navigation {
    background: #202020;
    border: 0px solid #2a2a2a;
    margin: auto auto 20px auto;
    padding: 10px;
    color: #fff;
    font-size: 14px;
    text-align: left;
}
				/* -Notification Pop Up */
.tfoot {
    padding: 10px;
    background: #1f1f1f;
    color: #e0e0e0;
}
.modal a.close-modal {
    position: absolute;
    top: -12.5px;
    right: -12.5px;
    display: block;
    width: 30px;
    height: 30px;
    text-indent: -9999px;
    background: url(http://google.com/) no-repeat 0 0;
    visibility: hidden;
}
/* ===Content=== */
.thead {
    background: #202020;
    color: #fff;
    padding: 10px;
}
#content {
    background: rgba(32, 32, 32, 0);
    border: 0px solid #2a2a2a;
    width: auto !important;
    padding: 20px;
    padding-bottom: 0px !important;
    margin-bottom: 40px;
    overflow: hidden;
}
div#copyright:before {
    content: 'Theme made by Siren.';
    opacity: 1
}
#container {
    width: 100%;
    text-align: left;
    line-height: 1.4;
    margin: 0 auto;
}
.tcat {
    background: #1b1b1b;
    color: #fff;
    padding: 6px 10px;
    font-size: 12px;
}
.forum_on {
    color: #3a3a3a;
}
/* ===Footer=== */
#footer {
    position: relative;
    background: #101010;
    border-top: 0px solid #cd1818;
    color: #fff;
    clear: both;
    display: none;
}
/* ===Buttons */
button, input.button {
    background: #202020;
    border: 0px solid #cd1818;
    padding: 5px 8px;
    color: #fff;
    font-size: 14px;
    font-family: Open Sans, Tahoma, Verdana, Arial, Sans-Serif;
    cursor: pointer;
    outline: 0;
}
a.button:link, a.button:visited, a.button:active {
    background: #202020;
    color: #fff;
    display: inline-block;
    padding: 4px 8px;
    margin: 2px 2px 6px 2px;
    border: 0px solid #cd1818;
    font-size: 14px;
    font-weight: 100;
}
a.button:hover {
    background: #303030;
    border-color: #303030;
    color: #fff;
}
.pagination a {
    background: #303030;
    border: 0px solid #cd1818;
    color: #fff;
}
.inline_row a:visited {
    color: #ffffff;
    font-size: 90%;
}
.inline_row a {
    color: #ffffff;
    font-size: 90%;
}
.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active {
    display: inline-block;
    padding: 5px;
    margin: 2px;
    font-size: 12px;
    background: #404040;
    border: 0px solid #cd1818;
    color: #fff;
    font-weight: 700;
}
.buttons {
    background-color: #101010;
    border: 0px solid #101010;
    padding: 6px;
    display: inline-block;
    border-radius: 3px;
}
.buttons:hover {
    background-color: #101010;
    border: 2px solid #101010;
    padding: 6px;
    display: inline-block;
    border-radius: 3px;
}
}
