css:
@-moz-document domain("roblox.com"), domain("forums.roblox.com") {


span.robux, div.robux {
    background: url(http://i.imgur.com/D6AESU6.png) no-repeat 0 1px;
    color: #060;
    font-weight: bold;
    padding: 0 0 2px 20px;
    font-size: 12px;
}



.rbx-header {
    background-color: #bd0000;
    font-size: 16px;
    height: 80px;
    box-shadow: 0 5px 25px 0 #191919;
}

.icon-default-logo, .icon-logo {
    background-image: url(http://i.imgur.com/fZA5e8B.png);
    background-repeat: no-repeat;
    width: 118px;
    height: 30px;
    display: inline-block;
    vertical-align: middle;
    background-size: 118px 30px;
}

.rbx-header {
    background-color: #bd0000;
    font-size: 16px;
    height: 42px;
}

.notification-blue {
    background-color: #118237;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 400;
    color: #fff;
    text-align: center;
    padding: 4px 6px;
    min-width: 24px;
}

body .forum-table-header, body .forum-table-header th, body .forum-table-footer {
    background-color: #bd0000;
}

.icon-default-branded, .icon-playerpoint, .icon-clanpoint, .icon-leaderboard, .icon-favorite, .icon-favorite.favorited, .icon-robux, .icon-tix, .icon-robux-gray, .icon-robux-white {
    background-image: url(http://i.imgur.com/Zh9XPcJ.png);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 28px;
    height: 28px;
    display: inline-block;
    vertical-align: middle;
}

.icon-default-branded, .icon-playerpoint, .icon-clanpoint, .icon-leaderboard, .icon-favorite, .icon-favorite.favorited, .icon-robux, .icon-tix, .icon-robux-gray, .icon-robux-white {
    background-image: url(http://i.imgur.com/Zh9XPcJ.png);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 28px;
    height: 28px;
    display: inline-block;
    vertical-align: middle;
}


.chat-main .chat-header {
    background-color: #118237;
}


.text-robux-lg {
    color: #71e562;
    font-size: 24px;
    font-weight: 400;
}



.icon-robux {
    background-position: 0 -140px;
}


.rbx-upgrade-now #upgrade-now-button:hover {
    background: #bd0000!important;
    border: #bd0000!important;
}

.btn-secondary-md {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #118237;
    border-color: #118237;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 18px;
    line-height: 100%;
    border-radius: 3px;
}

.rbx-left-col li a {
    color: #1d7c16;
    display: block;
}

.rbx-header .age-bracket-label {
    display: inline-block;
    color: #32b5ff;
    padding-top: 13px;
    margin-right: 6px;
}





#nav-robux {
    background-position: 0px 12.5%;
  }
  
  #nav-robux-icon:hover #nav-robux {
    background-position: -28px 12.5%;
  }




.btn-secondary-xs {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #118237;
    border-color: #118237;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 4px 4px;
    font-size: 14px;
    line-height: 100%;
    border-radius: 3px;
}



.btn-secondary-xs:hover {
    background: #bd0000!important;
    border: #bd0000!important;
    color: #118237;
}

.text-name, .text-name:link, .text-name:hover, .text-name:visited, .text-name:active {
    color: #118237;
}

.section-content {
    background-color: #fff;
    box-shadow: 0 5px 25px 0 #191919;
    padding: 15px;
    position: relative;
}




.chat-main {
    -webkit-transform: translate3d(0,0,0);
    transform: translate3d(0,0,0);
    -ms-transform: none;
    position: fixed;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    background-color: #fff;
    bottom: 0;
    right: 6px;
    z-index: 1020;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    width: 250px;
    height: 428px;
    box-shadow: 0 -5px 25px 0 #191919;
    border-radius: 10px 10px 0px 0px;
    background: #73AD21; 
   
}

.rbx-header {
    background-color: #bd0000;
    font-size: 16px;
    height: 42px;
    box-shadow: 0 5px 25px 0 #191919;
    
}


.rbx-header .rbx-navbar li .nav-menu-title {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    padding: 6px 9px;
    display: inline-block;
    box-shadow: 0 1px 1px 0 #191919;
    border-radius: 100%;
}



.rbx-header .rbx-navbar-search .input-field, .rbx-header .rbx-navbar-search .input-addon-btn {
    box-sizing: border-box;
    height: 28px;
    box-shadow: 0 5px 25px 0 #191919;
    -webkit-appearance: none;
}

.section-header .btn-more {
    float: right;
    margin: 6px 0 0;
    box-shadow: 0 5px 25px 0 #191919;
}

.chat-main .chat-header {
    background-color: #118237;
    border-radius: 10px 10px 0px 0px;
}

.container-header .btn-more:not(.btn-control-xs), .container-header .btn-more:hover:not(.btn-control-xs), .container-header .btn-more:active:not(.btn-control-xs) {
    color: #fff;
    box-shadow: 0 5px 25px 0 #191919;
}

element.style {
    box-shadow: 0 5px 25px 0 #191919;
}

element.style {
    box-shadow: 0 5px 15px 0 #191919;
}

.chat-search .chat-search-input {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
    border: 0 none;
    height: 32px;
    padding: 6px 6px 6px 26px;
    font-size: 14px;
    background-color: greenyellow;
}

.btn-control-md {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #bd0000;
    border-color: #B8B8B8;
    color: #ffffff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 18px;
    line-height: 100%;
    border-radius: 3px;
}

.btn-control-md:hover, .btn-control-md:focus {
    background-color: #d50000;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #B8B8B8;
    color: #ffffff;
    cursor: pointer;
}

.input-group-btn .input-dropdown-btn {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #bd0000;
    border-color: #B8B8B8;
    color: #ffffff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    border-radius: 3px;
    padding: 5px 12px;
    margin: 0;
    line-height: 18px;
    display: block;
    width: 100%;
}



.input-group-btn .input-dropdown-btn:hover, .input-group-btn .input-dropdown-btn:focus {
    background-color: #060;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #060;
    color: #fff;
    cursor: pointer;
}

.icon-default-logo-r, .icon-logo-r {
    background-image: url(http://i.imgur.com/D4KrVrJ.png);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 30px;
    height: 30px;
    display: inline-block;
    vertical-align: middle;
    background-size: 30px 30px;
}


}
