@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Browser Background */

browser[type="content-primary"] {
  background-color: #052832 !important;
}
 
@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(ftp://), url-prefix(file://) {

/* No background images. try to exclude icons, other misc items */

*:not([class*="ytp-bottom"]):not([class*="ytp-tooltip-bg"]):not([class*="ytp-tooltip"]):not([class*="ytp-preview"]):not([id*="movie_player"]):not([class*="video-thumb"]):not([class*="yt-thumb-48"]):not([class*="yt-thumb-28"]):not([class*="yt-thumb-square-48"]):not([class*="yt-thumb-square-28"]):not([class*="yt-thumb-20"]):not([class*="ux-thumb-96"]):not([class*="lb-nav"]):not([class*="yt-thumb-clip"]):not([class*="yt-thumb-square"]):not([class*="video-thumb"]):not([class*="yt-thumb"]):not([class*="yt-thumb-27"]):not([id*="player"]):not([id*="placeholder-player"]):not([class*="player-api"]):not([id*="theater-background"]):not([class*="layer"]):not([id*="lightbox-nav"]):not([id*="imageContainer"]):not(link):not(:empty):not([onclick*="open"]):not(span):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="prite"]):not([id*="prite"]):not([id*="lbImage"]) 
{
  background-image: none !important;
}

/* Basic Bodies */

html,
body:not([class*="ytp-bottom"]):not([class*="ytp-tooltip-bg"]):not([class*="ytp-tooltip"]):not([class*="ytp-preview"]):not([id*="movie_player"]):not([class*="video-thumb"]):not([class*="yt-thumb-48"]):not([class*="yt-thumb-28"]):not([class*="yt-thumb-square-48"]):not([class*="yt-thumb-square-28"]):not([class*="yt-thumb-20"]):not([class*="ux-thumb-96"]):not([class*="yt-thumb-clip"]):not([class*="yt-thumb-square"]):not([class*="video-thumb"]):not([class*="yt-thumb"]):not([class*="yt-thumb-27"]):not([id*="player"]):not([id*="placeholder-player"]):not([class*="player-api"]):not([id*="theater-background"]):not([class*="layer"]):not([id*="lightbox-nav"]):not([id*="imageContainer"]):not(:empty):not([onclick*="open"]):not(span):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="prite"]):not([id*="prite"]):not([id*="lbImage"]) 
{
  background-color: #052832 !important;
  color: #ddd !important;
}

/* :::::::: Headings + Header :::::::: */

/* Header rules */

head,
header, 
#header {
  color: #ddd !important;
  background: inherit !important;
  font-weight: bold !important;
}

h1 {
  color: #e02c6d !important;
  background-color: inherit !important;
}

h2 { 
  color: #d74937 !important;
  font-weight: bold !important;
  background-color: inherit !important;
} 

h3,
h4 {
  color: #c45aec !important;
  font-weight: bold !important;
  background-color: inherit !important;
}

h5,
h6 {
  color: #b58900 !important;
  font-weight: bold !important;
  background-color: inherit !important;
}

div * > h2 * {
  color: #d74937 !important;
  font-weight: bold !important;
  background-color: inherit !important;
}

div * > h3 *,
div * > h4 * {
  color: #c45aec !important;
  font-weight: bold !important;
  background-color: inherit !important;
}

div * > h5 *,
div * > h6 * {
  color: #b58900 !important;
  font-weight: bold !important;
  background-color: inherit !important;
}

/* Give id's BG hopefully */

div[class]:not([class*="my_slider"]):not([class*="slider_caption"]):not([class*="front-card"]):not([class*="hero__text-wrap"]):not([class*="ytp-bottom"]):not([class*="ytp-tooltip-bg"]):not([class*="ytp-tooltip"]):not([class*="ytp-preview"]):not([class*="video-thumb"]):not([class*="yt-thumb-48"]):not([class*="yt-thumb-28"]):not([class*="yt-thumb-square-48"]):not([class*="yt-thumb-square-28"]):not([class*="yt-thumb-20"]):not([class*="ux-thumb-96"]):not([class*="promo-content"]):not([class*="lb-nav"]):not([class*="yt-thumb-clip"]):not([class*="yt-thumb-square"]):not([class*="video-thumb"]):not([class*="yt-thumb"]):not([class*="yt-thumb-27"]):not([class*="player-api"]):not([class*="layer"]):not([class*="stars"]):not([class="rating"]):not([class*="prite"]):not(link):not(:empty):not([onclick*="open"]):not(span)
{
  color: #eee !important;
  background-color: inherit !important;
}

div[id]:not([id*="header-links"]):not([id*="movie_player"]):not([id*="player"]):not([id*="placeholder-player"]):not([id*="theater-background"]):not([id*="lightbox-nav"]):not([id*="imageContainer"]):not([id*="stars"]):not([id="rating"]):not([id*="prite"]):not([id*="lbImage"]):not(link):not(:empty):not([onclick*="open"]):not(span)
{
  color: #eee !important;
  background-color: inherit !important;
}

div[style]:not([class*="ytp-bottom"]):not([class*="ytp-tooltip-bg"]):not([class*="ytp-tooltip"]):not([class*="ytp-preview"]):not([id*="movie_player"]):not([class*="video-thumb"]):not([class*="yt-thumb-48"]):not([class*="yt-thumb-28"]):not([class*="yt-thumb-square-48"]):not([class*="yt-thumb-square-28"]):not([class*="yt-thumb-20"]):not([class*="ux-thumb-96"]):not([class*="lb-nav"]):not([class*="yt-thumb-clip"]):not([class*="yt-thumb-square"]):not([class*="video-thumb"]):not([class*="yt-thumb"]):not([class*="yt-thumb-27"]):not([id*="player"]):not([id*="placeholder-player"]):not([class*="player-api"]):not([id*="theater-background"]):not([class*="layer"]):not([id*="lightbox-nav"]):not([id*="imageContainer"]):not(link):not(:empty):not([onclick*="open"]):not(span):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="prite"]):not([id*="prite"]):not([id*="lbImage"]):not(link):not(:empty):not([onclick*="open"]):not(span)
{
  color: #eee !important;
  background-color: #052832 !important;
  border-width: 1px !important;
  border-color: #d74937 !important;
  border-radius: 5px !important;
}

/* :::::::: Text Presentation :::::::: */

* {
  border-color: #7d9002 !important;
  outline-color: #cde790 !important;
  text-shadow: none !important;
}

p {
  color: #658899 !important;
}

span {
  color: #ddd !important;
  background-color: inherit !important;
  border-radius: 5px !important;
}

section {
  color: #ddd !important;  
  background-color: #052832 !important;
  border-width: 3px !important;
  border-color: #16a085 !important;
  border-radius: 5px !important;
}

article {
  color: #aaa !important;
  background-color: #052832 !important;
  border-radius: 5px !important;
  padding: 5px !important;
}

form:not([class*="signin"]):not([id*="frontpage-signup-form"]) {
  background-color: #052832 !important;
  color: #ddd !important;
  border-radius: 5px !important;
  margin: auto !important;
}

i,
strong,
em,
b {
  color: #ddd !important;
  font-weight: bold !important;
}

cite {
  color: #cb4b16 !important;
  font-weight: bold !important;
}

summary,
details {
  color: #8c95dd !important;
  font-weight: bold !important;
} 

abbr,
progress,
time,
.date {
  color: #c45aec !important;
  font-weight: bold !important;
}

mark,
code,
pre,
blockquote,
[class*="quote"],
td[style*="inset"][class="alt2"] {
  background-color: #003945 !important;
  color: #ddd !important;
  font-weight: bold !important;
  padding: 2px !important;
} 

tt {
  background-color: #006666 !important;
  color: #ddd !important;
  font-weight: bold !important;
  padding: 2px !important;
}

main {
  background-color: #052832 !important;
}

footer {
  color: #ddd !important;
  background-color: #052832 !important;
  font-weight: bold !important;
  border-width: 2px !important;
  border-color: #8c95dd !important;
  border-radius: 0px !important;
}

/* :::::::: Lists :::::::: */

dl {
  color: #16a085 !important;
  font-weight: bold;
}

dt {
  color: #7d9002 !important;
  background-color: inherit !important;
}

dd {
  color: #ddd !important;
  background-color: inherit !important;
}

ol,
li,
ul {
  color: #bbb !important;
  background-color: inherit !important;
  font-weight: bold !important;
}

/* :::::::: Tables, cells :::::::: */

table {
  background-color: inherit !important; 
  color: #8c95dd !important;
  font-weight: bold !important;
  border-radius: 5px !important;
}

tbody {
  color: #ddd !important;
  border-radius: 5px !important;
}

th {
  background-color: #7d9002 !important;
  color: #ddd !important;
  padding: 5px !important;
}

td {
  background-color: inherit !important;
  color: #93a1a1 !important;
  padding: 5px !important;
  border-radius: 5px !important;
}

tr {
  background-color: #052832 !important;
  color: #ddd !important;
  border-width: 1px !important;
  border-color: #b58900 !important;
  border-radius: 5px !important;
}

caption {
  background-color: #7d9002 !important;
  text-decoration: none !important;
  color: #ddd !important;
}

/* :::::::: Input :::::::: */

/* anonymous divs */

button,
#login-existing,
div.form-controls > table > tbody > tr > td > input,
html body input[type="url"],
html body input[type="password"],
html body input[type="email"],
html body input[type="search"],
html body input[type="text"],
html body input[type="submit"],
html body input[type="button"],
html body textarea {
  background-color: #073642 !important;
  color: #ddd !important;
  border: solid 2px #006666 !important;
  -moz-appearance: textfield !important;
  -webkit-appearance: textarea !important;
  opacity: .95 !important;
  outline: inherit !important;
}

button:hover,
#login-existing:hover,
div.form-controls > table > tbody > tr > td > input:hover,
html body input[type="url"]:hover,
html body input[type="password"]:hover,
html body input[type="email"]:hover,
html body input[type="search"]:hover,
html body input[type="text"]:hover,
html body input[type="submit"]:hover,
html body input[type="button"]:hover,
html body textarea:hover {
  background-color: #132633 !important;
  color: #fff !important;
  border: solid 2px #7d9002 !important;
  -moz-appearance: textfield !important;
  -webkit-appearance: textarea !important;
  opacity: 1 !important;
  outline: inherit !important;
}

/* Placeholder*/

input[type="password"]::-moz-placeholder {
  color: white;
}

/* Style reset. */

html body input[type="checkbox"] {
  -moz-appearance: checkbox !important;
  -webkit-appearance: checkbox !important;
}

html body input[type="radio"] {
  -moz-appearance: radio !important;
  -webkit-appearance: radio !important;
}

/* :::::::: Custom styling :::::::: */

html body button[type="submit"],
ytd-item-section-renderer.style-scope > div > ytd-shelf-renderer > div > div > yt-horizontal-list-renderer > div > ytd-button-renderer,
input,
html:root input[type="button"],
html:root input[type="submit"],
html:root input[type="reset"],
html:root button[type="submit"],
html:root button {
  color: #ddd !important;
  background-color: #024354 !important;
  border-width: 2px !important;
  border-color: #006666 !important;
  border-radius: 5px !important;
  font-weight: bold !important;  
}

html body button[type="submit"]:hover,
ytd-item-section-renderer.style-scope > div > ytd-shelf-renderer > div > div > yt-horizontal-list-renderer > div > ytd-button-renderer:hover,
input:hover,
html:root input[type="button"]:hover,
html:root input[type="submit"]:hover,
html:root input[type="reset"]:hover,
html:root button[type="submit"]:hover,
html:root button:hover {
  color: #fff !important;
  background-color: #006666 !important;
  border-width: 2px !important;
  border-color: #7d9002 !important;
  border-radius: 5px !important;
  font-weight: bold !important;
}

/* Drop-down menu */

select,
option,
optgroup {
  border: none !important;
  background-color: #006666 !important;
  color: #ddd !important;
  -moz-appearance: caret !important;
  text-decoration: none !important;
}

/* :::::::: Misc :::::::: */

address {
  background-color: #006666 !important;
  border-radius: 5px !important;
}

hr {
  background-color: #9360a2 !important;
}

/*currently viewed page*/

.current {
  color: #7d9002 !important;
}

/* Remove Rounded Corners, Borders, Spacer, Padding images */

img[src*="spacer"]:empty,
[id*="round"]:empty,
[id*="bottom"]:empty, 
[class*="bottom"]:empty,
[class*="Bottom"]:empty,
[id*="top"]:empty,
[class*="top"]:empty,
[class*="Top"]:empty,
[class*="spacer"]:empty {
  background-image: none !important;
}

/* Menus and Navigation */

nav {
  background-color: inherit !important;
  border-top-style: hidden !important;
  border-right-style: hidden !important;
  border-bottom-style: hidden !important;
  border-left-style: solid !important;
  border-width: 1px !important;
  border-color: #a03470 !important;
  border-radius: 0px !important;
}

aside {
  color: #ddd !important;
  background-color: #052832 !important;
  border-top-style: hidden !important;
  border-right-style: hidden !important;
  border-bottom-style: hidden !important;
  border-left-style: solid !important;
  border-width: 1px !important;
  border-color: #a03470 !important;
  border-radius: 0px !important;
  padding: 5px !important;
}

menu {
  background-color: inherit !important;
}

/*Common naming conventions - in case previous declarations fail to give solid BG*/

html body [class*="open"],
html body [id*="Dropdown"],
html body [id*="dropdown"],
html body [class*="Dropdown"],
html body [class*="dropdown"],
html body [id*="menu"]:not(SELECT),
html body [class*="menu"]:NOT(SELECT),
html body [class*="tooltip"],
html body [class*="popup"], 
html body [id*="popup"],

/* Notes, details, etc.  Maybe useful*/

html body [class*="note"],
html body [class*="detail"],
html body [class*="description"]

/* Also common */

[class*="content"],
[class*="container"]

/* Headers, Logos */

[id*="masthead"] a,
[id*="header"] a,
[id*="logo"] a,
[class*="logo"] a {
  text-indent: 0 !important;
}

/* Instead of increasing specificity rating by using :not, set rules separately */

html:root body [class*="layer"],
html:root body #lightbox-nav,
html:root body #imageContainer,

html *:after,
html *:before {
  color: #16a085 !important;
  border-color: #7d9002 !important;
  background-color: transparent !important;
}

html * [after]:hover,
html * [before]:hover {
  color: #eee !important;
  border-color: #16a085 !important;
  background-color: inherit !important;
}

/* Link */

a:link,
a:link *:not(img) { 
  color: #16a085 !important;
  text-decoration: none !important;
  font-weight: bold !important;
}

a:visited, 
a:visited * {
  color: #6b582a !important;
  text-decoration: none !important;
}

a:hover,
a:hover *:not(img) {
  color: #e02c6d !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

html [href*="#"]:hover *:not(img) {
  color: #f39c12 !important;
  font-weight: bold !important;
  text-decoration: none !important;
}

/* Event Handlers/Attributes */

[onclick],
[ondblclick],
[onmousedown] {
  color: #006666 !important;
  text-indent: 0 !important;
}

[onclick]:hover,
[ondblclick]:hover,
[onmousedown]:hover {
  color: #c45aec !important;
}

/* Make images transparent */

img {
  opacity: .95 !important;
}

img:hover {
  opacity: 1 !important;
  text-decoration: none !important;
}

/* Highlight */

::-moz-selection {
  background-color: #e02c6d !important;
  color: #052832 !important;
}
}

/* :::::::: Specific Fixes :::::::: */

/* A fix for opengroup.org just to give a diferent color to li ol ul elements */
body > * > * > p,
body > * > * > p > * {
  color: #9360a2 !important;
  font-weight: bold !important;
}

body > ul > li > ul > li > p {
  color: #a03470 !important;
  font-weight: bold !important;
}

body > ul > li > ul > li > ul > li > p {
  color: #8c95dd !important;
  font-weight: bold !important;
}

body > ol > li > ul > li > p {
  color: #a03470 !important;
  font-weight: bold !important;
}

#container {
  background-color: #024354 !important;
}

#ogsyscontent > table > tbody > tr > td {
  border-radius: 10px !important;
}

/* A fix for facebook buttons and images color consistency */
