#pageFooter > div {
  background-color: #052832 !important;
}

li._2xr > div > div > a > div,
._1k67 > a,
div._4bl7,
div.ego_column > div > div > div > div {
  background: transparent !important;
  border-width: 0px !important;
}

#pageFooter > div {
  background-color: #052832 !important;
}

/* A fix for github for color consistenct and a h1 color */
.alt-h0 {
  color: #e02c6d !important;
}

.ajax-pagination-form {
  background-color: #052832 !important;
}

#lightbox {
  background: transparent !important;
}

/* A fix for codepen images */
.promo-content,
div.single-collection > div > a,
div.single-project > div > a,
div.single-pen > div > a {
  background: transparent !important;
}

/* Counple of fixes for Reddit */
#sr-header-area {
  background-color: #024354 !important;
}

.md > p > a {
  background: transparent !important;
}

.havemail,
.pref-lang,
.nohavemail {
  display: contents !important;
}

#login_login-main {
  background-color: #052832 !important;
}

/* A small fix for omgubuntu */
.rslides_tabs {
  background: transparent !important;
}

/* Small fix for extratorrent */
table.tl > thead > tr > th > table > tbody > tr > td {
  background-color: #7d9002 !important;
}

/* A fix for imgur when hover images */
#imagelist > * > * > * > a {
  background: transparent !important;
}

#topbar li a.topbar-icon.signin-register {
  display: ruby-text !important;
}

.comment[data-level="0"] > .caption .caption-gradient {
  display: contents !important;
}

#captions .comment[data-level="0"] > .caption .usertext {
  background-color: #024354 !important;
}

/* Couple of tweaks and a fix for Arch wiki */
#mw-content-text > div {
  border-width: 2px !important;
  border-color: #49796b !important;
  background-color: #003945 !important;
}

#archnavbarlogo > h1 > a {
  display: none !important;
}

#mw-content-text > div > strong {
  color: #e02c6d  !important;
}

#searchform {
  background-color: #052832 !important;
}

#mw-content-text > div > code {
  background-color: #7d9002 !important;
}

/* Some fixes for the new youtube UI still a broken tho =( the classic UI looks fine */

div.style-scope,
ytd-browse.style-scope {
  background-color: #052832 !important;
}

yt-formatted-string.ytd-guide-signin-promo-renderer {
 color: #ddd  !important;
}

li.channels-content-item > div > div > div > a > span > span {
  background: rgba(0, 0, 0, 0.6) !important;
}

/* Custom styling for Fun*T webpage funtown-urt.info */

.sub-header-right,
.sub-header-left,
.header-right,
.header-left,
.scapmain-right,
.scapmain-left,
.capmain-right,
.capmain-left,
.footer-right,
.footer-left {
  background-image: none !important;
}

.scapmain {
  font-size: 14px !important;
  font-weight: bold !important;
  color: #d74937 !important;
}


.tbl1 {
  font-size: 13px !important;
}

.side-body {
  font-size: 13px !important;
}

.sub-header {
  font-size: 15px !important;
}

table.tbl-border > tbody > tr > td > img {
  border-radius: 100% !important;
}

/* A small fix for google */

.sfbgx,
.sfbgg {
  background-color: #052832 !important;
}

#logocont {
  overflow: hidden !important;
}

._KBh {
  background-color: #024354 !important;
}

/* Some fixes for mywiki.wooledge.org */

#content > p {
  color: #658899 !important;
}

#content > p > tt,
#content > p > strong {
  color: #ddd !important;
}

/* A small fix for userstyles.org */

#main-article > p {
  color: #658899 !important;
}

/* Tecmint */

.sidebar-content {
  border-top-style: hidden !important;
  border-right-style: hidden !important;
  border-bottom-style: hidden !important;
  border-left-style: solid !important;
  border-width: 1px !important;
  border-color: #d74937 !important;
  border-radius: 0px !important;
}

/* Urban Zone */

.cluetip {
  color: #559eb3 !important;
}

/* consistency for webupd8 slide */

.my_slider,
.slider_caption {
  background: rgba(0,0,0,0.7) !important;
}
