css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pes.uniacc.cl") {
	td#left-column, td#right-column, div#dropdown,.forumsearch, .subscription,#header-profileoptions, .header-profilename font, li.first {
		display:none !important 
	}
	#course-view .section td.left, #course-view .section td.right {
		width: 1em;
	}
	#course-view .section td.content {
		border-bottom: 7px dotted #959595;
	}
	.header-profilepic img {
		width: 150px;
		height: 150px;
		border-radius: 50%;
	}
	#profileblock table {
		width: 210px;
	}
	.navbar .breadcrumb {
		float: none;
		margin: 2em 0 0.2em 0;
	}
	#logo, #page, body, .forumpost {
		width: 100% !important;
		max-width:100% !important;
	}
	body, textarea,select,input {
		font-size:40px !important;
	}
	#profileblock {
		float: left;
		text-align: left !important;
		max-width: 100px;
		margin-top: 15px;
		margin-left: 230px;
	}
	#graphicwrapper {
		background:none;
	}
	#logo {
		background-position: 30px 0px;
	}
	.forumpost img.userpicture {
		width: 100px;
		height: 100px; 
	}
	.forumpost .posting img {
		max-width: 100% !important;
		max-height: 100% !important;
		height: 100%;
		width: 95%;
	}
	.activityicon {
		width:30px !important;
		height:30px !important;
	}
	.activity.label img {
		width:100% !important;
		height:auto !important;
	}
	iframe.iframe, .mform div.fhtmleditor, .mform, .mform div.felement, textarea#id_message, .mform fieldset, #id_subject{
		width: 95% !important;
	}
}


@-moz-document domain("ecampus.uniacc.edu") {
	table, td {
		width: 98% !important;
	}
	.TabbedPanelsContent.TabbedPanelsContentVisible {
		width:100% !important;
	}
	
	*, body, textarea,select,input, p, span {
		font-size:40px !important;
	}
	table, thead, tbody, th, td, tr {
		display: block;
	}
	th img[usemap="#Map"], body > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(3) > th > img, body > table:nth-child(1) > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr:nth-child(1) > td > table, .TextoPie, body > table:nth-child(1) > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr:nth-child(2) > td:nth-child(2) > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(2) > td:nth-child(1), body > table:nth-child(1) > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr:nth-child(2) > td:nth-child(2) > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(1) > td:nth-child(1) > table > tbody > tr > td.TextoUsuario {
		display:none !important;
	}
	body > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(2) > td > table > tbody > tr:nth-child(1) > td:nth-child(1) > form > table {
		width: 95%;
	}
	input {
		width: 100%;
		line-height: 90px;
		padding: 25px;
	}
	.TextoUsuario {
		height:100px;
		line-height:100px;
	}
	.TextoNormalTitulo {
		background-color: #b0cbe2;
	}
	.fontLinkMenu {
		color: white !important;
		background: #005298;
		padding: 10px;
		height: 80px;
		display: block;
		text-align: center;
		margin-bottom: 91px;
		line-height: 80px;
		text-transform: uppercase;
	}
	input[value="Entrar"] {
		color: white !important;
		background: #005298;
		border:0;
		margin-bottom:30px;
	}
	a[href="mailto:contacto.pes@uniacc.cl"] {
		color: #003366 !important;
		background: white;
	}	
}
