@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("projecteuler.net") {

  /*[[theme]]*/

  /* Background */
body, .grid td, .problem_content, .forum_table, .gsc-control-cse, .gsc-webResult.gsc-result, .gsc-webResult.gsc-result:hover, #news_page .news {
  background-color: var(--bg)
}
table[style="background-color:#333333;"] {
  background-color: var(--bg) !important
}
#header, .navbar, #footer, #footer .business_card, .popover, .popover-title, .popover-content, .dropdown-menu, #problems_table_page #problems_table th.final_column, .grid th, #about_page #disclaimer, .info span, .forum_info, .forum_quote, .form_box, #progress_page .form_box, #statistics_general_table_page .rank_column, .grid tr, .form_box .grid tr:nth-child(2n) td, .form_box .grid td, .gsc-control-cse .gsc-option-menu, #forum_page .post_box {
  background-color: var(--bg-2)
}
td[style="border:none;background:none;"], td[style="background-color:#ffffff;"] {
  background-color: var(--bg-2) !important
}
oddrow, .grid tr:nth-child(2n) td, .forum_table:nth-child(2n), .nav {
  background-color: var(--bg-odd)
}
.form-control, #nav li, .code_click, .nav a:hover, .collapse_click, .collapse_box, #forum_page .post_header {
  background-color: var(--bg-input);
  color: var(--fg-input)
}
.form-control:focus, #nav li:hover, .code_click:hover {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover)
}
body, .navbar, input[type="text"], select, textarea, .form-control {
  background-image: none
}

/* Foreground */
body, #about_page #disclaimer, .info span, .forum_info, .forum_message, #statistics_general_table_page .info span, .gs-webResult .gs-snippet, .gsc-option-menu-item, .post_message, #account_page #profile_image_box a {
  color: var(--fg)
}
.info span[style*="color:#555"], div[style*="color:var(--color-text-variation-2)"] {
  color: var(--fg) !important
}
.brand, .grid th, #problems_table_page .sortby, h1, h2, h3, h4, h5, .info .heading, #statistics_general_table_page .rank_column, #nav #highlight, .gs-webResult div.gs-visibleUrl, td[style="text-align:left;"], .problem_content table td b, .problem_content table th {
  color: var(--fg-2)
}
.muted, #cache_info, #info_panel, #footer, .small_notice, #account_page .small_notice, .gsc-result-info, .gsc-orderby-label, .gcsc-branding-text, .problem_content .note {
  color: var(--fg-muted)
}
.info div[style*="color:#333"] {
  color: var(--fg-muted) !important
}

/* Links */
a, a:link, .gs-webResult.gs-result a.gs-title:link {
  color: var(--fg-link)
}
a:hover, a:visited:hover, .gs-webResult.gs-result a.gs-title:hover {
  color: var(--fg-link-hover)
}
a:visited, .gs-webResult.gs-result a.gs-title:visited {
  color: var(--fg-link-visited)
}

/* Borders */
noborder, .navbar, .grid th, .grid td, #about_page #disclaimer, .problem_content, .info span, .forum_table, .forum_info, .forum_quote, .form_box, #progress_page .form_box, #progress_page #level_image_box, #message, #statistics_general_table_page #image_box, .gsc-control-cse, .gsc-webResult.gsc-result, .gsc-webResult.gsc-result:hover, .gsc-above-wrapper-area, .gs-result img.gs-image, #statistics_select_page #select_grid .select_box, #news_page .news, #captcha_image {
  border: none
}
img[src*="images/flags/"], #account_page > div, .info[style*="background-color:#ccc"] {
  border: none !important;
}
#nav li {
  border-color: transparent;
}
#progress_page #progress_bar_box, .gsc-cursor-box, .problem_content table, .problem_content table td, .nav, .collapse_box, .post_box {
  border-color: var(--border-color)
}
span[style="border-top:1px solid #000;"], td[style="border-bottom:1px solid #000;"], span[style="border-bottom:1px solid #000;"] {
  border-color: var(--fg) !important
}
#news_page .news {
  border-bottom: solid 1px var(--fg-2)
}
dfn {
  border-bottom: dotted 2px var(--border-color)
}
div[style*="border:1px solid #999"], td[style="background-color:#ffffff;"] {
  border: solid 1px var(--fg-link) !important;
}
.problem_content table {
  border-collapse: collapse;
}

/* Rounded edges */
#nav li, #nav #current a, #about_page #disclaimer, .info span, .code_click, .forum_quote, #message {
  border-radius: 5px
}
#levels_completed_table img, #levels_table img, #image_box img, #select_grid img {
  border-radius: 50px
}

/* Shadows */
#header, #about_page #disclaimer, .info span, .forum_quote, .form_box, #progress_page .form_box, #message, #account_page > div, .gsc-control-cse .gsc-option-menu {
  box-shadow: 0 0 5px #000;
}

/* bg/fg combos */
success, success a, #progress_page .problem_solved, #progress_page .problems_solved_table td.problem_solved  {
  background-color: var(--bg-success);
  color: var(--fg-success)
}
info, info a, #message {
  background-color: var(--bg-info);
  color: var(--fg-info)
}
warning, warning a, #progress_page .problem_unsolved, #progress_page .problems_solved_table td.problem_unsolved, .gs-no-results-result .gs-snippet {
  background-color: var(--bg-warning);
  color: var(--fg-warning)
}
error, error a {
  background-color: var(--bg-error);
  color: var(--fg-error)
}
disabled, .gsc-results .gsc-cursor-box .gsc-cursor-current-page, .info[style*="background-color:#ccc"], .pagination a.current {
  background-color: var(--bg-disabled) !important;
  color: var(--fg-disabled) !important;
}
#nav #current a, .gsc-option-menu-item-highlighted, .gsc-selected-option-container, .info[style*="background-color:#ff9933"] {
  background-color: var(--fg-link);
  color: var(--bg);
}
#nav #current a:hover, .gsc-option-menu-item:hover, .gsc-selected-option-container:hover {
  background-color: var(--fg-link-hover);
}

/* Inputs */
input[type="text"], input[type="input"], input[type="password"], .code_click, .code_box, select, textarea {
  border: solid 0.5px var(--fg-link) !important;
  color: var(--fg-link) !important;
  background-color: var(--bg-input) !important;
}
input[type="text"]:focus, input[type="input"]:focus, input[type="password"]:focus, select:focus, textarea:focus {
  color: var(--fg-link-hover) !important;
  background-color: var(--bg-2) !important;
}

/* Buttons */
input[type="submit"], input[type="button"], .gsc-results .gsc-cursor-box .gsc-cursor-page, .pagination a:link, .pagination a:visited {
  background-color: var(--fg-link);
  color: var(--bg);
  border: none;
  padding: 3px 10px;
  box-shadow: -1px -1px #000 inset, 1px 1px 1px #000;
}
input[type="submit"]:active, input[type="button"]:active, .gsc-results .gsc-cursor-box .gsc-cursor-page:active, .pagination a:active, .pagination a.current:active {
  background-color: var(--fg-link-hover);
  box-shadow: 1px 1px #000 inset;
  padding: 4px 8px 2px 12px;
}
input[type="submit"]:hover, input[type="button"]:hover, .gsc-results .gsc-cursor-box .gsc-cursor-page:hover, .pagination a:hover {
  background-color: var(--fg-link-hover);
}


/* Code */
pre, .hljs {
  background-color: var(--syntax-bg);
  border: none
}
pre, .hljs {
  color: var(--syntax-fg)
}

.hljs-params { color: var(--syntax-fg-2) }
.hljs-number, .hljs-hexcolor { color: var(--syntax-number) }
.hljs-built_in, .hljs-preprocessor, .hljs-tag, .clojure .hljs-title, .hljs-typename { color: var(--syntax-builtin) }
.hljs-comment { color: var(--syntax-comment) }
.hljs-keyword, .hljs-literal, .hljs-sub .hljs-identifier, .hljs-pi, .hljs-tag, .hljs-tag .hljs-keyword, .hljs-decorator, .ini .hljs-title, .hljs-shebang, .hljs-prompt, .hljs-hexcolor, .hljs-rules .hljs-value, .css .hljs-value .hljs-number, .hljs-symbol, .hljs-symbol .hljs-string, .hljs-number, .css .hljs-function, .clojure .hljs-title, .clojure .hljs-built_in, .hljs-function .hljs-title, .coffeescript .hljs-attribute { color: var(--syntax-keyword) }
.hljs-string { color: var(--syntax-string) }
.hljs-function .hljs-title, .hljs-variable, .hljs-class .hljs-title, .clojure .hljs-built_in { color: var(--syntax-function) }

p[style*="font-family:'courier new'"] {
  font-family: 'Consolas', 'Monospace', 'Courier New' !important;
}

/* Logo Bypass */
#logo {
  width:0;
  height:0;
}
#logo:before {
  padding: 5px;
  content: 'ProjectEuler';
  font-size: 2.3em;
  color: var(--fg-2);
  font-weight: bold;
  font-family: Arial, Helvatica, Sans-Serif;
}
#logo:after {
  content: '.net';
  color: var(--fg-muted);
  font-size: 1.8em;
  font-family: Consolas, Monospace, "Courier New";
  position: absolute;
  top: 15px;
  left: 190px;
}

/* Image-manipulation */
#header > img, img[src="images/icon_forum_link.png"], img[src="images/icon_up_dark.png"], img[src="images/icon_down_dark.png"] {
  filter: brightness(400%)
}
img[src="images/icon_search.png"] {
    filter: brightness(200%)
}
.gsc-control-cse .gsc-option-selector {
  filter: brightness(0%)
}
#captcha_image {
  filter: invert(100%) hue-rotate(180deg);
  box-shadow: 0 0 5px #fff;
  border: none !important;
}
.problem_content img[src*="project/images/"] {
  /*background-color: #fff;*/
  /*box-shadow: 0 0 5px #fff;*/
  border-radius: 5px;
  filter: invert(100%);
}

/* Search Results */
.gsc-results .gsc-cursor-box .gsc-cursor-page {
  text-decoration: none
}
.gsc-results .gsc-cursor-box .gsc-cursor-current-page {
  font-weight: normal;
}
.problem_content {
    box-shadow: 2px 2px 4px #000;
}

}

