css:
@-moz-document url-prefix("http://www.pietsmiet.de/gallery/playlists/"), url-prefix("http://www.pietsmiet.de/gallery/categories/"), url-prefix("http://www.pietsmiet.de/gallery/neuste/") {
.right-col {
    padding-left: 20px;
    width: 100%;
}

@media screen and (min-width: 1400px) {
  .top {
    height: 5px;
  }
  
  .top > * {
  	visibility: hidden;
  }
  
  .top:hover > * {
  	visibility: visible;
  }
	
  #wrapper {
    padding-top: 3px;                      
  }
}

@media screen and (min-width: 1600px) {
  .moduletable .breadcrumb {
    visibility: hidden !important;
  }
    
  #hwd-container {
    margin-top: -45px;
  }
}

.right-color, .bodytext.bodytext9 {
    float: none;
	
	margin-left: auto;
	margin-right: auto;
	
    width: 98%;
    padding-right: 20px;
}

.container, .navbar-static-top .container, .navbar-fixed-top .container, .navbar-fixed-bottom .container {
    width: 100%; 
    max-width: 1700px;
}

.container article {
    padding-left: 5px;
    max-width: 100% !important;
    padding-right: 5px !important;
}
}
