@-moz-document domain("lichess.org") {
piece.pawn.black { background-image: url("http://imgh.us/andr_(7).svg")!important;} 
	piece.knight.black { background-image: url("http://imgh.us/andr_(1).svg")!important;} 
	piece.bishop.black { background-image: url("http://imgh.us/andr_(5).svg")!important;} 
	piece.rook.black { background-image: url("http://imgh.us/andr_(10).svg")!important;} 
	piece.queen.black { background-image: url("http://imgh.us/andr_(3).svg")!important;} 
	piece.king.black { background-image: url("http://imgh.us/andr_(12).svg")!important;} 

	piece.pawn.white { background-image: url("http://imgh.us/andr_(8).svg")!important;} 
	piece.knight.white { background-image: url("http://imgh.us/andr_(4).svg")!important;} 
	piece.bishop.white { background-image: url("http://imgh.us/1234_1.svg")!important;} 
	piece.rook.white { background-image: url("http://imgh.us/andr_(11).svg")!important;} 
	piece.queen.white { background-image: url("http://imgh.us/andr_(9).svg")!important;} 
	piece.king.white { background-image: url("http://imgh.us/king_2.svg")!important;}

.purple .is2d .cg-board, #top div.color_demo.purple  {
	background-image:url("http://i.imgur.com/7Tbr26q.png")  !important;
    
	}
  
  .cg-board-wrap coords {
    font-family: 'Courier New', monospace;
    font-size: 16px;
