css:
@-moz-document domain("chatvdvoem.ru")
{

body#vpp2 {
    background:url(http://i.imgur.com/2oeEVUY.jpg) repeat 50% 50% !important;
    background-position:0px 0px !important;
    background-attachment:fixed !important;
}

#vpp2-chats .vpp2-chat .header {
    background: #212121 !important;
}

#vpp2-chats .vpp2-chat .log {
    background: #1d1d1d !important;  
}

#vpp2-chats .vpp2-chat.android .controls {
	background: #2b2b2f !important; 
}

#vpp2-chats .vpp2-chat .controls .text {
    border-bottom: 1px solid #464646 !important;
    color: #757575 !important;
}

#vpp2-chats .vpp2-chat .controls .text.disabled {
    color: #757575 !important;
}

#vpp2-chats .vpp2-chat .header .header-left {
    color: #6b6b6b !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageFrom .message {
    border-color: rgb(56, 56, 56) !important;
    background: rgb(35, 35, 35) !important;
    color: rgb(199, 199, 199) !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageTo .message {
    border-color: rgb(56, 56, 56) !important;
    background: rgb(35, 35, 35) !important;
    color: rgb(199, 199, 199) !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageTo .message::before {
    border-top-color: rgb(35, 35, 35) !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageTo .message::after {
    border-color: rgb(56, 56, 56) transparent transparent !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageFrom .message::before {
    border-top-color: rgb(35, 35, 35) !important;
}

#vpp2-chats .vpp2-chat.android .log ol > li.messageFrom .message::after {
    border-color: rgb(56, 56, 56) transparent transparent !important;
}

#vpp2-chats .vpp2-chat .header .status {
	color: #797979 !important;
}

#vpp2-chats .vpp2-chat .fake-scrollbar-wrap {
    background: #1d1d1d !important;
}

#vpp2-options {
	opacity: 0.86;
}

}
