css:
@-moz-document domain("www.extreme-down.one"){
body {
     background-image :  url(https://image.noelshack.com/fichiers/2017/11/1489771596-797044217987-space-planet-star.jpg) !important;
    background-attachment:fixed;
}
/*interdiction*/
p#certificate, element.style {
background-color: #ff0000 !important;
} 
.day-current {
    border : solid #ff00df 1px ;
}
/*calendrier*/
#calendar.calendar {
    border : solid white 2px;}
.calendar th.monthselect {
    background : none ; 
    border: solid white 2px;
}
/*ruban du haut page acceuil*/
div.topbar {
    background : none
}
.top_nav a {
       border-right : 0.25px solid rgba(220,220,220,0.58);
}
div.inshadow {
    background: none ;
}
div#header {
    background : none ;
    border-bottom : none ;
    box-shadow: none;
}
div.loginblock, div.loginpad {
    background: none;
    color : white;
    border-right : 0.25px solid rgba(220,220,220,0.58);
    border-left : 0.25px solid rgba(220,220,220,0.58);
    border-top : 0.25px solid rgba(220,220,220,0.58);
    border-bottom : 0.25px solid rgba(220,220,220,0.58);
}
/*petite maison barre*/
.speedbar {
    box-shadow: none;
    background-image : none;
    background-color: transparent !important;
}
div.speedbar a {
    color : white;}

div#topnav ul li a {
     border-right : 0.25px solid rgba(220,220,220,0.58);
     color : white ;
}
/*MENU*/
.widget, .widget-content {
    background : none;
    background-color: transparent !important;
    border-color : rgba(250,250,250,0.30);
    box-shadow: none !important ;
}
    
div#toparticle {
    background-color: transparent !important ; 
}
.style {
    background : none !important;
    background-color : transparent !important;
}
/*FILM COUNTOUR*/
a.top-last{
    background-color: transparent !important ;
    border-color : rgba(250,250,250,0.30);
}

a.top-last span.top-genre {
    color : #fff900;
}
span.top-title, a span.top-imdb, .imdbRating {
    color : white;
}
a.welcome-next {
    color: white;
}
span.welcome {
    color : #f7bee7
}
span.welcome.welcome-exclusive {
    color : #fff900;
   }

div#toparticle.tab-content.main-listing {
    border : none ; 
}
hr {
    border-color : rgba(250,250,250,0.30);
}
a.logedbutton {
    background-color: rgba(255,255,255,0.50);
}
/*MENU*/
.blockbox {
    box-shadow: none !important;
    background : none;
    border : none;
}
#sidebar-left .sidetitle, #sidebar-right .sidetitle, .sidepad{
    color : #fff;
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
    background-color: transparent !important;
    background : none;
    box-shadow: none;
    border-right : 1px solid rgba(220,220,220,0.58);
    border-left : 1px solid rgba(220,220,220,0.58);
    border-top : 1px solid rgba(220,220,220,0.58);
    border-bottom : 0.25px solid rgba(220,220,220,0.58);
}
li{
    color : #fff;
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
    background-color: transparent !important;
    background : none;
    box-shadow: none;
    border :none !important;
}
a {
    color: #fff;
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
}

#sidebar-left {
    color: white;
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
}
ul.reset{ 
    border :  1px solid rgba(220,220,220,0.58) ;
}
div#copyrights,.lastpost tr {
    background-color: transparent !important;
    background: transparent !important;
}
.lastpost tr, .thead {
    border-right : 1px solid rgba(220,220,220,0.58);
    border-left : 1px solid rgba(220,220,220,0.58);
    border-top : 1px solid rgba(220,220,220,0.58);
    border-bottom : 0.25px solid rgba(220,220,220,0.58);
}
/*+PALETTE DE COULEUR*/
#maincolumn > div > div:nth-child(n) > div > table:nth-child(n) > thead > tr > td {
   background : none !important;
   background-color: transparent !important;
}
.lastpost-header, td{
    color: white;
    text-shadow: 0 0 0.2em purple, 0 0 0.2em purple, 0 0 0.2em purple;

}
/*bas de page liste */
.lastpost>thead>tr>th, .lastpost>thead>tr>td {
    font-size : 2.5em;
}
.lastpost>tbody>tr>td:first-child {
    font-size: 1.3em;
}
/*barre de recherche au bas de la page*/
input#searchinput.textin, input.change-view {
    font-size: 18px;
    color: grey;
    background-color: transparent !important;
    background: none;
     box-shadow: inset 1px 1px 2px #fcf8e3;
}
/*genre des listes tout en bas de la page*/
.lastpost-genre {
    font-size: 20px;
    font-style: italic;
    color: #fff900; }
/*épisode ajouter page principale*/
a.top-last span.top-episode, .lastpost-episode{
    color : #50ff00;
}
/*ruban bas de page*/
#botmodule, #botscreen {
    background: transparent !important;
    border: none;
    box-shadow: none;
}
h3, .prez_3, .ed3, .prez_2 {
    font-size : 25px;
    color : #fffaaa;
    border-bottom : 1px solid #fffaaa;
}
p.release-name {
    color : #fffaaa;
}
/*film nom*/
h2.blocktitle , .prez_7 {
    font-weight: bold;
    font-size: 32px;
    text-shadow: 0 0 0.2em #87F, 0 0 0.2em #87F, 0 0 0.2em #87F;
    border : none;
    background-color : transparent !important;
    color : #00ff31 ;
}
div.blockheader { 
    background : none;
    border: none;
}
.icon_desc, .rating {
    box-shadow: none;
    border : solid #fcf8e3 1px;
}
.speedbar .speedinner, .sortn .sorted {
    color: #00ffed ;
    font-size : 15px ;
}
/*numero de page*/
.navigation a, .navigation span, .navigation span.comnav {
    background: none !important;
    box-shadow : none;
    color : white;
    font-size : 20px;
   
}
.menu-header, .mlink, div.cominfo, #submit, .ui-widget-header, .related .dtop {
   background : none; 
}
/*encadrement des liens séries films mangas etc*/
#dle-content > div.blockbox > div.blockcontent > div:nth-child(n){
   background: transparent !important;
   border: none;
}
#dle-content > div.blockbox > div.blockcontent > div:nth-child(n) > a:nth-child(n) {
   font-size : 18px;
}
/*ruban dowload*/
div.blockfooter.links {
   background :none; 
   background-color: rgba(240,240,240,0.25);
   border: solid white 1px;
}
/*download butto*/
div.button {
   background-color: transparent;
}
.blocktitle a, .blocktitle a:visited {
    font-weight: bold;
    font-size: 35px;
    text-shadow: 0 0 0.2em #87F, 0 0 0.2em #87F, 0 0 0.2em #87F;
    }
/*section commentaires*/
textarea#comments {
   font-size : 15px;
   background-image : none !important;
   background-color: transparent !important;
   color : white;
}
.commenttitle {
   background: none !important;
   border-bottom : 1px solid #000000 !important;
   border-right : 1px solid #000000 !important;
}
#dle-comments-form > div:nth-child(2){
   background-color : transparent !important;
}
div > div.blockcontent{
color : white !important;
font-weight : normal;
}
.commentsblock, div.blockfooter.combtn > a{
   background-color: rgba(0,250,250,0.30);
   font-size:15px;
   color : black;
   border-bottom : 1px solid #000000 !important;
   border-top : 1px solid #000000 !important;
   border-right : 1px solid #000000 !important;
   border-left : 0.25px solid #000000 !important;
}
.commentfoot {
   border-top : 1px solid #000000 !important;
}
.quote2 {
   background-color : transparent !important;
   color : #ffffff !important;
}
.clr.berrors {
   background-color : rgba(210,241,44,0.45)!important;
   border: solid white 1px;
}
.pheading {
   background : none;
   background-color: rgba(200, 0, 255, 0.32);
}
/*PSEUDO COMMENTAIRE*/
div.commenttitle > a {
   font-size : 25px;
   text-shadow: 0 0 0.2em #87F, 0 0 0.2em #87F, 0 0 0.2em #87F;
   color : pink ;
}
/*LEXIQUE*/
#dle-content > div > div > p:nth-child(n) > span {
   COLOR : white;
   font-size : 18px;
   font-style : italic;
   font-weight : normal !important;
}
/*RECEMMENT RENOUVELE*/
.recentlyrenewed {
   border-collapse : inherit !important;
}
.recentlyrenewed tr:nth-of-type(odd),  .recentlyrenewed tr{
   background-color : transparent !important ;
}
/*MP POP*/
.ui-dialog .ui-dialog-content, .ui-dialog .ui-dialog-buttonpane{
   background-color : rgba(250,50,150,0.50) !important;
   color : white ;
   font-weight : bold;
}
/*MP BOITE*/
div.basecont {
   background-color : transparent;
   background : none;
   border-top : 1px solid green ;
}
.pm_status, .pm_status_head, .pm_status_head, .userstop td, .pm td.pm_list, .pm td.pm_head{
   background-color: transparent!important;
   color : white;
   background : none !important;
}
.pm_progress_bar span {
   background : #00ff31;
}
.pm td.pm_head {
   color : white ;
   font-weight : bold;
   font-size : 18px;
}
.blockfooter, #dle-content > div.commentsblock > div.blockfooter.combtn > a:nth-child(n){
   color : black ;
   border-top : 1px solid black;
   background-color: transparent !important; 
   background-image: none !important;
}
/*QUOTE*/
 div.quote{
   border :  1px solid black;
   background : none;
}
div.title_quote {
   color : black;
   font-style: italic;
   border-color: black;
   border-bottom : 0px;
   background : none;
}

/*SIGNALER*/
div.blockfooter.combtn > a {
   background-color: transparent !important ;
   background-image : none !important;
   color : black;
   font-weight: bold;
   font-size: 18px;
   border-bottom : 1px solid #000000 !important;
   border-top : 1px solid #000000 !important;
   border-right : 1px solid #000000 !important;
   border-left : 0.25px solid #000000 !important;
}
/*SPOILER*/
div.title_spoiler {
   background-color : rgba(70,15,20,0.50);
}
.text_spoiler {
   color : white;
   background-color : rgba(70,15,20,0.50);
}

ul.ussep {
   background-image : none !important;
}
.btn-other {
   background : rgba(100, 100, 150, 0.50);
                                 }
.other-qualities {
   color : #4b4b4b;
}
/*Pub qui conseille de pas DL avec nos vrais IP*/
#dle-content > div.blockbox > div.blockcontent > div:nth-child(n) > div {
   display : none !important;
}

}
