@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr") {

/*Noiz Famiiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/3aa67535290ea4ffad9472d529dc1851/tumblr_on1erh3KZQ1trxrtqo3_500.png") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/dfddfde00870c7a88cefc6224d2548d5/tumblr_on1erh3KZQ1trxrtqo2_500.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Noiz" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Noiz tiene un gran regalo para ti, Â¡Hey a donde crees que vas!?" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Humano" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/bdea76055ecc048ca19483f7e8b928c5/tumblr_on1erh3KZQ1trxrtqo1_75sq.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Pizza" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("http://68.media.tumblr.com/19d117cbd844636b3ea338eaabf7f656/tumblr_n5y55hP4E21rd26muo3_1280.png") no-repeat ;
    background-size : auto 100% ;
    height : 540px !important ;
    width : 200px !important ;
  }
.background-element {
  	  background: url('https://68.media.tumblr.com/e014c88ed770f4b56f5a3cdb19643385/tumblr_on1erh3KZQ1trxrtqo4_r1_1280.png') !important;
  }

#index-player-display {
   	  background: url('https://68.media.tumblr.com/e014c88ed770f4b56f5a3cdb19643385/tumblr_on1erh3KZQ1trxrtqo4_r1_1280.png') !important;   
}

  #player-display-pet > img {display : none !important ;}
}
