css:
@-moz-document domain("portal2.gamesparks.net") {
body {
    background-color: #2d2d2d;
    color: #80a9b1;
}
.page-head {
    background-color: #2d2d2d;
}
.card {
    background-color: #383838;
    border: 1px solid #28333c;
}
.ace-tm {
    background-color: #28333c;
    color: #8463a7;
}
.card-header {
    padding: .5rem 1rem;
    background-color: #2d2d2d;
    border-bottom: 1px solid rgb(74, 93, 109);
}
.ace-tm .ace_gutter {
    background: #2d2d2d;
    color: #984848;
}
.nav-tabs .nav-item.open .nav-link, .nav-tabs .nav-item.open .nav-link:focus, .nav-tabs .nav-item.open .nav-link:hover, .nav-tabs .nav-link.active, .nav-tabs .nav-link.active:focus, .nav-tabs .nav-link.active:hover {
    color: #80a9b1;
    background-color: #28333c;
    border-color: #ddd #ddd transparent;
}

.card-footer, .card-header {
    padding: .5rem 1rem;
    background-color: #2d2d2d;
    border-top: 1px solid rgba(74, 93, 109, 0);
}
.btn-secondary {
    background-color: #6d6d6d;
}
.table-striped tbody tr:nth-of-type(odd) {
    background-color: #444444;
}
.table-bordered, .table-bordered td, .table-bordered th {
    border: 1px solid #2d2d2d;
}
.bootstrap-tagsinput, .form-control {
    background-color: #313131;
}
.ace-tm .ace_storage, .ace-tm .ace_keyword {
    color: #ff8686;
}
.ace-tm .ace_gutter-active-line {
    background-color: rgb(38, 62, 82);
}
.ace-tm .ace_marker-layer .ace_active-line {
    background: rgb(38, 62, 82);
}
.ace-tm .ace_string {
    color: rgb(137, 140, 99);
}
.ace-tm .ace_support.ace_function {
    color: rgb(109, 130, 179);
}
.ace-tm .ace_constant.ace_numeric {
    color: rgb(119, 199, 232);
}
.ace-tm .ace_keyword.ace_operator {
    color: rgb(255, 183, 183);
}
.ace-tm .ace_indent-guide {
    background: url(http://asdaddasd) right repeat-y;
}
.nav-tabs .nav-item.open .nav-link, .nav-tabs .nav-item.open .nav-link:focus, .nav-tabs .nav-item.open .nav-link:hover, .nav-tabs .nav-link.active, .nav-tabs .nav-link.active:focus, .nav-tabs .nav-link.active:hover {
    border-color: #4a5d6d #4a5d6d transparent;
}
.ace-tm .ace_entity.ace_name.ace_function {
    color: #1effb0;
}
.list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #2b1f1f;
    border: 1px solid #ddd;
}
code {
    color: #bd4147;
    background-color: #4c3030;
    border-radius: .25rem;
}
.modal-content {
    background-color: #28333c;
}
.js-tree .boolean {
    color: #1fd2ff;
}
}
