css:
@-moz-document domain("lndb.info") {
body {
    background-image: url('http://i.imgur.com/6NFxSQP.png') !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    background-position: center center !important;
    }

* {
    font-family: segoe ui light !important;
    color: white !important;
    }

a {
    /*
    border-bottom: solid 1px white !important;
    border-image: linear-gradient(to right, rgba(255, 255, 255, 1), transparent) 1 !important;*/
    
    transition: all 0.2s !important;
    color: lightgrey !important;
    }

a:hover {
    color: white !important;
    border-image: linear-gradient(to right, rgba(255, 0, 0, 0.8), transparent) 1 !important;
    }

.fancy-line {
    display: none !important;
    }

.header {
    background-image: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.4), transparent) !important;
    margin: 0px !important;
    padding: 10px !important;
    text-align: center !important;
    border-bottom: solid 1px transparent !important;
    border-image: linear-gradient(to right, transparent, white, transparent) 1 !important;
    margin-bottom: 20px !important;
    }

.header a:after {
    content: 'The Light Novel Database' !important;
    font-size: 12px !important;
    bottom: 0px !important;
    color: white !important;
    }

.header a:before {
    content: 'LNDB' !important;
    font-size: 32px !important;
    color: white !important;
    }

.header a {
    border: none !important;
    }

.header img {
    display: none !important;
    }

.opt_related_titles,
#lightnovel_associated_titles,
#lightnovel_official_site,
#lightnovel_original_character_design,
#lightnovel_original_work,
.ui-autocomplete-input,
.ui-autocomplete-multiselect-item,
#lightnovel_title_japanese,
.bodycontent,
#comment_lightnovel,
.writecommentcontent,
.lightnovelhistory,
.secondary-info-title,
.lightnovelcovers,
.lightnovellabels,
.lightnovelheader,
.maincontent,
.search,
.navigation,
.external-websites,
.login,
.recentreleases,
.recentchanges,
.recentcomments,
.randomlightnovels,
.randomlightnovelcharacters,
.randomlightnoveldiagrams,
.usercontent,
.lightnovelcontent>div,
.personcontent>div {
    border-radius: 0px !important;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.2) !important;
    background-color: rgba(0, 0, 0, 0.5) !important;
    padding: 10px !important;
    border: none !important;
    }

.subtitle,
.secondarytitle,
.main-menu {
    border-bottom: 1px solid white !important;
    border-image: linear-gradient(to right, white, transparent) 1 !important;
    }

.ui-state-default,
.ui-state-active {
    background-color: transparent !important;
    border: none !important;
    border-radius: 0px!important;
    }

.ui-tabs-anchor {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border: none !important;
    padding: 10px !important;
    }
}
