css:
@-moz-document domain("ptchan.org") {
body, .tabuas, #options_div { background: black; color: #b2b2b2; }
.desktop-style div.boardlist:not(.bottom) { background: black; color: white; border-top: 2px solid #4285f4; border-radius: 0; }
a:link { color: #4285f4 !important; text-decoration: none !important; }
a:link:hover { color: #2e5daa !important; }
.info, .caixa.esquerda, .caixa.direita, div.ban, table.announcements-list tbody { background: #0c0c0c; color: white; }
.caixa.esquerda li, .caixa.direita li { color: #b2b2b2; }
div.ban h2, table.modlog tr th, #bcima, #bbaixo, #expa { background: #191919; }
h1, .intro span.subject { color: #006666; }
#bcatalogo, #barquivo, #brecarregar, #bres, #bvoltar { background: black; border-bottom: solid 2px #2e5daa; border-radius: 0; }
hr { border-color: black; }
form[name="post"], table.announcements { color: black; }
#PostAreaToggle + span > a > label, #toggleAnnouncements, #announcements tfoot tr td span { border-bottom: solid 2px #2e5daa; }
div.post.reply, div.pages { background: #0c0c0c; border-color: #191919; }
.intro span.name, div.blotter { color: #00b3b3; }
div.pages #bcatalogo, form table tr th, #u50 { background: #0c0c0c; }
div.pages a.selected { color: white; }
form table tr th { color: white; }
div.post.reply.highlighted { background: #242424; border-color: #242424; }
.thread p a { color: #b2b2b2 !important; }
.thread p { color: #b2b2b2 !important; }
.options_tab_icon { color: white; }
#announcements { color: white; }
#announcements tfoot tr td { color: black; }
.theme-catalog div.thread { background: #0c0c0c !important; }
#omi { background: #191919; }
#update_thread { background: black; }
#aviso { background: black; border: 2px solid #191919; }
#aceito, #naoaceito { background: #191919; color: white; }
div.boardlist a:hover { border-top: 3px solid #b2b2b2; }
}
