css:
@-moz-document domain("www.sharklasers.com") {
body {
    height: 100vh;
    overflow: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    background: url(http://api.thumbr.it/whitenoise-361x370.png?background=333333ff&noise=079bd2&density=15&opacity=15) repeat;
}
pre {
	color:#ccc
}
a {
    color: #079bd2;
}
a:hover {
    color: #21e3bb;
}
.logo {
    margin: 0 0 1.5rem;
}
h3.heading.opaque-box {
    display: none;
}
#stats {
    font-size: 0.63rem;
    font-family: "Fira Code Retina", 'Lato', Georgia, sans-serif;
    line-height: 1.6rem;
    color: #ccc;
    background: none;
    margin: 0;
}
#stats:before {
    content: "";
    height: 1px;
    width: 100%;
    display: block;
    margin-bottom: 1.5rem;
    background-image: linear-gradient(to left, #eb018d 0%, #3a90be 20%, #05c4cb 75%, #05a1dc 100%);
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
}
#guerrilla_mail {
    width:100%;
    background: none;
    margin: 1.5rem 0 auto;
    border-radius: 0;
    box-shadow: none;
}
#guerrilla_mail:before, #guerrilla_mail:after {
    content: "";
    height: 1px;
    width: 100%;
    display: block;
    margin-bottom: 1.5rem;
    background-image: linear-gradient(to left, #eb018d 0%, #3a90be 20%, #05c4cb 75%, #05a1dc 100%);
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
}
.fancy-box {
    display: none;
}
#copyright {
    opacity: 0.2;
    font-family: "Fira Code Retina", 'Lato', Georgia, sans-serif;
    font-size: 0.63rem;
    color: #ccc;
    background: none;
    margin: auto 0 0;
    transition: .2s all;
}
#copyright:hover {
    opacity: 1;
}
span.strong {
    display: inline-block;
    background: #2f98c1;
    color: #ccc;
    border-radius: .5em;
}
.button,
a.button.small,
#gm-host-select,
#inbox-id {
    display: inline-block;
    border-radius: .5rem;
    background: url(http://api.thumbr.it/whitenoise-361x370.png?background=333333ff&noise=079bd2&density=15&opacity=15) repeat;
    box-shadow: none;
    outline: none;
    font-size: .75rem;
    line-height: 1rem;
    padding: 0.75rem 1rem;
    font-family: "Fira Code Retina", 'Lato', Georgia, sans-serif;
    border: 1px solid #2f98c1;
    color: #ccc;
    font-weight: 400;
    transition: .2s all;
}
.button.small:active {
    padding: 0.5em 1em;
    color: #eb018d;
}
.button:hover, .hovered {
    box-shadow: none;
    color: #777;
    background-color: #ccc;
}
span#email-widget {
    background: none;
    box-shadow: none;
    padding: 0;
    line-height: 1em;
    display: inline-flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    margin-top: 1rem;
    border: none;
    font-size: 1.5rem;
    box-sizing: border-box;
    height: 4rem;
    float: left;
    width: 50% !important;
}
span#copy-to-clip-container {
    float: left;
    height: 4rem;
	width:30%;
    padding: 0;
    margin-top: 1rem;
	display: flex;
    justify-content: center;
    align-items: center;
    border: 1px solid #05a1dc;
    border-width: 1px 0;
    box-sizing: border-box;
}
span#email-widget:before,
span#email-widget:after {
    content: "";
    height: 1px;
    width: 100%;
    display: block;
    background-image: linear-gradient(to right, #eb018d 0%, #3a90be 20%, #05c4cb 75%, #05a1dc 100%);
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
}
span#alias-box {
    float: left;
    height: 4rem;
    width: 20%;
    padding: 0;
    margin-top: 1rem;
    vertical-align: middle;
    border: none;
    border-radius: 0;
    background: none;
}
#alias-box input[type="checkbox"] {
    visibility: hidden;
    height: 0;
    width: 0;
    margin: 0;
}
#alias-box input[type="checkbox"]+label {
    height: 100%;
    width: 100%;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    border: solid #eb018d;
    border-width: 1px 0 1px 0;
    border-radius: 0 1em 1em 0;
    position: relative;
    box-sizing: border-box;                       
    background: url("data:image/svg+xml,%3Csvg width='6' height='6' viewBox='0 0 6 6' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23cccccc' fill-opacity='0.4' fill-rule='evenodd'%3E%3Cpath d='M5 0h1L0 6V5zM6 5v1H5z'/%3E%3C/g%3E%3C/svg%3E");
}
#alias-box input[type="checkbox"]:checked+label {
    background-image: url("data:image/svg+xml,%3Csvg width='6' height='6' viewBox='0 0 6 6' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23ec018d' fill-opacity='0.4' fill-rule='evenodd'%3E%3Cpath d='M5 0h1L0 6V5zM6 5v1H5z'/%3E%3C/g%3E%3C/svg%3E");
}
::selection {
    color: rgb(11, 179, 203);
    text-shadow: 0px 1px 1px #eb028d;
    background: rgba(255, 255, 255, 0.1);
}
.tabs-content {clear:both;}
.tab_content, #email_table, .tab-active-space, .email_controls{
	background:transparent;
    background-image: url("data:image/svg+xml,%3Csvg width='6' height='6' viewBox='0 0 6 6' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23cccccc' fill-opacity='0.1' fill-rule='evenodd'%3E%3Cpath d='M5 0h1L0 6V5zM6 5v1H5z'/%3E%3C/g%3E%3C/svg%3E");
    border-color: #10b1ca;
}
.tab_content {
	margin:-1em 0 0 0;
    border: 0 transparent;
}
.tab_content table {border-color: #10b1ca;}
#tab_bar>ul {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 1rem;
    padding: 0;
    box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.2);
}
#tab_bar li {
    border-radius: 0;
    padding: .25em 1em;
    border: solid #10b1ca;
    border-width: 1px 0 1px 0;
    margin: 0;
    background:transparent;
    width: 100%;
    text-align: center;
}
#tab_bar li.current_nav_item, #tab_bar li.current_nav_item:hover, .current_nav_hover {
    border-image: linear-gradient(to left,#10b1ca,#eb048d,#10b1ca) 1;
}
#tab_bar li a:link, #tab_bar li a:visited {
	color:#ccc
}
#tab_bar li.current_nav_item:first-child {
    border-image: linear-gradient(to left,#eb048d,#10b1ca) 1;
}
#tab_bar li.current_nav_item:last-child {
    border-image: linear-gradient(to right,#eb048d,#10b1ca) 1;
}
#tab_bar li a, #tab_bar li.current_nav_item a {
    padding: .5em 1em;
}

span#inbox-id.edit-in-progress {
	display: inline-flex;
    align-items: baseline;
}

#inbox-id.edit-in-progress input{
    border-radius: .5rem;
    padding: 0 0 0.3em 1em;
    font-family: "Fira Code Retina", 'Lato', Georgia, sans-serif;
    font-weight: 400;
    transition: .2s all;
    background: none;
    color: #eb018d;
    border: 2px solid;
    margin: 0 1em 0 0;
}
}
