css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br") {

/*Clear Famiiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://s-media-cache-ak0.pinimg.com/originals/50/7a/ca/507acab17fbb1bd44d22b9bdd91b7dea.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://s-media-cache-ak0.pinimg.com/originals/50/7a/ca/507acab17fbb1bd44d22b9bdd91b7dea.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Clear" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Clear pregunta que quieres de comer, Eh? Porque te sonrojas?" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Androide" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://i959.photobucket.com/albums/ae71/guernika/110213_nikujaga.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Nikujaga" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("http://68.media.tumblr.com/9f9d6350a26197818ea6f530032dc63f/tumblr_n6a18epP6P1td200xo3_500.png") no-repeat ;
    background-size : auto 100% ;
    height : 570px !important ;
    width : 260px !important ;
  }
.background-element {
  	  background: url('https://68.media.tumblr.com/c2d2b382264f756dd78021aea2d229e9/tumblr_on2xozvJSL1trxrtqo1_1280.png') !important;
  }

#index-player-display {
   	  background: url('https://68.media.tumblr.com/c2d2b382264f756dd78021aea2d229e9/tumblr_on2xozvJSL1trxrtqo1_1280.png') !important;   
}

  #player-display-pet > img {display : none !important ;}
}
