css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/message/") {
  

  html,body{
    height:100%!important;
  }
  #page{
    position:relative;
    top:-30px;
    height: 100%;
    z-index:4;
  }
  #page-content{
    z-index:4;
    width:688px!important;
  }

  .paging a{
    display:inline-block;
    width:20px;
    height:20px;
    line-height:20px;
    background-color: #A1A0AD;
    text-decoration: none;
    text-align: center;
    color:white;
  }
  .paging .previous{
    width:auto;
    padding:0 6px;
  }
  .paging .next{
    width:auto;
    padding:0 6px;
  }
  .paging a:hover{
    background-color:#337AB7;
  }
  
  
  /* The All messages and Recent Messages links at top of page */
  .mdl-align.messagehistorytype {
    bottom:271px;
    width:654px!important;;
    border-bottom:rgb(1,84,162) 12px solid !important;
    padding-left:6px;
    border-radius:3px;
    left:18px;
    position: fixed;
    display: block;
    height:22px;
    padding-top:30px;
    z-index:2;
    width:660px;;
  }
  
  /* There are 4 elements within messagehistory - a, span, a span.  The inactive tab is the span and active is the a.  They are turned on and off by switching the "hidddenelement" and "visible" classes.*/
  .mdl-align.messagehistorytype a,
  .mdl-align.messagehistorytype span{
background: linear-gradient(to bottom, rgba(65,125,188,1) 0%,rgb(1,84,162) 100%);
    color:white!important;
    border-top-right-radius:24px!important;
    border-top-left-radius:4px!important;
    padding-left:10px!important;
    padding-top:0px!important;
    margin-top:10px!important;
    margin-bottom:2px!important;
    display:block;
    position: absolute!important;;
    box-shadow: -5px -6px 9px -7px rgba(0,0,0,0.75);
    width:162px;
    top:-3px!important;;
    line-height:8px;
  }
  .mdl-align.messagehistorytype a{
   text-decoration: none;
   background: linear-gradient(to bottom, rgba(22,104,182) 0%,rgb(65,125,218,1) 100%);
    height:20px;
    line-height:15px!important;;
    font-weight:normal;
    top:-0px!important;;
  }
 .mdl-align.messagehistorytype span{
    height:26px;
    top:-6px!important;;
  }
  .mdl-align.messagehistorytype a:nth-of-type(1),
  .mdl-align.messagehistorytype span:nth-of-type(1){
    left:0px;
  }
  .mdl-align.messagehistorytype a:nth-of-type(2),
  .mdl-align.messagehistorytype span:nth-of-type(2){
    left:162px;
  }
  /*a third span appears when click on link saying a new message is available.  The tab will display, "New Message (1)" for example*/
  .mdl-align.messagehistorytype span:nth-of-type(3){
    left:0px;
    line-height: 20px;
  }
  .mdl-align.messagehistorytype a.visible:hover{
    height:26px!important;;
    top:-5px!important;
    line-height: 28px;
    background: linear-gradient(to bottom, rgba(65,125,188,1) 0%,rgb(11,94,192) 100%);
    border-top:2px rgba(40,40,40,.5) solid!important;
    border-left:2px rgba(40,40,40,.5) solid!important;
    border-right:2px rgba(40,40,40,.5) solid!important;
  }
  
  
  /* drop down of student contacts */
  .contactselector.mdl-align{
    position:fixed !important;
    width: 284px !important;
    bottom:12px !important;
    left:700px;
    z-index:4;
    background:rgb(1,84,162);
    background: #17227d; /* Old browsers */
    background: -moz-linear-gradient(-45deg, #17227d 0%, #0154a2 100%);
    background: -webkit-linear-gradient(-45deg, #17227d 0%,#0154a2 100%);
    background: linear-gradient(135deg, #17227d 0%,#0154a2 100%);
    background: linear-gradient(135deg, #0154a2 0%,#3E457C 100%);
    color:white !important;
    margin:0px !important;
    padding:0px !important;
    padding-top:3px !important;
    padding-left:3px !important;
    -webkit-box-shadow: -8px -8px 30px -10px rgba(0,0,0,0.75);
    box-shadow: -8px -8px 30px -10px rgba(0,0,0,0.75);
    border-radius:8px;
    border:2px rgb(1,84,162) solid;
    
  }
  .contactselector.mdl-align:hover{
    overflow:visible;
  }
  .singleselect label{
    display:none;
  }
  .select.autosubmit.singleselect {
    width:276px !important;
    margin:0px!important;
    margin-left: 1px;
    padding:2px!important;
    height:24px;
  }
  .messageselecteduser{
    color:white;
  }
  ul#message-offlinecontacts.message-contacts *, ul#message-onlinecontacts.message-contacts *{
    margin-top:0px !important;
    padding-top:0px !important;
  }
  ul#message-offlinecontacts.message-contacts .pix, ul#message-onlinecontacts.message-contacts .pix{
    padding-left:2px;
  }
  ul.message-contacts{
    padding-right:4px!important;
  }
  ul.message-contacts .contact{
    padding-left:2px!important;
    color:white!important;
  }
  .contact>a{
    padding-left:2px!important;
    color:white!important;
  }
  ul#message-offlinecontacts.message-contacts .contact a,  
  ul#message-onlinecontacts.message-contacts .contact a{
    display:block;
    width:300px;
    text-decoration:none;
  } 
  
  ul#message-offlinecontacts.message-contacts .contact a:hover, 
  ul#message-onlinecontacts.message-contacts .contact a:hover{
    height:100%;
    width:100%;
  } 
  .message-contacts strong{
    display:inline-block;
    width:auto;
    max-width:247px;
    border:3px red solid;
    -webkit-box-shadow: -11px 10px 29px -7px rgba(0,0,0,0.75);
    box-shadow: -11px 10px 29px -7px rgba(0,0,0,0.75);    
  }
  .message-contacts{
    background:none;
  }
  .box.message .contactselector .message-contacts li:nth-child(2n+1){
    background:none;
  }
  .box.message .contactselector .message-contacts li{
    margin:0px !important;
    line-height:18px !important;
    padding:2px 0px!important;;
    background-color:none;
  }

  .box.message .contactselector .message-contacts li .pix .userpicture{
    height:18px !important;
    width:18px !important;
  }
  .box.message .contactselector .message-contacts li:nth-child(2n) {
    background-color: none;
  }
    .box.message .contactselector .message-contacts li:nth-child(2n+1) {
      background-color: none;
  }
  .box.message .contactselector .message-contacts li {
      border-top: 1px solid #ddd;
  }
  .box.message .contactselector .message-contacts li:hover, 
  ul#message-offlinecontacts.message-contacts .pix:hover,
  .box.message .contactselector .message-contacts li:nth-child(2n):hover,
  .box.message .contactselector .message-contacts li:nth-child(2n+1):hover{
    background-color: rgb(171,13,41) !important;
  }
  .box.message .contactselector .message-contacts{
    margin-bottom:3px!important;
  }

  
  
  /*
  Conversations down the page me and them
  */
  .messagearea.mdl-align{
    padding:0px 0px 0px 0px  !important;
    width:100%!important;
    background-color:white;
  }
  .messagearea.mdl-align>.mdl-left.messagehistory{
    border-bottom:320px rgba(10,50,255,.2) solid;
    border-radius: 4px;
    padding-bottom:4px!important;
  }
  .mdl-left.messagehistory>div:last-child::after{
    margin-bottom:0px!important;
  }

  .box.message .messagearea{
    width:678px !important;
    margin-left:10px;
  }
  .message.other{
    width:630px !important;
    left:18px !important;
    border: 2px  rgba(0,180,00,.8)  solid !important;
    border-top: 0px blue solid !important;
    margin-bottom:2px;
    border-radius:8px;
    background-color: rgba(0,256,0,.02);
  }
  .message.me{
    width:630px !important;
    left:38px !important;
    background-color: rgba(0,0,255,.02);
    border: 2px  rgba(0,40, 250,.8)  solid !important;
    border-top: 0px solid !important;
    margin-bottom:2px;
    border-radius:8px;
  }
  .deleteicon{
    height:0px;
  }
  .deleteicon a{
    position:relative;
    top:-28px;
    left:9px;
    padding:0px 3px 6px 3px  !important;
    margin:0px!important;
    margin-bottom:4px!important;
    border-radius:5px; 
    background-color: rgba(256,0,0,.3);
  }
  
  .message.other .text, 
  .message.me .text{
    padding:0px !important;
    padding-bottom:4px!important;
    padding-top:2px!important;
  }
  /* The time and date headings */
  h4.mdl-align{
    margin:0px; 
    padding:0px; 
    margin-top:4px;
    margin-bottom:4px;
    font-size: 14px;
    font-weight: bold;
  }
  .time{
    margin:0px; 
    padding:0px; 
    display:inline-block;
    position:relative;
    left:-2px;
    line-height:16px;
    -webkit-box-shadow: -2px 5px 5px -7px rgba(0,0,0,0.75);
    box-shadow: -2px 5px 5px -7px rgba(0,0,0,0.75);
  }
  .text{
    border:0px solid black !important; 
  }

  
  
  /* The Send Message box */
  .mdl-align.messagesend{
    position:fixed !important;
    background-color: white!important;
    bottom:10px;
    left:10px;
    width:668px;
    z-index:5;
    border:5px solid rgb(1,84,162)!important;
    border-top:8px solid rgb(1,84,162)!important;
    border-radius:8px;

  }
  #id-message{
    background-color:white!important;
    background-image:none!important;
  }
  .mdl-align.messagesend .fitemtitle{
    padding-left:6px!important;
    padding-bottom:6px!important;
    margin:0px!important;
    border-radius:8px;
    height:24px;
  }
  .box.message .messagearea textarea{
    color:black;
  }
  .mdl-align.messagesend .fitemtitle label{
    display:none; 
  }
  .link{
    position:absolute;
    right:0px;
  }
  .link span{
    display:inline-block;
    float:left;
  }
  #id_submitbutton{
    height:28px;
    line-height:28px;
    padding:0px 4px;
    margin:0px;
  }
  

  
  /* the details about the current contact */
  .box.between{
    display:none;
  }
  .box.user #user1{
    display:none;
  }
  .box.user #user2{
    position:fixed;
    bottom:100px;
    left:0px;
    z-index:11;
  }
  .box.user #user2 .heading{
    position: absolute;
    left:18px;
    top:-176px;
    height:26px;
    line-height:28px;
    width:652px;
    padding-left:4px;
    font-size:18px;
    text-align: left;
    background-color:white;
    border-radius: 4px;
  }
  .box.user #user2 .useractionlinks{
    position: absolute;
    left:506px;
    top:35px;
    width:215px;
    display:none;
  } 
  .box.user #user2 .userpicture:hover{
    width:140px;
    height:140px;
    top:-290px;
    left:532px;
  }
  .box.user #user2 .userpicture{
    border-radius:4px;
    position:absolute;
    display:block;
    left:616px;
    top:-206px;
    width:56px;
    height:56px;
    border-radius:6px;
    resize: both;
    overflow: auto;
    z-index:6;
    border:thin rgba(50,50,50,.3) solid;
    padding:1px;
    box-shadow: -5px -6px 9px -7px rgba(0,0,0,0.75);
}
  .blockcontact{
    display:block;
    border-radius: 4px;
  }
  
  /* Search form */
  .message_heading.mdl-left{
    position: absolute;
    top:60px;
  }
  /*Advanced search page */
  .messagearea.mdl-align{
    position:absolute!important;;
    top:-0px;
  }
  .message_heading .heading{
    display:none;
  }
  .message_form > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(2){
    width:160px;
  }
  .message_form > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1){
    width:100px;
  }
  .message_form > tbody:nth-child(1) > tr:nth-child(7) > td:nth-child(3) > input:nth-child(1){
  }
  
  /* PageFooter, which appears to be empty always */
  #page-footer{
    position:fixed !important;
    bottom:400px !important;
    height:110px;
    width:800px;
    background-color: red!important;
    border:none;
    display:none;
  }
}
