@namespace url(http://www.w3.org/1999/xhtml);
/*Sylveon Familiar*/ 

/* i really want this to be global */
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://s-media-cache-ak0.pinimg.com/564x/3e/c9/b6/3ec9b6f8919347465e49a5814d5ac9b6.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://s-media-cache-ak0.pinimg.com/736x/a5/6a/94/a56a943ee5b6c98b323391e51ca26e35.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Sylveon" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Besos de caramelo!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Evolucion de Eevee tipo hada" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://pre06.deviantart.net/6f82/th/pre/f/2016/145/1/8/amie_town_poke_puff_logo_by_snowdoe-d9ag3h0.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Pokelitos" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Sylveon sur la page du profil*/

  #player-display-pet {
    background : url("http://vignette1.wikia.nocookie.net/pokemonreloaded/images/6/63/Sylveon.png/revision/latest?cb=20131224195323&path-prefix=es") no-repeat ;
    background-size : auto 100% ;
    height : 268px !important ;
    width : 368px !important ;
  }

  #player-display-pet > img {display : none !important ;}
