@namespace url(http://www.w3.org/1999/xhtml);
/*Perla Azul Familiar*/  
 
/* i really want this to be global */

  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("http://pm1.narvii.com/6147/c0f20f634c3a3ba1f042a236531492fdd47e0e91_hq.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("http://pm1.narvii.com/6147/c0f20f634c3a3ba1f042a236531492fdd47e0e91_hq.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Perla Azul" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : "A sus ordenes mi diamante" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Gema" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/83ca4a3e84f3c4baec32b791ecfc0354/tumblr_omtqlobjp41trxrtqo2_75sq.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Miel Estelar" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Perla Azul sur la page du profil*/

  #player-display-pet {
    background : url("http://vignette4.wikia.nocookie.net/universosteven/images/b/ba/Current-Blue_Diamond%27s_Pearl.png/revision/latest?cb=20160207050650&path-prefix=es") no-repeat ;
    background-size : auto 100% ;
    height : 400px !important ;
    width : 200px !important ;
  }

  #player-display-pet > img {display : none !important ;}
