@namespace url(http://www.w3.org/1999/xhtml);
/*Ken Kaneki Familiar*/  

/* i really want this to be global */
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://s-media-cache-ak0.pinimg.com/originals/ff/cf/83/ffcf835c003684703b7a1787017b2886.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://s-media-cache-ak0.pinimg.com/originals/ff/cf/83/ffcf835c003684703b7a1787017b2886.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Ken Kaneki" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : "Kaneki es sangriento no te enamores" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Ghoul" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://vignette1.wikia.nocookie.net/zombie/images/8/8f/Sangre1.png/revision/latest?cb=20131215215920&path-prefix=es") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Sangre" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ken Kaneki sur la page du profil*/

  #player-display-pet {
    background : url("http://vignette3.wikia.nocookie.net/familiar-hda/images/d/d2/Ken_Kaneki_Root_A.png/revision/latest?cb=20150227035131&path-prefix=es") no-repeat ;
    background-size : auto 100% ;
    height : 520px !important ;
    width : 300px !important ;
  }

  #player-display-pet > img {display : none !important ;}
