css:
@-moz-document url-prefix("https://www.nyaa.se/"), url-prefix("https://sukebei.nyaa.se/") {
div#main>div:first-of-type,
div#main>div:nth-of-type(2),
div#main>div:nth-of-type(4) {
    display: none;
}
* {
    box-sizing: border-box;
}
body {
    background: #8493a8;
    min-width: 0;
    font-family: Open Sans;
    font-size: 1em;
    padding-top: 0;
}
div#main {
    width: 100%;
    max-width: 1000px;
    min-width: 70%;
    overflow: auto;
    padding-top: 50px;
}
div#topbar {
    top: 0;
    background: #323742;
    height: 50px;
}
div#topbar ul#tabnav {
    margin-left: 0;
}
div#topbar ul#tabnav li {
    display: inline-block;
}
div#topbar ul#tabnav li a {
    padding: 0 15px;
    display: inline-block;
    line-height: 50px;
}
div#searchcontainer {
    float: right !important;
    padding-right: 20px!important;
}
.content {
    width: 100%;
    border: none;
    margin: 0;
    padding: 20px;
    border-radius: 0;
}
.content.trusted {
    background-color: #dff0d8;
}
.content.remake {
    background-color: #ffcccc;
}
.content.aplus {
    background-color: #aedbff;
}
#tabnav a {
    line-height: 1em;
    padding: 14px 9px;
}
div#searchcontainer {
    padding: 0;
    float: left;
    width: auto;
}
ul#tabnav {
    float: none;
    margin: 0px 12px 0px 8px;
}
select.inputsearchcategory,
input.inputsearchterm,
select[name="filter"] {
    margin: 10px 0 5px 0;
    height: 30px;
    width: auto;
    padding-left: 5px;
    border: none;
    border-radius: 3px;
}
input.inputsearchsubmit {
    margin-left: 4px;
    margin-top: 10px;
    height: 30px;
    padding: 0 15px;
    font-size: .735em;
    letter-spacing: 1px;
    width: auto;
    cursor: pointer;
    background: #cad3df;
    border: none;
    border-bottom: 1px solid #adb9ca;
    text-transform: uppercase;
    border-radius: 3px;
}
.refinesearch {
    background-color: #cad3df;
    padding: 20px;
    margin: 0;
}
.refinesearch br {
    display: none;
}
.refinesearch b {
    padding-right: 15px;
}
.notice {
    display: block;
    background-color: #3d4d65;
    color: #FFF;
}
input[value="Search"] ~ br,
.notice ~ br {
    display: none;
}
.viewdownloadtxtbutton,
.viewdownloadbutton {
    width: auto!important;
}
.viewdownloadtxtbutton a,
.viewdownloadbutton a {
    padding: 0 15px!important;
    line-height: 40px;
    display: inline-block;
    text-transform: uppercase;
    color: #FFF;
    border-radius: 5px;
}
.viewdownloadbutton a {
    background-color: #8cc200;
    border-bottom: 1px solid #78a600;
}
.content.remake .viewdownloadbutton a {
    background-color: #de3314;
    border-bottom: 1px solid #af260d;
}
.viewdownloadbutton a::after {
    content: 'Download';
}
.viewdownloadtxtbutton a {
    background-color: #003366;
    border-bottom: 1px solid #002b57;
}
.viewdownloadtxtbutton a::after {
    content: '.txt';
}
.viewdownloadbutton a img,
.viewdownloadtxtbutton a img {
    display: none;
}
.tlistsortorder {
    background: #eceff4;
}
.torrentcats,
.tlistsortorder {
    padding: 0;
    width: 100%;
    text-align: center;
}
.torrentcats *,
.tlistsortorder * {
    display: inline-block
}
.torrentcats a,
.tlistsortorder a {
    padding: 0 15px;
    line-height: 30px;
    border: 1px solid #cad3df;
    border-radius: 5px;
}
.tlistsortorder b {
    padding: 0 15px;
    line-height: 30px;
    border: 1px solid #cad3df;
    border-radius: 5px;
    background: #eceff4;
}
.torrentcats {
    padding-bottom: 10px;
}
.tlistsortorder {
    border-top: 1px solid #cad3df;
    padding-top: 10px;
    padding-bottom: 10px;
}
.leftpages {
    text-align: left;
}
.rightpages {
    text-align: right;
}
.leftpages .page,
.rightpages .page {
    border: 1px solid #cad3df;
    padding: 5px 0;
    width: 40%;
    display: inline-block;
    text-align: center;
    margin: 0;
}
.pages {
    text-align: center;
}
.pages b,
.pages a.page {
    display: inline-block;
    padding: 5px;
    border: 1px solid #cad3df;
}
.pages b {
    background-color: #cad3df;
}
.tlist {
    border: 10px solid #eceff4;
    background-color: #eceff4;
}
.tlist * {
    padding: 0;
    margin: 0;
    border: none;
}
.tlist td {
    padding: 10px 3px;
}
.tlisthead {
    background: #54657e;
    height: 30px;
    line-height: 30px;
    color: #FFF;
    text-align: center;
}
.tlistrow {
    background: #f5f6f7;
    border-bottom: 1px solid #adb9ca;
}
.tlistrow.trusted {
    background: #dff0d8;
}
.tlistrow.remake {
    background: #ffcccc;
}
.tlistrow.aplus {
    background: #aedbff;
}
.tlistththree {
    width: 40px;
}
[title="Literature >> Raw"] img,
[title="Anime >> Raw"] img,
[title="Anime >> Anime Music Video"] img,
[title="Anime >> English-translated"] img,
[title="Anime >> Non-English-translated"] img,
[title="Audio >> Lossless"] img,
[title="Audio >> Lossy"] img,
[title="Literature >> Non-English-translated"] img,
[title="Literature >> English-translated"] img,
[title="Live Action >> Raw"] img,
[title="Live Action >> Non-English-translated"] img,
[title="Live Action >> English-translated"] img,
[title="Live Action >> Idol/Promotional Video"] img,
[title="Pictures >> Graphics"] img,
[title="Pictures >> Photos"] img,
[title="Software >> Games"] img,
[title="Software >> Applications"] img,
[title="Art >> Doujinshi"] img,
[title="Art >> Games"] img,
[title="Real Life >> Videos"] img,
[title="Art >> Anime"] img,
[title="Art >> Manga"] img,
[title="Art >> Pictures"] img,
[title="Real Life >> Photobooks & Pictures"] img {
    display: none;
}
[title="Literature >> Raw"],
[title="Anime >> Raw"],
[title="Anime >> Anime Music Video"],
[title="Anime >> English-translated"],
[title="Anime >> Non-English-translated"],
[title="Audio >> Lossless"],
[title="Audio >> Lossy"],
[title="Literature >> Non-English-translated"],
[title="Literature >> English-translated"],
[title="Live Action >> Raw"],
[title="Live Action >> Non-English-translated"],
[title="Live Action >> English-translated"],
[title="Live Action >> Idol/Promotional Video"],
[title="Pictures >> Graphics"],
[title="Pictures >> Photos"],
[title="Software >> Games"],
[title="Software >> Applications"],
[title="Art >> Doujinshi"],
[title="Art >> Games"],
[title="Real Life >> Videos"],
[title="Art >> Anime"],
[title="Art >> Manga"],
[title="Art >> Pictures"],
[title="Real Life >> Photobooks & Pictures"] {
    display: block;
    color: #FFF;
    text-align: center;
}
[title="Literature >> Raw"]::after,
[title="Anime >> Raw"]::after,
[title="Anime >> Anime Music Video"]::after,
[title="Anime >> English-translated"]::after,
[title="Anime >> Non-English-translated"]::after,
[title="Audio >> Lossless"]::after,
[title="Audio >> Lossy"]::after,
[title="Literature >> Non-English-translated"]::after,
[title="Literature >> English-translated"]::after,
[title="Live Action >> Raw"]::after,
[title="Live Action >> Non-English-translated"]::after,
[title="Live Action >> English-translated"]::after,
[title="Live Action >> Idol/Promotional Video"]::after,
[title="Pictures >> Graphics"]::after,
[title="Pictures >> Photos"]::after,
[title="Software >> Games"]::after,
[title="Software >> Applications"]::after,
[title="Art >> Doujinshi"]::after,
[title="Art >> Games"]::after,
[title="Real Life >> Videos"]::after,
[title="Art >> Anime"]::after,
[title="Art >> Manga"]::after,
[title="Art >> Pictures"]::after,
[title="Real Life >> Photobooks & Pictures"]::after {
    display: block;
    line-height: 30px;
    border-radius: 5px;
}
[title="Anime >> Raw"]::after {
    background-color: #999;
    content: '生';
}
[title="Literature >> Raw"]::after {
    background-color: #6e8400;
    content: '文献';
}
[title="Anime >> Anime Music Video"]::after {
    background-color: #d251e7;
    content: 'AMV';
}
[title="Anime >> English-translated"]::after {
    background-color: #7300a6;
    content: 'E-SUB';
}
[title="Anime >> Non-English-translated"]::after {
    background-color: #8cc200;
    content: 'F-SUB';
}
[title="Audio >> Lossless"]::after {
    background-color: #ff9900;
    content: 'LLESS';
}
[title="Audio >> Lossy"]::after {
    background-color: #de3314;
    content: 'LOSSY';
}
[title="Literature >> Non-English-translated"]::after {
    background-color: #496500;
    content: 'NE-LIT';
}
[title="Literature >> English-translated"]::after {
    background-color: #bd7100;
    content: 'E-LIT';
}
[title="Live Action >> Raw"]::after {
    background-color: #cad3df;
    content: 'LIV-RAW';
}
[title="Live Action >> Non-English-translated"]::after {
    background-color: #adb9ca;
    content: 'LIV-N-ENG';
}
[title="Live Action >> English-translated"]::after {
    background-color: #003366;
    content: 'LIV-ENG';
}
[title="Live Action >> Idol/Promotional Video"]::after {
    background-color: #a5895f;
    content: 'IDL-PROM';
}
[title="Pictures >> Graphics"]::after {
    background-color: #b70051;
    content: 'Graphics';
}
[title="Pictures >> Photos"]::after {
    background-color: #955d76;
    content: 'Photos';
}
[title="Software >> Games"]::after {
    background-color: #07729b;
    content: 'Games';
}
[title="Art >> Doujinshi"]::after {
    background-color: #de3314;
    content: 'Doujinshi';
}
[title="Art >> Games"]::after {
    background-color: #de3314;
    content: 'Games';
}
[title="Software >> Applications"]::after {
    background-color: #2698c3;
    content: 'App';
}
[title="Real Life >> Videos"]::after {
    background-color: #de3314;
    content: 'Videos';
}
[title="Art >> Anime"]::after {
    background-color: #de3314;
    content: 'Anime';
}
[title="Art >> Manga"]::after {
    background-color: #de3314;
    content: 'Manga';
}
[title="Art >> Pictures"]::after {
    background-color: #de3314;
    content: 'Pictures';
}
[title="Real Life >> Photobooks & Pictures"]::after {
    background-color: #de3314;
    content: 'Photobooks';
}
[title="Download"] img {
    display: none;
}
[title="Download"] {
    display: block;
    height: 30px;
    width: 30px;
}
[title="Download"]::after {
    content: '↓';
    background-color: #8cc200;
    color: #fff;
    border-radius: 5px;
    height: 30px;
    width: 30px;
    text-align: center;
    line-height: 30px;
    display: block
}
.tlisticon {
    padding: 5px;
}
}
