css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Hatsune Miku Familiar*/  

/* i really want this to be global */
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("http://images6.fanpop.com/image/photos/34200000/HATSUNE-MIKU-hatsune-miku-34273853-371-500.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://s-media-cache-ak0.pinimg.com/originals/db/8e/90/db8e9047ace5ab6a7d40ae2cf64da4bc.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Hatsune Miku" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : "Music" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Cantante virtual" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://4.bp.blogspot.com/_oi0Hgx38i6s/TIa1PSpKasI/AAAAAAAACEk/f4pECwpVsC8/s1600/Picture+3.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Pudding" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Hatsune Miku sur la page du profil*/

  #player-display-pet {
    background : url("http://vignette1.wikia.nocookie.net/deathbattlefanon/images/9/93/Hatsune_Miku.png/revision/latest?cb=20160128222920&format=original") no-repeat ;
    background-size : auto 100% ;
    height : 510px !important ;
    width : 450px !important ;
  }

  #player-display-pet > img {display : none !important ;}
