css:
@-moz-document domain("mel.fm") {
/* COLOR */

p span, p a {
	background-color: transparent !important;
}

.l-post__frontpage-wrapper,
.b-subscribe-newsletter__input-email, .l-publication__frontpage-wrapper {
  background-color: #1a1a1a;
}

/* 262626 */
body, .i-layout, .i-layout__content, .i-layout__footer, .i-layout_page_feed .i-layout,
.l-article,
.l-article__frontpage-wrapper,
.l-frontpage,
.b-footer_pablo.b-footer_white-theme.b-footer,
.g-dropdown_pablo.g-dropdown_select,
.l-post, .l-publication {
  background-color: #202020;
}

/* 333333 */

.b-popular-authors,
.i-control.b-pb-advertising.b-pb-advertising_pablo_mel.b-pb-advertising_pablo,
.b-pb-article-card,
.b-pb-frontpage__recent-publications,
.b-pb-article-fresh_pablo.b-pb-article-fresh,
.g-modal_pablo .g-modal__content,
.g-auth-social_pablo.g-auth-social,
.g-dropdown_pablo.g-dropdown_menu-theme .g-dropdown__content,
.g-input_pablo .g-input__input,
.g-dropdown_pablo.g-dropdown_select .g-dropdown__opener,
.g-dropdown_pablo .g-dropdown__content,
.g-list_select .g-list__item,
.b-pb-publication-body .b-pb-publication-body__background,
.g-button_pablo.g-button_round,
.EmbeddedTweet,
div#twitter-widget-0,
.EmbeddedTweet-tweet,
.b-checkbox_pablo.b-checkbox,
.l-feed__subheader,
.b-tag-cloud_pablo.b-tag-cloud,
.b-pb-radio_pablo .b-pb-radio__variant_chacked,
.b-cookie-notification, .g-textarea.g-textarea_pablo.g-textarea_padding_comment, .b-permission-message, .b-coach-mark {
  background-color: #333333 !important;
}

.b-coach-mark .b-coach-mark__anchor {
	color: #333;
}

.b-popular-authors,
.i-control.b-pb-advertising.b-pb-advertising_pablo_mel.b-pb-advertising_pablo,
.b-pb-article-card,
.b-pb-frontpage__recent-publications,
.b-pb-article-fresh_pablo.b-pb-article-fresh,
.g-modal_pablo .g-modal__content, 
.g-dropdown_pablo.g-dropdown_menu-theme .g-dropdown__content {
  box-shadow: 0 0 10px #1d1d1d;
}

.b-pb-article-fresh_pablo.b-pb-article-fresh {
  box-shadow: none;
}

.b-floatbar_pablo.b-floatbar {
  background-color: rgba(36, 36, 36, 0.9);
}

.b-pb-article-fresh_pablo.b-pb-article-fresh .b-pb-article-fresh__article-all-news {
  background: none;
}

/* CDCDCD */
.g-list_pablo.g-list_categories-theme.g-list_dark .g-list__item,
.b-teaser .b-teaser__caption,
.b-pb-author_pablo .b-pb-author__link,
.b-popular-authors__caption,
.b-floatbar_pablo .b-floatbar__title,
.b-comments_pablo .b-comments__header,.b-simple-comment_pablo .b-simple-comment__author-url, .b-simple-comment_pablo .b-simple-comment__reply-to-author-url,
.b-pb-article-card_half .b-pb-article-card__title,
.b-pb-article-card_icon .b-pb-article-card__title,
.b-pb-article-fresh_pablo.b-pb-article-fresh .b-pb-article-fresh__caption,
.b-welcome__welcome-caption,
.b-suggestion-feed__tags-caption,
.b-suggestion-feed__authors-caption,
.b-footer_pablo.b-footer_white-theme .b-footer__link, .b-footer_pablo.b-footer_white-theme .b-footer__service-pages-item,
.g-list_pablo.g-list_dropdown-menu-theme .g-list__item, .g-list_pablo.g-list_dropdown-menu-theme .g-list__item_selected,
.g-tab.g-tab_pablo .g-tab__tab,
.b-profile__title,
.b-profile__label,
.g-input_pablo .g-input__input,
.g-list_select .g-list__item,
.b-profile__subtitle,
.b-pb-author_big .b-pb-author__content,
.b-blog__no-publications,
.b-blog__no-publications-title,
.b-article-preview_full .b-article-preview__title,
.b-article-preview_full .b-article-preview__comments-count, .b-article-preview_full .b-article-preview__read-time-minutes, .b-article-preview_full .b-article-preview__views-counter,
.b-pb-article_pablo .b-pb-article__title,
.b-pb-article_pablo .b-pb-article__comments-count, .b-pb-article_pablo .b-pb-article__read-time-minutes, .b-pb-article_pablo .b-pb-article__views-count,
.g-textarea.g-textarea_pablo .g-textarea__text-container, .g-textarea.g-textarea_pablo .g-textarea__textarea,
.b-checkbox__checked-label,
.g-auth-social.g-auth-social_pablo .g-auth-social__terms-caption,
.g-auth-social.g-auth-social_pablo .g-auth-social__header,
.b-auth-email__tab,
.b-checkbox_pablo.b-checkbox,
.b-password-recovery-request .b-password-recovery-request__title,
.b-password-recovery-request .b-password-recovery-request__page-done, .b-password-recovery-request .b-password-recovery-request__page-email,
.b-password-recovery .b-password-recovery__title,
.b-password-recovery .b-password-recovery__page-done, .b-password-recovery .b-password-recovery__page-password,
.l-feed__subheader-frame,
.b-subheader__description,
.b-article-preview_pablo .b-article-preview__source-title a,
.b-tag-cloud_pablo.b-tag-cloud,
.b-tag-cloud_pablo .b-tag-cloud__caption,
.b-article-preview_full .b-article-preview__reason-phrase,
.g-auth-social.g-auth-social_pablo.g-auth-social_pablo_mel .g-auth-social__extra-text,
.b-pb-article_pablo .b-pb-article__ctrl-enter-message, .b-permission-message__message, .b-coach-mark, .b-pb-author_pablo.b-pb-author_big .b-pb-author__content {
  color: #cdcdcd;
}

#Group-3, #comments path, #time_icon, .g-icon_img_attention, .g-icon_img_close-notification, .g-icon_img_share-close, .b-permission-message__bell, .g-icon_img_logo-black-x {
	filter: invert(1);
}

.b-pb-article_pablo .b-pb-article__line {
  background-color: rgba(255, 255, 255, 0.2);
}

/* B3B3B3 */
p, 
.b-pb-publication-body,
.b-pb-publication-body__lead,
.b-pb-image__caption,
.g-button_pablo.g-button_inline-theme,
.b-pb-author_pablo.b-pb-author_type_compressed .b-pb-author__content .b-pb-author__title,
.b-simple-comment_pablo.b-simple-comment,
.b-pb-article-card__link.b-pb-article-card_hoverable:hover, .b-pb-article-card__link:active, .b-pb-article-card__link:link, .b-pb-article-card__link:visited,
.b-pb-article-fresh_pablo.b-pb-article-fresh .b-pb-article-fresh__article-title,
.g-auth-social_pablo.g-auth-social,
.g-dropdown_pablo.g-dropdown_select .g-dropdown__opener-text,
.g-dropdown_pablo .g-dropdown__content,
.b-article-preview_full .b-article-preview__subtitle,
.b-article-preview_full .b-article-preview__publication-date_highlighted, 
.b-article-preview_full .b-article-preview__publication-date, .b-article-preview_full .b-article-preview__publication-time,
.b-article-preview_full .b-article-preview__announcement,
.b-publication-link .b-publication-link__caption,
.b-pb-article_pablo .b-pb-article__subtitle,
.b-tag-list_pablo .b-tag-list__name,
.relap-mel_fm__list__block-title,
.b-subscribe-newsletter__input-email,
.b-subscribe-newsletter_pablo.b-subscribe-newsletter_popup-theme .b-subscribe-newsletter__input-email::-webkit-input-placeholder,
.b-pb-survey_pablo .b-pb-survey__question,
.b-pb-survey_pablo .b-pb-survey__counter,
.b-pb-radio_pablo .b-pb-radio__label,
.b-pb-radio_pablo .b-pb-radio__caption,
.b-pb-survey-result_pablo .b-pb-survey-result__total-score,
.b-pb-survey-result_pablo .b-pb-survey-result__social-title,
.b-pb-survey-result_pablo .b-pb-survey-result__content, .b-footer_white-theme .b-footer__copyright, .b-footer_white-theme .b-footer__link {
  color: #b3b3b3 !important;
}


.g-button__label {
	color: #fff !important;
}

/* BORDER */
.b-pb-article__cover,
.b-pb-author_pablo.b-pb-author_type_compact .b-pb-author__image-container,
.b-popular-authors,
.b-pb-article_pablo .b-pb-article__banners-item,
.b-pb-publication-body hr,
.g-textarea.g-textarea_pablo.g-textarea_border-color_lite,
.b-pb-frontpage__card,
.b-pb-frontpage__recent-publications,
.b-pb-frontpage__cover,
.b-pb-author_pablo .b-pb-author__highlighting, .b-pb-author_pablo .b-pb-author__image,
.g-dropdown_pablo.g-dropdown_menu-theme .g-dropdown__content,
.g-textarea.g-textarea_pablo {
  border: 1px solid rgba(255, 255, 255, 0.05);
  border-radius: 4px;
}

.b-floatbar_pablo.b-floatbar,
.relap-mel_fm-wrapper,
.l-article__frontpage-wrapper,
.b-pb-article-fresh_pablo.b-pb-article-fresh .b-pb-article-fresh__article,
.b-suggestion-feed__author-frame,
.b-footer_pablo.b-footer_white-theme.b-footer,
.b-header_pablo.b-header .b-header__logout-button,
.g-input_pablo .g-input__input,
.g-dropdown_pablo.g-dropdown_select .g-dropdown__opener,
.g-tab.g-tab_pablo .g-tab__tabs,
.b-blog__author-info,
.b-author__author-info,
.b-article-feed_pablo.b-article-feed .b-article-feed__news, .b-article-feed_pablo.b-article-feed .b-article-feed__publication {
  border-color: rgba(255, 255, 255, 0.05);
  border-radius: 4px;
}

.g-button_pablo.g-button_round,
.b-pb-advertising.b-pb-advertising_type_bottom-leaderboard,
.b-password-recovery-request .b-password-recovery-request__title,
.b-password-recovery .b-password-recovery__title,
.l-author__article-preview, .l-author__author, .l-blog__article-preview, .l-blog__author, .l-feed__subheader,
.b-tag-cloud_pablo.b-tag-cloud {
  border-color: rgba(255, 255, 255, 0.05);
}

.b-subscribe-newsletter_pablo.b-subscribe-newsletter_popup-theme .b-subscribe-newsletter__input-email,
.b-pb-radio_pablo .b-pb-radio__variant,
.b-pb-radio_pablo, .l-publication__frontpage-wrapper {
    border-color: rgba(255, 255, 255, 0.2);
}

.b-subscribe-newsletter,
.l-post__frontpage-wrapper {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}

.relap-mel_fm-wrapper {
  border-top: 1px solid;
  border-bottom: 1px solid;
}

.g-auth-social.g-auth-social_pablo .g-auth-social__header {
	border: none;
}

.relap-mel_fm__list-wrapper {
    padding: 16px !important;
    border-radius: 4px;
}

/* ACCENT */

.b-pb-publication-body ul li:before {
	color: #ec345e;
}
}

@-moz-document domain("mel.fm") {
/* FONT */

@font-face {
  font-family: 'Lato';
	font-style: normal;
	font-weight: 300;
	src: 	local('Lato Light'), 
			url(https://apushkarev.github.io/font/Lato-Light.woff2) format('woff2');
}

@font-face {
  font-family: 'Lato';
	font-style: italic;
	font-weight: 300;
	src: 	local('Lato Light Italic'), 
			url(https://apushkarev.github.io/font/Lato-LightItalic.woff2) format('woff2');
}

@font-face {
  font-family: 'Lato';
	font-style: normal;
	font-weight: 400;
	src: 	local('Lato Regular'), 
			url(https://apushkarev.github.io/font/Lato-Regular.woff2) format('woff2');
}

@font-face {
  font-family: 'Lato';
	font-style: italic;
	font-weight: 400;
	src: 	local('Lato Italic'), 
			url(https://apushkarev.github.io/font/Lato-Italic.woff2) format('woff2');
}

@font-face {
  font-family: 'Lato';
	font-style: normal;
	font-weight: 700;
	src: 	local('Lato Bold'), 
			url(https://apushkarev.github.io/font/Lato-Bold.woff2) format('woff2');
}

@font-face {
  font-family: 'Lato';
	font-style: italic;
	font-weight: 700;
	src: 	local('Lato Bold Italic'), 
			url(https://apushkarev.github.io/font/Lato-BoldItalic.woff2) format('woff2');
}

* {
  font-family: Lato !important;
}

.g-list_pablo .g-list__value,
.b-teaser .b-teaser__caption,
p,
.b-pb-publication-body .b-pb-publication-body__lead,
.b-pb-publication-body ul li {
  font-weight: 300;
}

* {
  text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.2);
}

.b-pb-publication-body a {
    text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.1);
}


p, p span[style="font-size: 16px;"],
.b-pb-publication-body h4, .b-pb-publication-body p b,
.b-pb-publication-body ul li,
.b-pb-publication-body .b-pb-publication-body__incut {
  line-height: 1.618em;
  font-size: 21px !important;
}

.b-pb-publication-body .b-pb-publication-body__lead {
  font-size: 24px;
}

.b-pb-publication-body .b-pb-publication-body__incut {
	font-style: italic;
    font-weight: 400;
    font-size: 24px !important;
}
}

@-moz-document domain("mel.fm") {
/* LAYOUT */

  .b-pb-frontpage__card,
.b-pb-frontpage__cover
  {
    margin-right: 13px;
  }

  .b-pb-article-fresh__article:nth-child(n+4)
  {
    display: none;
  }

.i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_logo-black-s, .i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_logo-black {
    background-position: -111px 246px;
    width: 109px;
    height: 33px;
}

  .relap-mel_fm__list__top-container
  {
    padding: 16px !important;
    border-radius: 4px;
  }

.i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_fb_circle-color,
.i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_vk_circle-color,
.i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_twitter_circle-color,
.i-control.g-icon.g-icon_pablo_mel.g-icon_pablo.g-icon_img_ok_circle-color {
	height: 37px;
}

.b-pb-publication-body .b-pb-publication-body__background {
	border-radius: 4px;
    border: 1px solid rgba(0, 0, 0, 0.3);
}

.b-pb-article__half-page-banner {
	display: none;
}
}
