css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https://www\\.8muses\\.com/forum/post/discussion/.*'), url('https://www.8muses.com/forum/post/discussion') {
  .FormWrapper select, .FormWrapper .InputBox, .FormWrapper .Form-Tags ul {
    background: #333;
    border: 1px solid #555;
    color: #fff !important;
    border-radius: 3px;
  }
  .FormWrapper input {
    background: #333;
    color: #fff;
  }
  .FormWrapper input:focus {
    background: #333;
    color: #fff;
  }
  .FormWrapper label { color: #fff;}
  .token-input-dropdown {
    background: #333 !important;
    border-left: 1px solid #555 !important;
    border-bottom: 1px solid #555 !important;
    border-right: 1px solid #555 !important;
    color: #fff;
  }
  .token-input-dropdown * {
    background: #333 !important;
  }
}
@-moz-document regexp('https://www\\.8muses\\.com/forum/profile/edit') {
  #Content .FormTitleWrapper, #Content .FormTitleWrapper form {
    background: #292929 !important;
    color: #fff !important;
    border: none !important;
  }
  #Content .FormTitleWrapper form .InputBox {
    background: #333 ;
    color: #fff;
    border: 1px solid #555;
    border-radius: 3px;
  }
}
@-moz-document regexp('https://www\\.8muses\\.com/forum/entry/register.*') {
  .FormWrapper {
    background: #292929;
    color: #fff;
    border: none;
  }
  div.Background, div.Separator { background: #292929 !important;}
  
  .InputBox {
    border-radius: 3px;
    background: #444 !important;
    color: #fff;
  }
}
@-moz-document regexp("https://www\\.8muses\\.com/forum.*") { /*url("https://www.8muses.com/forum/") {*/
  /* Start General */
    a {
    color: #1390BA;
  }
  a:hover {
    color: #0F84AB;
  }
  .FormWrapper { border: none !important; }
  /* End General */
  
  /* Start Top Bar */
  .BreadcrumbsWrapper {
    background: #292929;
  }
  .BreadcrumbsWrapper, .BreadcrumbsWrapper .Breadcrumbs a {
    color: #ffffff;
  }
  #Head > .Row > div:nth-of-type(1) { display: none;}
  /* End Top Bar */
  
  /* Tag Cloud */
  .TagCloud li {
    background: #333;
    font-size: 10px;
  }
  .TagCloud span {
    background: #333;
  }
  .BoxCategories .PanelCategories span {
    background: #292929;
  }
  /* End Tag Cloud */
  
  /* Sign in Popup */
  div#Popup .Body {
    background: #292929;
    color: #fff;
  }
  div#Popup .Body .InputBox {
    border-radius: 3px;
    background: #444444;
    color: #fff;
  }
  div#Popup .Close {
    color: #fff;
  }
  div#Popup .Close:hover {
    color: #0f84ab;
  }
  /* End Popup */
  
  /* Site Name Bar */
  #Head {
    background: #333333;
    border-bottom: none;
  }
  
  #Head .SiteSearch .InputBox {
    border-radius: 3px;
    background: #444444;
    color: #fff;
  }
  
  .SiteSearch .Button {
    display: none;
  }
  /* End Site Name Bar */
  
  /* Main Site */
  #Body, html {
    background: #333333;
  }
  .Preview {
    background: #333;
    color: #fff;
    border: 1px solid #555;
    border-radius: 2px;
  }
  .Button {
    background: -moz-linear-gradient(top, #4c4c4c 0%, #595959 12%, #666666 25%, #474747 39%, #2c2c2c 50%, #000000 51%, #111111 60%, #2b2b2b 76%, #1c1c1c 91%, #131313 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #4c4c4c 0%,#595959 12%,#666666 25%,#474747 39%,#2c2c2c 50%,#000000 51%,#111111 60%,#2b2b2b 76%,#1c1c1c 91%,#131313 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #4c4c4c 0%,#595959 12%,#666666 25%,#474747 39%,#2c2c2c 50%,#000000 51%,#111111 60%,#2b2b2b 76%,#1c1c1c 91%,#131313 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    box-shadow: none;
  }
  .SpNotifications, .SpGlobe {
    background-position: -32px -212px;
  }
  .SpInbox, .SpEnvelope {
    background-position: 0 -227px;
  }
  .SpBookmarks, .SpStar {
    background-position: -80px -245px;
  }
  .SpDashboard, .SpOptions, .SpCog {
    background-position: 0 -212px;
  }

  .WhoIs .MeMenu {
    color: #fff;
  }
  
  #Panel {
    background: #292929;
    color: #fff;
    border: 1px solid #555555;
    border-radius: 2px;
  }
  
  #mc-embedded-subscribe-form {
    display:none;
  }
  #Panel > div:nth-of-type(5) {
    display:none;
  }
  #Content {
    background: #292929;
    color: #fff;
    border: 1px solid #555555;
    border-radius: 2px;
  }
  #Content .DismissMessage {
    display:none;
  }
  #Content .DataTableWrap tbody tr:nth-child(odd), .Profile .DataListWrap li:nth-child(odd) {
    background: #333333 !important;
    border: none;
  }
  #Frame {
    background: #333;
  }
  #Content .DataTableWrap tbody tr:nth-child(even), .Profile .DataListWrap li:nth-child(even) {
    background: #292929 !important;
    border:none;
  }
  #editor-fullpage-candidate {
    background: #292929 !important;
    color: #fff !important;
  }
  #editor-fullpage-candidate .editor {
    background: #333 !important;
    color: #fff;
  }
  #editor-fullpage-candidate textarea, .TextBoxWrapper textarea {
    background: #333;
    border: 1px solid #555;
    border-radius: 3px;
    color: #fff;
  }
  #Content strong.HasNew {
    background: #444;
    color: yellow;
  }
  #Content .FormWrapper {
    background: #292929;
    color: #fff;
    border: 1px solid #555;
    border-radius: 3px;
  }
  .editor-file-preview {
    background: #333;
    border: 1px solid #555;
    color:#fff;
  }
  .editor-file-preview .filename {
    color: #1390ba !important;
    white-space: none;
    text-decoration: none;
    text-shadow: none;
  }
  .editor-file-preview .filename:hover {
    color: #0f84ab !important;
  }
  
  .Tag {
    background: #444;
    text-transform: uppercase;
    font-size: 10px;
  }
  /* End Main */
}
