css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("game20.gr") {

  section, body{
    background: #262630 !important;
    color: #eee !important;
  }
  article {
    background: #1a1716 !important;
  }
  a{
    color: #ddd !important;
  }
  .author > a{
    color: #98c !important;
  }
  header {
	background: url(https://68.media.tumblr.com/e8652e625273e80642539b7cd1ceb419/tumblr_on6spmndPp1rmszoio1_100.png) repeat-x top center transparent;
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	z-index: 1200;
}
  nav ul.sub li.current-menu-item span, nav ul.sub li a:hover span {
	background: url(https://68.media.tumblr.com/9f3bb28b13c691f3db4c504d1f5f7ea4/tumblr_on6t7bAns51rmszoio1_250.png) no-repeat scroll top left transparent;
	padding: 11px 0 5px 12px;
}
  nav ul.sub li.current-menu-item a, nav ul.sub li a:hover {
	background: transparent url(https://68.media.tumblr.com/9f3bb28b13c691f3db4c504d1f5f7ea4/tumblr_on6t7bAns51rmszoio1_250.png) no-repeat scroll bottom right;
	color: #fff;
	outline: none;
}
  .content > p, .yarpp-thumbnail-title, .search > div > h2, .newsletter > p{
    color: #fff !important;
  }
  .comments-info, #disqus_thread{
     background: #202023 !important;
     color: #fff !important;
  }
  h2 {
   background: transparent !important;
  }
  .right-bar section, .post-title {
	 background: #202023 !important;
   color: #fff !important;
}
  .middle-bar .content-block h2 {
   background: #d12724 !important; 
  }
  section.twitter h2 {
	background: #00aced !important;
}
  section.twitter {
    background: transparent !important;
  }
  .page-numbers, .page-numbers a{
     background: #575260 !important;
  }
  nav ul.sub li a, nav ul.main li a, .article-bar article p{
    color: #fff !important;
    font-weight: bold !important;
  }
  img:hover {
    box-shadow: 0px 0px 10px #d12724;
  }
  section.widget_disqus83_latest_widget a span.post-title:hover {
    color: red !important;
  }
  .article-bar blockquote{
    background-color: black !important;
  }
}
