@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.es"), domain("eldarya.com"), domain("eldarya.fr"), domain("eldarya.com.br") {

/*Nevra Famiiar*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://68.media.tumblr.com/b261c98fe267de4ca03397e026a1b2dd/tumblr_on6x8u2zce1trxrtqo2_500.png") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://68.media.tumblr.com/be6f1b0d61c27ad3338c7ae4b7332019/tumblr_on6x8u2zce1trxrtqo1_500.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Nevra" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Nevra tiene hambre, Â¡alimentale por favor!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Vampiro" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://68.media.tumblr.com/ce59b675fadae0d6d1c8527985d0662d/tumblr_on6x8u2zce1trxrtqo4_75sq.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Sangre" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("http://static.eldarya.es/img/npc/mood/web/9fa3599c7c91eda8fb39861cc7e47f5d~1409054648.png") no-repeat ;
    background-size : auto 100% ;
    height : 556px !important ;
    width : 243px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
