@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.allocine.fr") {

  /* Arriere plan */
  body {
    background: #2d2d2d;
    color: #c3c3c3; /* NE MARCHE PAS. Sensé changer la couleur des commentaires */
  }
  
  /* --------- FILMS --------- */
  
  /* CORPS */
  .content-layout {
    background: #474747;
  }

  /* Bande sous le menu */
  .header-nav-holder .ephemeride {
    background: #474747;
  }
  
  /* Liens bleus de la bande sous le menu */
  .header-nav-holder .ephemeride a {
    color: #0082e9;
  }
  
  /* Petite bande sous le menu */
  .header-nav-holder > .is-active .ephemeride, .header-nav-holder > .is-active .subnav {
    box-shadow: 0 1px 0 0 #474747,0 2px 0 0 #474747;
  }
  
  /* Rechercher une salle */
  .search-theater {
    background: #323131;
  }
  
  /* Couleur du texte */
  
  /* Concerne plusieurs zones dont le synopsis, les commentaires des gens */
  html {
    color: #c3c3c3;
  }

  /* Synopsis */
  .synopsis-txt {
	color: #dadada;
  }
  
  a {
    color: #c3c3c3;
  }
  
  /* PAGE FILM */
  
  /* Titre */
  .titlebar-title {
    color: #c3c3c3;
  }
  
  /* Date, réalisateur, acteurs... */
  .blue-link {
    color: #0082e9;
  }
  
  /* Autres liens bleus */
  .end-section-link {
    color: #0082e9;
  }
  
  /* Secrets de tournage */
  .trivia {
    color: #c3c3c3;
  }
  
  /* Commentaires - identification */
  .disqus-logged-out .login-trigger {
    color: #0082e9;
  }
  
  /* Menu de film (Seances, BA, Casting...) */
  .third-nav .item {
    color: #c3c3c3;
  }
  
  /* Recherche Séances, DVD, VOD */
  .ovw-product-item {
    background: #323131;
  }
  
  /* Sortie semaine prochaine */
  .player-emergence {
    background: #474747;
  }
  
  /* Sous-menu film */
  .header-nav-holder > .is-active .subnav {
    background: #d1a700;
  }
  /* Synopsis */
  .card-entity .synopsis {
    color: #dadada;
  }
  /* Texte "nom_du_film : Qu'en avez-vous pensé ? */
  .media-info-title {
    color: #dadada;
  }
  
  
  /* CRITIQUES */
  
  /* Texte - critiques spectateurs */
  .reviews-users-comment .item p {
    color: #dadada;
  }
  /* Texte - critiques presse */
  .reviews-press-comment .text {
    color: #dadada;
  }
  
  /* SEANCES */
  /* Arriere plan */
  .lylisting {
    background: #474747;
  }
  .boxbasicctt {
    background-color: #474747;
  }
  .rubric {
    background-color: #474747;
  }
  .colleft .nav_secondary ul li a, .colleft .nav_secondary_more strong, .colleft .nav_secondary ul li span, .colleft .nav_secondary ul li em {
    background-color: #474747;
  }
  .colleft .nav_secondary ul ul.clmsubtopic {
    background: #474747;
  }
  .colleft .nav_secondary ul li ul li {
    background-color: #474747;
  }
  .pagelayoutbggrad {
    background: #474747;
  }
  .boxbasichdr {
    background: #474747;
  }
  .icofavadd, .icoplay, .icoquestion, .icocalendar, .icoredcross, .icomagnifier, .icocomment, .icopicture, .icorated, .icoevent, .icovideo, .icofile, .icotv, .icoextlink, .icoextlink_inline, .icoquotopen, .icoquotclose, .overlayclose, .hniptick, .rssbtn, .sharethisbtn, .icotroiscent, .icofacebookmini, .icoticka, .icotickdown, .icotickup, .icoarrowdown2, .icoarrowright2, .icoarrowleft3, .icoarrowright3, .stareval, .icoshadowbig, .hnipctt_m3 .hnipdatacontainer .hnipbgfiller, .icoshadowmini, .navlistnull, .navlistoff, .navliston, .navprevbtn img, .navnextbtn img, .datepickerGoPrev a, .datepickerGoNext a, .sortbythumbnailsbtn img, .sortbylistbtn img, .sortbytheaterbtn img, .sortbymoviebtn img, .comboboxlist .first img, .colleftmenu img, .colleftmenub .first img, .hdrsubmenuitems.extra .titleitem img, div.star-rating, div.star-rating a, div.starZero, div.starZero a, .fm_rating_notinterested_class, .fm_rating_notinterestedchoosen_class, .fm_rating_wanttosee_class, .fm_rating_wanttoseechoosen_class, .colleft .nav_secondary ul li.current a img, .colleft .nav_secondary ul ul.clmsubtopic li a img, .colleft .nav_secondary ul li em img, .mod_untier .linkmore img, .mod_deuxtier .linkmore img, .table_items_list table td.top_arrow img, .table_items_list table td.bottom_arrow img, .DragSortList li div.top a.close, .table_items_list table img.heart, .table_items_list table img.heart_selected, .table_items_list table img.arrow, .table_items_list table .selected img.arrow, .table_items_list a.iconredcross img, #tab_myallo li a img, #tab_myallo .close img, #tab_myallo .col_select navprevbtn a img, #tab_myallo .col_select .navnextbtn a img, .pager_navbar ul li.prev span, .pager_navbar ul li.next span, .pager_navbar ul li.prev a, .pager_navbar ul li.next a, .pager_navbar ul li.prev a:hover, .pager_navbar ul li.next a:hover, #contributorBox .arrow, .WebmailCompatibility ul li.gmail, .WebmailCompatibility ul li.yahoo, .WebmailCompatibility ul li.hotmail, .WebmailCompatibility ul li.facebook, #myAC_inviteFriend_step2 img.checkOK, .serie_items_list img.arrow, .serie_items_list .serie_selected img.arrow, #connectbar, #connectbar .bg_connectbar, #connectbar .connectedInner, #connectbar .disconnectedInner, #connectbar .connectedInner .userID, #connectbar .disconnectedInner .leftZone, #connectbar .bg_arrow, #connectbar .connectedInner .userMenu li.mailbox a, #connectbar .connectedInner .userMenu li.mailbox a.message, #connectbar .disconnectedInner .rightZone .member, #connectbar .disconnectedInner .rightZone .connect, #connectbar .disconnectedInner .rightZone .fb, #connectbar .shadowbar, .yellowPagerTop .morezonecontent .btn.arrow_prev img, .yellowPagerBottom .morezonecontent .btn.arrow_prev img, .yellowPagerTop .morezonecontent .btn.arrow_next img, .yellowPagerBottom .morezonecontent .btn.arrow_next img, .OverlayPlay, #popin_box .popin_boxclose, .boxhl, .boxhr, .boxhm, .boxfl, .boxfr, .boxfm, .boxbasichdr, .ad_mktblock, .recobar, .recomask, .recopercent1, .recopercent2, .recobg, .recobgact, .connectbar, .subheaderbggrad, .pagelayoutbggrad, .morezonebggrad, .sitemapbggrad, .topfooterbggrad, .hrembossed, .hrdotted, .hrdotted2, .hrbasic, .hrbasic2, .hrgolden, .hrbicolor, .hrdottedbig, .hrdottedbigshifted, .btn em, .btn span, .btnbasic, .tabs a, .tabs a span, .tabs em, .tabs em span, .tabshdr .label, .tabshdr .ddarrow, .aclogo img, .aclogofooter, .aklogofooter, .close_news_tooltip, .news_tooltip .arrow_b, .pres_letter .btn01 span {
    background-image: url("http:");
  }
  
  /* BOX OFFICE */
  /* Arriere plan */
  #wrapper #contentlayout {
    background: #474747;
  }
  /* Colonne "Entrées */
  .bg_01 {
    background: #474747;
  }
  
  /* COURT-METRAGE */
  #shortfilms_overview .banner_brand, #shortfilms_about .banner_brand, #shortfilms_top_videos .banner_brand, #shortfilms_news .banner_brand, #shortfilms_festival_list_top .banner_brand, #shortfilms_list_all_content .banner_brand, #newspage_shortfilms .banner_brand {
    background: #474747;
  }
  
  
    /* --------- SERIES --------- */
  
  /* CORPS */
  #contentlayout {
    background: #272727;
    border: solid #272727;
  }
  
  /* Sous-menu serie */
  .main-header #header__nav > ul > li.is_active .subnav {
    background: #d1a700;
  }

  /* Petite barre blanche sous le sous-menu */
  .main-header #header__nav > ul > li.is_active .ephemeride, .main-header #header__nav > ul > li.is_active .subnav {
    box-shadow: 0 1px 0 0 #474747,0 2px 0 0 #474747;
  }
  
  /* Réseaux sociaux */
  .module-actionbar {
    background: #333;
  }
  .module-actionbar .module-actionbar-action {
    background: #333;
  }
  
  /* Barre blanche (pubs?) sous le sous-menu */
  #contentlayout::before {
    display: none;
  }
  
  /* Bande verticale de séparation pour plusieurs block (autres series, derniere news...) */
  .bg_col_right {
    background: -moz-linear-gradient(left,#fc0,#fc0);
  }
  
  /* Casting */
  #moviepage_videos_trailer #col_main .media_list_02 li, #seriespage_videos_trailer #col_main .media_list_02 li, #col_main .media_select li, #col_main .media_list_hl li {
    border: 1px solid #474747;
  }
  #moviepage_videos_trailer #col_main .media_list_02 img, #col_main .media_list_hl li img {
    border: 3px solid #474747;
  }
  
  
  
  /* ------------- EMISSIONS ------------ */
  /* Texte et arriere plan pour "Sélection de vidéos" */
  .titlebar .tt_wline .mask {
    background: #474747;
    color: #dadada;
  }
  .titlebar .tt_wline::before {
    background: #474747;
  }
  /* Blocs emissions */
  /* Arriere plan */
  .media-meta-list .media-meta.medium .media-meta-figcaption-inner {
    background: #2d2d2d;
  }
  /* Texte */
  .media-meta.medium .title, .media-meta.medium .title a {
    color: #dadada;
  }
  /* Bloc "Webséries, émissions... à découvrir" */
  .othersProgramsBox::before {
    background: #2d2d2d;
  }
  
}
