css:
@-moz-document domain("gitter.im") {
/*
Note: There are a lot of !important overrides, Gitter now uses scoped styles
(data-v-xxxxxxx), wich should change at every build.
*/
body {
    line-height: 1em;
    font-size: 12px;
}

/* Header */
.chat-and-toolbar-wrapper {
    padding-top: 3.5rem;
}
.header,
.chat-header,
.chat-header__main-grouping {
    height: 3.5rem !important;
}

.chat-header__heading,
.chat-header__topic {
    margin-top: 3px;
}

.chat-header__group-avatar-upload-label {
    height: 3.2rem;
    width: 3.2rem;
    padding: 0px;
    margin-top: .15em;
    margin-left: .7rem;
}

.chat-header__topic:not(:empty),
.chat-header__topic-wrapper.is-admin::after {
    margin-top: calc(8px + 24px - 25px + .125em);
    font-weight: lighter;
    font-size: 12pt
}

.chat-header__topic,
.chat-header__title {
    font-size: 12pt;
    line-height: 1em;
}

.chat-header__action-grouping .profile-menu {
    padding: .2rem 0 .2rem 1.5rem;
}

.chat-header__action-grouping .dropdown.profile-menu__list {
    top: 3rem;
}
/* Left and right panel */
.layout-minibar,
.right-toolbar.right-toolbar--collapsible.collapsed {
    width: 3.5rem !important;
}

.unpinned:not(.mobile) .layout-main-menu {
    left: 3.5rem;
}

.right-toolbar-region > .right-toolbar.collapsed #people-list .trpToolbarListContent {
    padding: 0;
    margin: 2px;
}
.right-toolbar-region__footer {
    height: 7rem;
    padding-top: 2.5rem;
    bottom: 0;
}

/* Rooms */
.room-list-title {
    line-height: 3em !important;
}

.favourite-room-list .list-item,
.room-list .list-item,
.favourite-room-list .list-item .list-item-link,
.room-list .list-item .list-item-link {
    height: 2.5rem;
}

/* Chat */
.chat-item.burstStart {
    margin-top: 1.5rem;
}

.chat-item.burstStart .chat-item__aside {
    margin-top: .3rem;
}

.chat-item__details {
    font-size: 10pt;
    margin-bottom: .3rem
}
}
