css:
@-moz-document regexp("http(|s)://(|www.)2(|-)ch\.(so|ru|hk|ec|pm|re|tf|wf|yt).*") {
section.posts, .thread
.thread,
.thread-nav {
    width: 1008px !important;
    margin-left: 0px !important;
    margin-right: auto !important;
}
/*Header*/
/*Список тредов*/
.desktop.rmenu {
    display: none;
}
/*Лого*/
.logo {
    display: none;
}
/*Баннер*/
.moneymoneymoney {
    display: none;
}
/*Доски пользователей*/
.desktop.top-user-boards {
    display: none !important
}
.top-user-boards{
    display: none;
}
/*Новые доски, Тренды*/
.board-tags {
    display: none;
}
/*Разделители*/
header > hr:nth-of-type(7) {
    display: none;
}
header > hr:nth-of-type(6) {
    display: none;
}
header > hr:nth-of-type(5) {
    display: none;
}
.postrekl-hr {
    display: none;
}
/*Доска и посты*/
/*Кнопка ответа на доске*/
.desktop:nth-of-type(5) {
	font-weight: bold;
	display: block;
}
#nsfw {
	display: none;
}
/*Время поста*/
.posttime {
	display: none;
}
/*Показ ответов*/
.de-thread-buttons {
    display: none;
}
/*Количество постов*/
.de-omitted {
    float: right;
	color: #333333;
}
/*Footer*/
/*Новости*/
.desktop.nb {
    display: none;
}
/*Навигация*/
#boardNavBottom {
    display: none;
}
/*Скорость борды*/
.board-speed {
    display: none;
}
/*Информация*/
.board-info {
    display: none;
}
/*Разделители*/
.adminbar-bottom.adminbar.desktop {
    display: none;
}
.footer > hr:nth-of-type(3) {
    display: none;
}
.footer > hr:nth-of-type(2) {
    display: none;
}
/*Стикеры в плавающей форме*/
.comment-area > .symbol-counter > .message-sticker-btn {
    display: none;
}
}
