css:
@-moz-document domain("4chan.org") {
/*			/* 4chan Flat Dark & Clean v.0.4 */
                    /* by MarUlberg */
/* Inspired by kirnbauer's "Youtube Dark Style Material [BETA]" */

body, div, li, .post.reply, a, a:visited, a:hover, span, text, #footer-links, a.replylink:not(:hover), div#absbot a:not(:hover), h2, .quoteLink, .quotelink, .deadlink, .pageJump a, input{
    background: /*[[background]]*/ !important;
    border-right: transparent !important;
    border-left: transparent !important;
    border-bottom: transparent !important;
    color: /*[[text]]*/ !important;
}
img {
    border: transparent !important;
    border-radius: 6px !important;
}
img.extButton.threadHideButton, img.extButton.expbtn{
    border: transparent !important;
    border-radius: 2px !important;
    -webkit-filter: grayscale(100%)!important; /* Safari 6.0 - 9.0 */
    filter: grayscale(100%)!important;
} 
input, #bannerCnt{
    border-color: transparent !important;
}
hr{
    border-color: /*[[border]]*/!important;
}
#disclaimer-dialog{
    box-shadow: none!important;
}
body{
    padding-top: 30px !important;
    padding-left: 0px !important;
    margin: 0px !important;
}
#boardNavDesktop span, #boardNavDesktop a, #boardNavDesktop{
    background: /*[[header]]*/ !important;
    height: auto !important;
    font-size: 11px !important;
}
#boardNavDesktop{
    padding: 5px !important;
    position: fixed !important;
    top: 0px !important;
    width: 100% !important;
    z-index: 9000 !important;
}
.aboveMidAd, .abovePostForm{
    visibility: hidden !important;
}
.expanded-thumb{
    max-height: 960px !important;
}
/*	Scroll Bar	*/
*::-webkit-scrollbar{
    width:14px !important;
}
*::-webkit-scrollbar-thumb{
    min-height:35px !important;
    background: /*[[header]]*/ !important;
    border: none !important;
    border-radius: 0px !important;
}
*::-webkit-scrollbar-track-piece{
    background-color: transparent !important;
    border: none !important;
}
}
