css:
@-moz-document domain("youpouch.com") {


body {
    background: url('https://s20.postimg.cc/wi6bk8v59/l_As_I3ml.jpg') center top fixed !important;
    background-size:cover !important;
    background-repeat:no-repeat;
}

#header {
    background: none !important;
}


#container > div#header > div#header-main{
    margin-top:100px !important;
    background:rgba(255,255,255,0.5) !important;
}


#div-gpt-ad-header{
    display:none !important;
    }

    
#container > div#header > div#header-menu{
    background:rgba(255,255,255,0.5) !important;        
    }    
    
#side > ul#primary-widgets > li{
        background:rgba(255,255,255,0.5) !important;        
    }
    
    
#side > ul#primary-widgets{
        background:rgba(255,255,255,0.5) !important;        
    }
    
#side > ul{
        background: transparent !important;        
    }
    
#side{
        background:rgba(255,255,255,0.5) !important;        
    }
    
        
#content {
     background:rgba(255,255,255,0.7) !important;     
    }    
        
    
    #header > div#header-main > h1 > a{
        margin-left:20px !important;
        background: url('https://s20.postimg.cc/q8w6ogiwd/youpouchlogo.png')  bottom right  !important;
        background-repeat:no-repeat !important;
        background-size:90% !important;
        font-size:10px !important;
        
    }
    

    #header > div#header-main > h1 > a{
        width:260px !important;
        height:83px !important;
    }    
    
    
    #header > div#header-main > h1 > a > img{
        display:none !important;
    }    
    
    a{
        color:#fff !important;
    }    
   
    
#cto_bannerContent{
    background:transparent !important;
    background-image:none !important;
    background: rgba(255,255,255,0.7) !important;
    }
    
    

#container > div#content > div#main > div > div.post-header > h2.entry-title > a{
    color:#ff1515 !important;
    }    
    
#container > div#header > div#header-main {
    margin-top: 100px !important;
    background: rgba(255,255,255,0) !important;
}    
    
#header-nav li a{
    filter: hue-rotate(90deg);    
}    
    
#related-posts > ul{
    background: rgba(0,0,0,0.5) !important;    
    }    
    
    
#container.wide > div#content > div#side > ul#primary-widgets > li{
    background: rgba(0,0,0,0.5) !important;        
    }
    
    
#widget_rankings > div#widget_realtimeranking > ol{
    background: rgba(0,0,0,0.5) !important;      
    }    
    
#content > div#side > ul#fixed-widgets > li > ol > li{
    background: rgba(0,0,0,0.5) !important;         
    }    
    
    #logly-lift-widget-content{
    background: rgba(0,0,0,0.5) !important;                 
    }
    
    #main > div.outbrain{
    background: rgba(0,0,0,0.5) !important;           
    }
    
    
#main > div.post-misc > div.ranking{
    background: rgba(0,0,0,0.5) !important;         
    }    
    
    
    #side > ul#primary-widgets  {
    background: rgba(0,0,0,0.5) !important;                 
    }
    
    
    
    
    
    
    
    
    
    
    
    
}
