css:
@-moz-document domain("usg.service-now.com") {
/* vietnamese */
@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 200;
  src: local('Montserrat ExtraLight'), local('Montserrat-ExtraLight'), url(https://fonts.gstatic.com/s/montserrat/v10/eWRmKHdPNWGn_iFyeEYja4uHZpzeMvIxgxffEGR1vRs.woff2) format('woff2');
  unicode-range: U+0102-0103, U+1EA0-1EF9, U+20AB;
}
/* latin-ext */
@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 200;
  src: local('Montserrat ExtraLight'), local('Montserrat-ExtraLight'), url(https://fonts.gstatic.com/s/montserrat/v10/eWRmKHdPNWGn_iFyeEYjax3FbGwR6vt0FcFQ55rEhNY.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 200;
  src: local('Montserrat ExtraLight'), local('Montserrat-ExtraLight'), url(https://fonts.gstatic.com/s/montserrat/v10/eWRmKHdPNWGn_iFyeEYja_bbaTZmtPDRvp9xUdyvPg4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
.navbar .container-fluid {
  background: #2980b9;
  padding: 5px;
}
.sn-frameset-header .container-fluid {
  background: #2C3E50;  
  padding: 0;
}

.navpage-main {
    top: 40px;
}
table#homepage_header_table {
 padding-top: 10px;   
}

.navpage-nav {
 top: 41px;   
}
.navpage-bottom-hidden .navpage-main {
 top: 50;  
 z-index: 1;
}
.navbar-default {
padding: 0;
    margin: 0;
    height: 0;
    border: none;
}

.nav-header .nav-segmented li.active {
    background: #E74C3C;
    border-bottom: 3px solid #c9392a;
}
.nav-body ul li ul {
 background-color: #ECF0F1;  
}

.table > thead > tr > th, .table > thead > tr > td, .table > tbody > tr > th, .table > tbody > tr > td, .table > tfoot > tr > th, .table > tfoot > tr > td {
    border-top: none;
}

HTML[data-doctype=true] .drag_section, HTML[data-doctype=true] .drag_section_hilight_neighbors {
     border-radius: 0;
    box-shadow: rgba(0, 0, 0, 0.1) 0 0 0 0;
    border: none;
    border-bottom: 2px solid #ecf0f1;
    padding: 10px 0;
}

.drag_section_header TD {
    padding: 5px;
    background: #ecf0f1;
}

.table td.col-control {
    background-color: #ECF0F1;
}
tr.list_even td {
   background-color: #ECF0F1; 
}

.nav-body-wrapper {
    border-right: 1px solid #ECF0F1;
}
.nav-body ul.nav-application-tree > li {
    border-bottom: 1px solid #ECF0F1;
}
.navbar {
 border-bottom: none;
    
}
.sn-frameset-header, .navpage-header {
 background-color: #3498DB;   
}
.navpage-header {
 height: auto;   
}

.navbar-divider {
 display: none;   
}

.input-group-transparent input[type=search].form-control, .input-group-transparent input.form-control.form-control-search {
 border-radius: 0;   
}

.nav-header .icon-filter {
 display: none;   
}

.nav-header input[name=filter] {
 padding-left: 8px;
    border-color: #e1e1e1;
}

.header_decorations {
 top: 14px!important;   
}

li.module-node .icon-star:before {
 color: #E74C3C;   
}

@media (min-width: 550px) {
.navbar-right {
    position: absolute!important;
	top: 10;
    right: 11px;
    z-index: 9999;
}
}

.sn-frameset-header .navbar-brand {
    background-image: url(http://www.usg.edu/assets/global/images/bor_logos/bor_logo.svg)!important;
    background-size: 30px 30px;
    position: absolute;
    left: -5px;
    top: -3px;  
    height:auto;
}
.sn-frameset-header h1.banner-text {
	color: #D7DADB;
    font-size: 15.8px;
    margin-left: 50px;
    font-weight: 200;
    padding-bottom: 3px;
    padding-top: 6px;
    font-family: Montserrat,sans-serif;
}

.sn-frameset-header .navpage-header-content, .sn-frameset-header .sysparm-search-icon:before {
  color: #D7DADB;  
}

.icon-add {
    color: #2C3E50;
    background-color: #6DBCDB;
    border: none;  
}

.icon-arrow-left-rounded:before {
    color: #E74C3C;
}

.table td.col-control {
 	background-color: #ECF0F1;  
}

.nav-footer {
   border-top: 1px solid #ECF0F1;
    border-right: 1px solid #ECF0F1;
    background: #ECF0F1;
}

.nav-header .nav-segmented {
    border-top: 1px solid #ECF0F1;
}

.nav-body a:focus {
    background-color: #6DBCDB!important;
}

.nav-body a:hover {
    background-color: #6DBCDB!important;
}

.navbar_ui_actions .btn-default, .navbar-header .btn-default {
    border: none;
}

HTML[data-doctype=true] .custom-form-group {
    width: 100%;
}

.nav-body ul.nav-favorites-list {
 background-color: #fff;   
}

.navpage-nav-collapsed .nav-body ul.nav-favorites-list > li > a {
 border-bottom: 1px solid #ECF0F1;   
}

.breadcrumb_container {
 height: 48px   
}


}
