css:
/* 
 * Name:    LinuxToday Dark
 * Author:  enren
 * Version: 20170521
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxtoday.com") {

.globalHeader {
  background-image: none;
}
.globalHeader .brandLogo p {
  color: #888;
}
.globalHeader .logout .greeting span,
.globalHeader .logout .greeting a {
  color: #ccc !important;
}
body {
	color: #ccc;
  background-color: #111 !important;
}
.print {
  color: #ccc;
}
a,
a.nav,
a.active_page {
  color: #4495d4 !important;
}
a:visited,
a:visited.nav {
  color: #2d82c4 !important;
}
a:hover {
  color: #00aeef !important;
}
a:hover.nav {
  background: none;
  color: #00aeef !important;
}
h1 {
  color: #eee;
}
.error,
#nl_response_message {
  color: #c00;
}
.success,
.successbox {
  color: #0c0;
}
.rightnav_box {
  border: 1px solid #555;
}
.rightnav_box h3 {
  background-color: #333;
  color: #eee;
}
.rightnav_box hr {
  color: #4495d4;
}
.sidebar li {
  color: #888;
}
.sidebar img {
  border: 3px solid #555;
}
ul.css-tabs {
  border-bottom: 1px solid #555;
}
ul.css-tabs a {
  background-color: #222;
  border: 1px solid #555;
  color: #888;
}
ul.css-tabs a:hover {
  background-color: #222;
  color: #ccc;
}
ul.css-tabs a.current {
  background-color: #111;
  border-bottom: 1px solid #111;
  color: #ccc;
}
.css-panes div {
  background-color: #222;
  border: 1px solid #555;
}
.blog_content {
  background-color: #111;
  color: #ccc;
}
.blog_content p,
.blog_content h1,
.blog_content h2,
.blog_content h3,
.blog_content h3 a {
  color: #ccc;
}
.blog_content p.posted {
  color: #eee;
  border-top: 1px solid #555;
}
#loginfo,
#loginfo a {
  color: #eee;
}
#container_tile {
  background-image: none;
  background-color: #111;
}
#container_tile hr {
  color: #555;
}
#itbelogo img {
  -webkit-filter: invert(93%) grayscale(100%);
	filter: invert(93%) grayscale(100%);
}
#contentfoot {
  background: none;
  background-color: #111;
}
#form_container {
  background: #111 !important;
  border: 1px solid #555 !important;
}
label {
  color: #ccc !important;
}
/*** Inputs ***/
input[type=button],
input[type=reset],
button {
  background: #333 !important;
  border: 1px solid #555 !important;
  color: #ccc !important;
}
input[type=text],
input[type=password],
input[type=textarea],
textarea,
select {
  background-image: none !important;
  background: #000 !important;
  border: 1px solid #555 !important;
  color: #ccc !important;
}
input.button[alt="Go"] {
  -webkit-filter: brightness(90%);
	filter: brightness(90%);
  -webkit-border-radius: 3px;
  border-radius: 3px;
  width: 31px;
  height: 16px;
  background-position: center;
  position: relative;
  top: 2px;
}
/*** ACL_readMoreBox ***/
.ACL_readMoreBoxTitle {
	color: #09f;
}
#ACL_openSignInBox {
	color: #09f;
}
/*** SignIn box ***/
#ACL_ssoSignInBox_TopImage,
#ACL_ssoSignInBox_BottomImage {
  display: none;
}
#ACL_ssoSignInBox_content {
  background-image: none;
  border: 1px solid #555;
}
#ACL_ssoSignInBox h1 {
  color: #eee;
}
#ACL_ssoSignInBox p {
  color: #888;
}
#ACL_ssoSignInBox a {
	color: #4495d4;
}
#ACL_ssoSignInBox a:hover {
	color: #64a7db;
}
#ACL_ssoSignInBox #ACL_openRegFormFromSignInBox {
	color: #ccc;
}
#ACL_ssoSignInBox #ACL_openRegFormFromSignInBox:hover {
	color: #aaa;
}
#ACL_SignInBox_linkDivider {
	color: #4495d4;
}
#ACL_ssoSignInBox label {
  color: #ccc;
}
#ACL_ssoSignInBox #ACL_signInButton {
  -webkit-filter: brightness(90%);
	filter: brightness(90%);
  -webkit-border-radius: 3px;
  border-radius: 3px;
  width: 80px;
  height: 30px;
  background-position: center;
}
/*** Registration form ***/
#ACL_Form_Container_TopImage,
#ACL_Form_Container_BottomImage {
  display: none;
}
#ACL_Form_Content {
  background-image: none;
  border: 1px solid #555;
}
#ACL_Form_Container h1 {
  background-image: none;
  color: #eee;
  border-bottom: 1px solid #555;
}
#ACL_Form_Container a {
	color: #4495d4;
}
#ACL_Form_Container a:hover {
	color: #64a7db;
}
#ACL_Form_Container .ACL_registeredText {
	color: #ccc;
}
.ACL_form_description {
	border-top: 1px solid #555;
	color: #888;
}
.ACL_form_description_highlight {
}
.ACL_Form_SubHeader {
	color: #eee;
	border-bottom: 1px dotted #555;
}
.ACL_formFooterText {
	color: #888;
}
#ACL_Form_Content #ACL_FormSubmitButton {
  -webkit-filter: brightness(90%);
	filter: brightness(90%);
  -webkit-border-radius: 3px;
  border-radius: 3px;
  width: 100px;
  height: 30px;
  background-position: center;
}
#ACL_Form_Content .ACL_FormField_Container {
  background-color: #111;
}
#ACL_Form_Container label {
  color: #ccc;
}
/*** Account Management Page ***/
#ACL_Form_Container_Profile h1,
#ACL_Form_Container_Password h1,
#ACL_Form_Container_NL h1,
#ACL_Form_Container_AL h1 {
	background-image: none;
	color: #09f;
}
/*** Comment Section ***/
#comment_acl div {
  background-color: #111 !important;
}
#comment_form {
  background-color: #111;
  color: #ccc;
}
#comment_form img.deferload {
  display: none;
}
#commentform input[type=submit] {
  background: #333;
  border: 1px solid #555;
  color: #ccc;
}
.comment {
  background-color: #1a1a1a;
}
.even {
  background-color: #222;
}
span.username {
  color: #ccc;
}
div.userinfo {
  color: #999;
}
.comment p {
  color: #999;
}
/*** Images and sprites ***/
.deferload {
  background: #222;
  overflow: hidden;
}
img.deferload {
  -webkit-filter: invert(87%);
	filter: invert(87%);
  position: relative;
	top: -4px;
}
.sprite {
  background: none;
}
.sprite-home_nav:after,
.sprite-preferences_nav:after,
.sprite-contribute_nav:after,
.sprite-linkto_nav:after {
  visibility: visible;
  position: absolute;
  padding-top: 4px;
  padding-left: 14px;
}
.sprite-home_nav:after {
  content: "Home";
}
.sprite-preferences_nav:after {
  content: "Preferences";
}
.sprite-contribute_nav:after {
  content: "Contribute";
}
.sprite-linkto_nav:after {
  content: "Link To Us";
}
.sprite-linuxnews_tab:after,
.sprite-developer_tab:after,
.sprite-highperform_tab:after,
.sprite-infrastructure_tab:after,
.sprite-itmanagment_tab:after,
.sprite-security_tab:after,
.sprite-storage_tab:after {
  visibility: visible;
  position: absolute;
  padding-top: 4px;
  padding-left: 10px;
}
.sprite-linuxnews_tab:after {
  content: "Linux News";
  padding-left: 54px;
}
.sprite-developer_tab:after {
  content: "Developer";
}
.sprite-highperform_tab:after {
  content: "High Performance";
}
.sprite-infrastructure_tab:after {
  content: "Infrastructure";
}
.sprite-itmanagment_tab:after {
  content: "IT Management";
}
.sprite-security_tab:after {
  content: "Security";
}
.sprite-storage_tab:after {
  content: "Storage";
}
.sprite-search_txt:after {
  visibility: visible;
  content: "Search Linux Today";
  font-size: 12px;
}
.contenttd p img {
  -webkit-filter: invert(93%) grayscale(100%);
	filter: invert(93%) grayscale(100%);
}
/*** ReCaptcha ***/
.recaptcha_image_cell {
  background-color: #111;
}
#recaptcha_response_field {
  border: none !important;
}
#recaptcha_area {
  border: 3px solid #555;
}
.recaptcha_r1_c1,
.recaptcha_r2_c1,
.recaptcha_r2_c2,
.recaptcha_r3_c1,
.recaptcha_r3_c2,
.recaptcha_r3_c3,
.recaptcha_r4_c2,
.recaptcha_r4_c4,
.recaptcha_r7_c1,
.recaptcha_r8_c1 {
  visibility: hidden;
}
#recaptcha_challenge_image {
  -webkit-filter: invert(93%);
	filter: invert(93%);
}
}
