css:
@-moz-document domain("company.atlassian.net") {
#ghx-pool,
#ghx-column-header-group.ghx-fixed,
.ghx-column-headers .ghx-column,
.ghx-column-headers .ghx-column.ghx-busted-max,
body:not(.adg3) .ghx-column-headers .ghx-column,
.ghx-columns .ghx-column {
    background: #f9fafb;
}

/* Fixed column header  */
#ghx-column-header-group.ghx-fixed { z-index: 155;}

/* Ticket */
.ghx-issue {
    padding: 2px 5px 5px 5px;
    margin-top: 1px;
    margin-bottom: 10px;
	border:1px solid #ccc;
    font-size:12px;
}

/* We need to reset it, it was used for grabber */
.ghx-issue.ghx-card-color-enabled {
	padding-left: 5px;
}


/* Ticket Color */
.ghx-issue .ghx-grabber { width:100%; height:35px; top:auto; left:0; bottom:0; z-index:100;}
.ghx-xtra-narrow-card.ghx-narrow-card .ghx-issue .ghx-grabber { top:auto;}

/* Ticket ID, life, priority */
.ghx-issue .ghx-stat-fields { position:relative; z-index:150;}

/* Ticket ID Text */
.ghx-issue .ghx-key,
.ghx-has-swimlanes .ghx-swimlane a.ghx-key { color:#fff; text-shadow:0px 0px 10px #000;}

/* Order of Ticket ID and fields as Priority, type */
.ghx-narrow-card .ghx-issue .ghx-stat-fields > .ghx-row.ghx-stat-1 { width: auto; order: 2; margin-right:0; line-height:22px;}
.ghx-narrow-card .ghx-issue .ghx-stat-fields > .ghx-row.ghx-stat-2 { width: auto; order: 1;}
.ghx-xtra-narrow-card.ghx-narrow-card .ghx-issue .ghx-stat-fields > .ghx-row.ghx-stat-2 { margin-top:0; }
.ghx-issue .ghx-field + .ghx-field { margin-left: 0;} 

/* Ticket Tag Font-size */
.aui-label { font-size:12px;}

/* Ticket Time */
.ghx-issue .ghx-days { width: 16px; height: 16px;
 background:url('data:image/png;base64,');
}
.ghx-issue .ghx-days.more-then-2 { background-position: 0 -16px;}
.ghx-issue .ghx-days.more-then-3 { background-position: 0 -32px;}
.ghx-issue .ghx-days.more-then-5 { background-position: 0 -48px;}
.ghx-issue .ghx-days.more-then-8 { background-position: 0 -64px;}
.ghx-issue .ghx-days.more-then-12 { background-position: 0 -80px;}
.ghx-issue .ghx-days.more-then-20 { background-position: 0 -96px;}

/* Flagged Tickets*/
.ghx-issue.ghx-flagged:after { content:""; position:absolute; top:-7px; right:-7px; width:20px; height:20px;
background:url('data:image/png;base64,'); background-size:20px 20px;
}
}
