css:
@-moz-document domain("www.wakanim.tv") {
.header-main.js-header-main, 
section.slider-section.slider_homepage, 
div.hero-details,
li.slider_mini-item,
body,
button.slider_arrow,
div.article-related,
div.promo,
div.promo_credits,
nav.nav-user.js-header-main-component.is-open div.nav-user_wrap,
li.article-list_item,
div.discover-page{
    background: #202020 !important;
}

div.slider_item_description a, a.slider_item_season,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_wrap > div > a,
#comments-list > li > div.comment.Comment > div > header > a,
#comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > footer > a > span,
#comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div > p > a,
.subscription_table-containerButtonWhite .subscription_table-button,
  #comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > input:hover, 
  #comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > a:hover{
    color: #e3474b !important;
}

div.slider_item_description a:hover, a.slider_item_season:hover,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_wrap > div > a:hover,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_wrap > a.slider_box_title:hover,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_footer > div > div.slider_box_user > a:hover,
#comments-list > li > div.comment.Comment > div > header > a:hover,
#comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > footer > a > span:hover,
#comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div > p > a:hover {
    color: #da6f72 !important;
}

article > section > h3,
article > section > div,
div.row.write-preview-container > div.col-md-6.col-sm-12.write-container > textarea,
div.row.write-preview-container > div.col-md-6.hidden-sm.hidden-xs.preview-container > div.preview.well > p,
div.btn-group.pull-right.action-bar.hidden-sm.hidden-xs.open > ul > li > a,
#content > div > div > ul > li > div > small > span.dropdown.moderator-tools.open > ul > li > a,
#content > div > div > div.post-bar > div > div.topic-main-buttons.pull-right > div.btn-group.thread-tools.open > ul > li > a,
#content > div > div > div.post-bar > div > div.topic-main-buttons.pull-right > div.btn-group.action-bar.open > ul > li > a,
#content > div > div > ul > li > div.post-bar > div > div.topic-main-buttons.pull-right > div.btn-group.action-bar.open > ul > li > a,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_wrap > a.comments-count,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_wrap > a.slider_box_title,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_footer > div > div.slider_box_user,
body > section > div > div > div.slider_wrap > div > ul > li.slider_item > div > div.slider_box_footer > div > div.slider_box_user > a,
body > section > div > div > p,
#comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > p,
#comments-list > li > div.comment.Comment > div.Comment-menu.active > div > a,
.subscription_table-containerButtonWhite .subscription_table-button:hover,
.profile_status_input,
body > section.episode > div > div > div.episode_main > div.episode_video.js-tabs > div.episode_premium > div > a > span,
  #comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > b,
  #comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > input,
  #comments-list > li > div.comment.Comment > div.comment_main.comment_main_full > div.Comment-message > *,
html body.Client.window_full_loaded div.Client-footer div.container.Client-container-sm-xs-fluid div.row div.col-md-6 div.ClientBox.ClientBoxInfo.ClientBoxInfo-security p,
html body.Client.window_full_loaded div.Client-footer div.container.Client-container-sm-xs-fluid div.row div.col-md-6 div.ClientBox.ClientBoxInfo.ClientBoxInfo-security ul,
html body.Client.window_full_loaded div#clientadd-panel.ClientBox div#form-addclient.AddClient form#form0 div.ClientDeviceInfo p,
html body.Client.window_full_loaded div#clientadd-panel.ClientBox div#form-addclient.AddClient form#form0 div.ClientDeviceInfo div.Client-method div.Client-methodRadio.radio,
html body.Client.window_full_loaded div.Client-footer div.container.Client-container-sm-xs-fluid div.row div.col-md-6 div.ClientBox.ClientBoxInfo.ClientBoxInfo-authentification p,
html body.Client.window_full_loaded div#clientadd-panel.ClientBox div#form-waiting.WaitingClient{
  color: #000 !important;
}
div.preview.well a,
#content > div > div > ul > li > div > div > p > a { color: #e3474b !important; }

#ReviewMessage,
#comments-list > li > div.comment.Comment { background: #fff; }

.pcom-btn  {
    background: #181818 !important;
}

.Calendar-emptyTxt, body > section.serie > div > div.serie_grid > div > div.serie_buttons > div.serie_grid > div > a > span.button_text.js-button-text > span.removeText { color: #181818 !important; }

.review,
#ReviewMessage,
body > section.serie > div > div.serie_grid > div > div.serie_buttons > div.serie_grid > div > a:hover > span.button_text.js-button-text > span.addText { color: #000 !important; }
.comment_review_number,
.comment_input { color: #ffffff !important; }

.Calendar-hourIcon, .header-main_credits svg {
    fill: #ffffff !important;
}

.Calendar-hourTxt {
    color: #ffffff !important;
}

section.Calendar, .Calendar-header, .Calendar-day {
    background: #181818 !important;
    color: #ffffff !important;
}

.Calendar-Col {
    border: none !important;
    border-radius: 0px;
}

article.article div p, a.slider_item_link.js-tooltip-hover, div.discover-page{
    color:#000000 !important;
}

nav.nav-user.js-header-main-component.is-open{
    right: -9.4%;
}

nav.nav-user.js-header-main-component.is-open div.nav-user_wrap{
    width: 140%;
}

div.share_bubble.share-btn span.count{
    color:#000000 !important;
}

body,
div.article-related,
div.article-related_item_title,
a,
span.button_text.js-button-text,
div.discover-center{
    color: #ffffff !important;
}

div.section, 
span.slider_item_info_text, 
span.button_text.js-button-text:hover,
div.slider_item_text strong,
div.slider_item_description strong{
    color: #000000 !important;
}

a.header-main_why{
    color: #ffffff !important;
}

a.header-main_why:hover{
    color: #e3474b !important;
}

a.header-main_button.gift-icon.js-open-iframe-lightbox{
    background: #202020 !important;
    color: #ffffff !important;
}

.header-main_button{
    background: #202020 !important;
    color: #ffffff !important;
}

.header-main_button:hover, .header-main_button:active{
    color: #e3474b !important;
}



/* Calendrier des épisodes */

.gift-icon svg {
    fill: #ffffff !important;
}

.gift-icon svg:hover, .gift-icon svg:active{
    fill: #e3474b !important;
}





/* Scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #ffffff;
  border: 0px none #ffffff;
  border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover, ::-webkit-scrollbar-thumb:active {
  background: #e3474b;
}
::-webkit-scrollbar-track {
  background: #202020;
  border: 0px none #ffffff;
  border-radius: 0px;
}
::-webkit-scrollbar-track:hover {
  background: #202020;
}
::-webkit-scrollbar-track:active {
  background: #202020;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
}
