/*####----BROWSER SCROLL BAR----####*/
::-webkit-scrollbar {background: -webkit-linear-gradient(40deg , #0B0F24 , #0B0F24 , #0B0F24 100%)!important;}
::-webkit-scrollbar-thumb {background-color: #394264!important;border-radius: 16px!important;}
/*####----SELECT_DRAG----####*/
