css:
@-moz-document domain("altright.com") {
/*
* Developed hastily by Kayla
* Do whatcha want with it.
*/
/* general */
.site-container,
.top-bar{
    background:#1C1C1C;
}
.top-nav ul.menu li a:link,
.top-nav ul.menu li a:visited,
.top-nav ul.menu li a:active,
.main-nav #main-menu .menu > li > a:hover,
.main-nav #main-menu .menu > li > a:active {
   color: /*[[highlight]]*/ !important;
}
/* logo */
.header .logo img {
    filter: hue-rotate(195deg) invert(90%)
}
/* menu */
.main-nav #main-menu .menu > li > a {
    color:#ccc;
}
.header-wrap {
    background:#333;
}
.main-nav .header-inner {
    height: 60px;
    border:0;
}
/* posts n stuff */
.single-page .main .article-content {
   	color:#BFBFBF;
}
.post-title,
.s-post-nav .nav-btn h3,
.related-box ul li .post-title,
.article-content a,
.article-content a span {
    color: /*[[highlight]]*/ !important;
}
.entry-excerpt,
.article-content,
.post-excerpt p,
.post-teaser p,
.related-box .block-title,
.singletop .post-meta > div, .singletop .post-meta > div:after, .post-meta, .widget-posts-list .style-3 .post-meta > div, .sub-post .post-meta > div {
 	color:#999;   
}
.kid-author-box .author-info .kid-author-page-contact a:hover,
.error-number h1,
#kid-404-wrap .kid-error-title,
.page-404-wrap .redirect-home,
.article-content p a,
.read-more:hover,
.main-nav #main-menu .menu > li > a:hover,
.footer .kid-header .main-title h3,
.header-social li a:hover,
.tweet-bird,
.post-title:hover,
.classic-blog-style .post-cat a,
.large-blog-style .post-cat a,
.grid-1-type .post-cat a,
.module-main-slider .slider-wrap .slides .post-info .post-cat a,
.singletop .post-cat a,
.comment-navigation a:hover,
.post-teaser p a {
 	color:#ccc;   
}
/* captions */
.article-content .wp-caption .wp-caption-text,
.textwidget .wp-caption .wp-caption-text,
.article-content .gallery-caption,
.textwidget .gallery-caption {
	color:#999;
}
/* tags */
.s-tags a {
    background-color: #666;
    border: 1px solid transparent;
}
/* share */
.kid-share-box > span {
    color:#ccc;
}
/* author box */
.kid-author-box {
	border-color:#333;
}
.kid-author-box .author-info h3 {
 	color:#999;   
}
/* titles, category labels and sidebar headers */
.sidebar .kid-header .main-title h3,
.content-sb-section .kid-header .main-title h3,
.kid-header .main-title h3{
 	background:#333;
    color:#ccc;
}
/* disqus comments */
#disqus_thread {
	color:#ccc!important;
	background:#ccc;
	padding:2rem;
	border-radius:3px;
}
/* Font Awesome fix */
i.fa {
     font-style: normal;
}
/* pagination */
#pagination span,
#pagination a {
 	border-color:#333;   
}
/* footer */
.footer .widget_text .textwidget {
    color: #666;
}
.searchform-wrap > input {
 	background:#333;   
}
/* slider on media page */
.flexslider .slides {
 	background:#1c1c1c;   
}
/* forms */
.article-content input,
.textwidget input,
.article-content textarea,
.textwidget textarea {
    background: #333;
    color: #ccc; 
}
.article-content button,
.textwidget button,
.article-content input[type="button"],
.textwidget input[type="button"],
.article-content input[type="reset"],
.textwidget input[type="reset"],
.article-content input[type="submit"],
.textwidget input[type="submit"] {
    background: /*[[highlight]]*/ !important;
    border:0;
    border-radius:3px;
    color:#fff;
}
#kid-gallery-slider .flex-control-paging li a.flex-active,
.kid-carousel-wrap .slides .post-cat a,
.module-main-grid .post-cat a,
.module-post-two .large-post .post-cat a,
.module-post-three .large-post .post-cat a,
.type-in .post-cat a,
.kid-carousel-large-wrap .slides .post-cat a,
.rating-wrap,
h3.ticker-header,
.post-cat-main-slider,
.module-main-slider .carousel-ctrl .slides li.flex-active-slide,
.ajax-load-btn span,
.loadmore-button .ajax-load-btn,
.s-tags a:hover,
.post-page-links > span,
.post-page-links a span:hover,
#comment-submit,
.kid-review-box .kid-overlay span,
.kid-score-box,
#pagination .current,
.widget_archive ul li:hover,
.widget_categories ul li:hover,
.widget_tag_cloud a:hover,
.archive-share-but i:hover,
.widget .searchform-wrap .search-icon,
.flex-control-paging li a.flex-active,
.widget-social ul li:hover,
.footer .widget-social ul li:hover,
input[type="submit"]:hover {
   background: /*[[highlight]]*/ !important;
}
.article-content blockquote,
.textwidget blockquote,
#kid-gallery-slider .flex-control-paging li a.flex-active,
.loadmore-button .ajax-load-btn,
.widget_flickr li a:hover img,
.post-page-links > span,
.post-page-links a span:hover,
#comment-submit,
#pagination .current,
.widget_archive ul li:hover,
.widget_categories ul li:hover,
.widget_tag_cloud a:hover {
  border-color: /*[[highlight]]*/ !important;
}
}
