css:
@-moz-document domain("github.com") {
#js-pjax-container > div > div.col-3.float-left.pr-3 > div.user-profile-sticky-bar.js-user-profile-sticky-bar.is-stuck > div > span.d-table-cell.v-align-middle.lh-condensed.js-user-profile-following-mini-toggle > strong {
    color: #3a3a3a;
}
#readme h3,
.discussion-item .author,
.discussion-item .label-color a,
.follow-list .follow-list-name a,
.gh-header-meta .author,
.issues-listing a.btn.btn-primary.float-right,
.pinned-repo-item p,
.pl-e,
.pl-en,
.repository-content .content a,
a.btn,
a.css-truncate.css-truncate-target.notifications-repo-link,
article.markdown-body.entry-content,
span.blob-code-inner,
span.lang,
span.num.text-emphasized,
.numbers-summary .octicon,
span.percent,
strong.user-select-contain.gist-blob-name.css-truncate-target,
td.blob-code.blob-code-inner.blob-code-hunk,
textarea#commit-description-textarea,
time-ago,
.dashboard .news .alert .simple .title,
.dashboard .news .alert .octicon,
.dashboard .news .alert .time,
svg.octicon.octicon-rss.mr-1 {
    color: #FFF;
}
.Box {
    background-color: #24292e;
    border: none;
}
.Box-header--blue {
    background-color: #4CA4FF;
    border: none;
    color: #efefef;
}
.Box-row {
    border: none;
}
.Box-row--focus-gray.navigation-focus {
    background-color: #4c4c4c;
}
.CodeMirror {
    color: #dddddd;
}
.TableObject.gh-header-meta {
    border: none;
}
.blankslate {
    background-color: #262626;
    border: 1px solid #e5e5e5;
    border-radius: 3px;
    box-shadow: inset 0 0 74px rgba(0, 0, 0, 0.73);
    padding: 30px;
    position: relative;
    text-align: center;
}
.blob-code-addition .x {
    background-color: #34f634;
    color: #000;
}
.blob-code-addition,
.blob-num-addition {
    background-color: #439b43;
}
.blob-code-deletion .x {
    background-color: #ff0f0f;
    color: #FFF;
}
.blob-code-deletion,
.blob-num-deletion {
    background-color: #a53a3a;
}
.blob-code-hunk,
.blob-code-expandable {
    background-color: #444;
    border: none;
}
.blob-code-inner {
    color: #fff;
    font-size: 14px;
}
.blob-num-hunk,
.blob-num-expandable,
.blob-num .blob-num-expandable,
td.blob-code.blob-code-expandable {
    background-color: #444;
}
.blog-post-body h2 {
    color: white;
}
.blog-post-body p {
    color: #dedede;
}
.border-left,
.border-bottom,
.border-right,
.boxed-group > h3,
.boxed-group .heading,
.boxed-group-inner,
.code-list .file-box,
.timeline-comment-wrapper.js-comment-container,
span.timeline-comment-label.member-comment.tooltipped.tooltipped-multiline.tooltipped-s,
.news .alert {
    border: none;
}
.border-right.border-bottom.border-left {
    background-color: #3e3e3e;
}
.box-body-row,
table.files td {
    border-top: none;
}
.box-body-row--highlight .box-row-link {
    color: #fff;
    text-decoration: none;
}
.box-body-row--highlight.navigation-focus {
    background-color: #4d4d4d;
}
.boxed-group .counter {
    background-color: #404040;
    color: #fff;
}
.boxed-group > h3,
.boxed-group .heading {
    background-color: #4ca4ff;
}
.breadcrumb strong.final-path,
.discussion-item-header,
.header-logo-invertocat,
.header-nav-link,
.repo-list-description {
    color: #fff;
}
.btn-primary {
    color: #fff;
    text-shadow: none;
}
.closed-banner,
thead {
    display: none;
}
.cm-s-github .cm-string {
    color: #469aff;
}
.cm-s-github .codemirror-gutters {
    background: #222;
}
.cm-s-github.codemirror {
    background: #222;
    font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
    font-size: 12px;
    line-height: 18px;
}
.code-list .code-list-item + .code-list-item {
    border-top: none;
    margin-bottom: 10px;
    margin-top: 30px;
    padding-top: 20px;
}
.code-list .divider .blob-num,
.code-list .divider .blob-code {
    background-color: #262626;
    cursor: default;
    padding-bottom: 0;
    padding-top: 0;
}
.code-list .language {
    color: #767676;
}
.codesearch-results .repo-list-name em,
.codesearch-results .repo-list-description em,
.wiki-list em,
.code-list em {
    background-color: transparent;
    border-radius: 3px;
    color: #ffff63;
    font-style: normal;
    font-weight: 400;
}
.column.three-fourths.codesearch-results {
    background-color: #24292e;
    margin-left: 2%;
    padding: 25px;
}
.commit-author-section {
    color: #7d7d7d;
}
.commit-comments-heading {
    margin: 35px 0 0;
}
.commit-desc + .commit-branches {
    border-top: solid 1px #333;
}
.commit-desc pre,
.follow-list-info span.css-truncate.css-truncate-target {
    color: grey;
}
.commit-sha,
.outline-box.outline-box-highlighted.empty-repo-setup-option h3,
.outline-box.outline-box-highlighted.empty-repo-setup-option p,
.preview-content,
a.btn.btn-sm,
a.css-truncate-target.js-notification-target.js-navigation-open.list-group-item-link,
span.label-color > a,
.css-truncate.css-truncate-target,
.css-truncate .css-truncate-target {
    color: #000;
}
.commit-tease {
    background-color: #222;
    border: none;
    color: gray;
    padding: 20px;
    position: relative;
}
.commit-tease span.user-mention {
    color: #cecece;
}
.commits-listing::before {
    background-color: rgba(239, 241, 243, 0.4);
    bottom: 0;
    content: "";
    display: block;
    left: 15px;
    position: absolute;
    top: 0;
    width: 1px;
    z-index: -1;
}
.container {
    width: 80vw;
}
.container.repohead-details-container {
    margin-bottom: 30px;
}
.copyable-terminal.js-zeroclipboard-container {
    background-color: transparent;
    border-radius: 3px;
    padding: 40px 55px 10px 10px;
    position: relative;
}
.dashboard blockquote {
    color: #dedede;
}
.discussion-item .label-color {
    color: #FFF;
    /*    background-color: #6cc644;*/
}
.discussion-item .renamed-was,
.discussion-item .renamed-is {
    color: #bebfc0;
    font-weight: 100;
}
.discussion-sidebar .discussion-sidebar-item {
    border-top: 1px solid #464646;
}
.discussion-sidebar-item.sidebar-assignee.js-discussion-sidebar-item {
    border: none;
}
.discussion-timeline-actions {
    border: none;
    border-radius: 5px;
}
.edit-comment-hide,
div.header.header-logged-in.true,
table.highlight {
    background-color: #262626;
}
.exploregrid-item {
    background-color: #171616;
    border-radius: 5px;
    box-shadow: 4px 6px 12px 0 rgb(0, 0, 0);
    /* border: 5px solid rgb(250, 250, 250); */
}
.exploregrid-item-title {
    color: white;
}
.exploregrid-item:hover {
    box-shadow: 4px 6px 12px 4px rgb(0, 0, 0);
}
.exploregrid-item:hover .exploregrid-item-title {
    color: white;
}
.file {
    border: 1px solid #000;
}
.file-diff-split .empty-cell {
    background-color: #333;
    cursor: default;
}
.file-header {
    background-color: #222;
    border: none;
    color: #FFF;
}
.file-wrap {
    border: none;
    margin-bottom: 20px;
}
.follow-list .follow-list-info {
    color: gray;
}
.full-commit {
    background: #222;
    border: none;
    border-radius: 3px;
    margin: 40px 0;
}
.full-commit .commit-meta {
    background: #444;
    border: none;
}
.full-commit .sha-block > .sha {
    color: #9c9c9c;
}
.full-commit p.commit-title {
    color: #6ed1ff;
    font-size: 19px;
    margin: 0 0 15px;
}
.gh-header-meta {
    border-bottom: 1px solid #6a6a6a;
    color: #cacaca;
}
.gh-header-meta relative-time {
    color: white;
}
.markdown-body .highlight {
    margin-bottom: 25px;
}
.markdown-body code,
.markdown-body tt {
    background-color: rgba(28, 32, 35, 0.32);
}
.markdown-body h1,
.markdown-body h2,
.markdown-body h3,
.markdown-body h4,
.markdown-body h5,
.markdown-body h6 {
    font-weight: 600;
    line-height: 1;
    margin-bottom: 6px;
    margin-top: 40px;
}
.markdown-body h2 {
    border-bottom: 1px solid #6a6a6a;
}
.markdown-body hr {
    background-color: #616161;
    height: .1em;
}
.markdown-body pre > code {
    background-color: transparent;
}
.markdown-body table th,
.markdown-body table td {
    border: none;
    padding: 10px 16px;
}
.markdown-body table tr {
    background-color: #2d2d2d;
    border: none;
}
.markdown-body table tr:nth-child(2n) {
    background-color: #1f1f1f;
}
.new-discussion-timeline .release-meta a {
    color: white;
}
.new-discussion-timeline a.js-selected-navigation-item.selected.subnav-item {
    color: white;
}
.new-discussion-timeline a.js-selected-navigation-item.subnav-item {
    border: 1px solid #404a52;
}
.new-discussion-timeline ul > li:nth-child(1) > a span.css-truncate-target {
    color: white;
}
.octofication .message {
    border: solid 1px #4ca4ff;
}
.octofication .message p {
    color: #deddd8;
}
.overall-summary {
    border: none;
    margin: 0;
    position: relative;
}
.overall-summary.overall-summary-bottomless {
    background-color: #222222;
    padding-top: 10px;
}
.pagehead {
    margin-bottom: 40px;
}
.pl-c {
    color: #4e5d4e;
}
.pl-c1,
.pl-s .pl-v {
    color: #56aaff;
}
.pl-k {
    color: #ff5d51;
}
.pl-s,
.pl-pds,
.pl-s .pl-pse .pl-s1,
.pl-sr,
.pl-sr .pl-cce,
.pl-sr .pl-sre,
.pl-sr .pl-sra {
    color: #ffd45c;
}
.pl-smi,
.pl-s .pl-s1 {
    color: #31c67b;
}
.profile-timeline-year-list .filter-item.selected {
    color: white;
}
.profile-timeline-year-list.bg-white.col-2.float-right.pl-5.js-profile-timeline-year-list.js-sticky {
    background-color: #252b30;
}
.readme .markdown-body,
.readme .plain {
    background-color: #444;
    border: none;
    text-align: justify;
}
.repo-list-item {
    border-bottom: 1px solid #4d4d4d;
}
.repo-list-meta {
    color: #4b4b4b;
}
.repo-list-stats {
    color: #fff;
    float: right;
    font-size: 12px;
    font-weight: 700;
    margin-top: 6px;
}
.repo-list-stats .repo-list-stat-item {
    color: #5b5b5b;
    display: inline-block;
    margin-left: 8px;
    text-decoration: none;
}
.repository-content .repository-meta-content a,
p a,
nav a,
ul.site-footer-links a {
    color: #358dff;
}
.repository-content a.btn {
    color: #4f96ff;
}
.repository-content pre {
    border-radius: 2px;
    padding: 5px;
}
.repository-lang-stats ol.repository-lang-stats-numbers li > a,
.repository-lang-stats ol.repository-lang-stats-numbers li > span {
    font-weight: 300;
}
.repository-lang-stats-graph {
    border: none;
}
.repository-meta {
    color: #fff;
    font-size: 20px;
    margin: 0 0 10px;
}
.showcase-page-header {
    border: none;
}
.showcase-page-pattern {
    display: none;
}
.sidebar-labels .labels .label {
    color: white;
}
.site-footer {
    border-top: 1px solid #6a6a6a;
}
.stats-switcher-viewport {
    height: 82px;
    overflow: hidden;
}
.table-list-header {
    background-color: #222;
    border: none;
    border-radius: 3px 3px 0 0;
    margin-top: 20px;
    position: relative;
}
.task-progress .progress-bar .progress {
    background-color: #6cc644;
    display: block;
    height: 100%;
}
.text-gray-dark {
    color: #fff;
    font-size: 1.2em;
    font-weight: 300;
}
.thread-subscription-status {
    background-color: transparent;
    border: none;
    color: #767676;
}
.three-fourths {
    width: 73%;
}
.timeline-comment {
    border: 1px solid rgb(91, 93, 95);
}
.timeline-comment-wrapper.timeline-new-comment.js-comment-container {
    border: none;
    margin: 0 auto;
    padding: 0;
}
.wiki-wrapper .wiki-auxiliary-content {
    background: #262626;
}
.wiki-wrapper hr {
    margin: 40px 0;
}
a,
a.link-gray-dark.mt-3.d-inline-block,
.markdown-body h1 .octicon-link,
.markdown-body h2 .octicon-link,
.markdown-body h3 .octicon-link,
.markdown-body h4 .octicon-link,
.markdown-body h5 .octicon-link,
.markdown-body h6 .octicon-linksvg.octicon.octicon-link {
    color: #4ca4ff;
}
a.btn.btn-outline {
    background-color: #4078c0;
}
a.btn.btn-outline.tooltipped.tooltipped-sw {
    color: white;
}
a.commit-tease-sha {
    color: #3487f2;
}
a.label.label-link {
    color: #000;
    font-weight: lighter;
}
a.link-gray-dark.no-underline.h4.js-navigation-open {
    color: #dadada;
    margin-right: 10px;
}
a.sha.btn.btn-outline.BtnGroup-item {
    color: white;
}
a.subnav-item {
    background-color: #2d2d2d;
    border: none;
}
a.subnav-item.selected {
    color: white;
}
a.title-link {
    color: #dadada;
    font-weight: 100;
}
a.user-mention {
    color: #e2e2e2;
}
article p {
    font-weight: 100;
}
article.markdown-body a {
    color: #1a9aff;
}
article.markdown-body a:hover {
    color: #ff17ce;
    text-decoration: none;
}
article.markdown-body.entry-content {
    font-size: 17px;
}
body {
    background-color: #252b30;
    color: #fff;
}
div.col-3.float-left.pr-3 > div.user-profile-sticky-bar.js-user-profile-sticky-bar.is-stuck.is-follow-stuck > div > span.d-table-cell.v-align-middle.lh-condensed.js-user-profile-following-mini-toggle > strong {
    color: #358dff;
}
div.edit-comment-hide > table > tbody > tr > td {
    color: #c5c5c5;
}
div[role="main"] {
    color: #d4d4d4;
}
h1.gh-header-title {
    font-weight: 200;
}
h3.profile-timeline-month-heading.bg-white.d-inline-block.h6.pr-2.py-1 {
    background: #252b30;
}
h4.f4.mb-2.text-normal {
    color: #333333;
}
li.commit.commits-list-item.table-list-item.js-navigation-item.js-details-container.Details.js-socket-channel.js-updatable-content.navigation-focus {
    background: #535353;
}
li.pinned-repo-item.p-3.mb-3.border.border-gray-dark.rounded-1.public.fork {
    background-color: #262626;
    border: none;
    border-radius: 6px;
}
li.pinned-repo-item.p-3.mb-3.border.border-gray-dark.rounded-1.public.source {
    background-color: #191818;
    border: none;
    border-radius: 6px;
}
li.vcard-detail.pt-1.css-truncate.css-truncate-target {
    color: white;
}
p.org-description {
    color: #444444;
}
p[itemprop~="description"] {
    color: #e6e6e6;
}
pre {
    background-color: #3e3e3e;
}
pre.copyable-terminal-content.js-zeroclipboard-target {
    padding: 16px;
}
rect.day[fill="#eeeeee"] {
    fill: #384046;
}
span.labels a {
    color: white;
}
span.timeline-comment-label {
    background-color: #15c809;
    color: #FFF;
}
strong {
    font-weight: 400;
}
svg.octicon.octicon-git-commit {
    background: #252b30;
    color: #ffffff;
    margin-right: 17px;
}
table.files {
    background: #282828;
}
table.files td.age {
    padding-right: 20px;
}
table.files td.icon {
    color: #bbb;
    padding-left: 20px;
    padding-right: 5px;
}
table.files td.icon .octicon-file-directory {
    color: rgb(0, 118, 255);
}
table.files td.message a,
.commit-tease > a.message {
    color: #888;
}
table.files tr.navigation-focus td {
    background: #3a3a3a;
}
td.blob-num {
    border: none;
    color: #eee;
    font-size: 14px;
}
td.js-file-line {
    opacity: 0.8;
}
td.js-line-number {
    opacity: 0.05;
}
.profile-timeline-year-list {
    background-color: #252b30 !important;
}
h3.profile-timeline-month-heading {
    background-color: #252b30 !important;
}
.profile-timeline-month-heading .text-gray {
    color: white !important;
}
.profile-timeline-card {
    border: none !important;
}
.profile-timeline-card-wrapper {
    border: none;
}
li.pinned-repo-item {
    border: none !important;
}
.user-profile-nav {
    border: none;
}
.underline-nav-item {
    border-bottom: 5px solid transparent;
}
h2.f4.mb-2.text-normal {
    margin-bottom: 15px !important;
    margin-top: 35px !important;
}
.user-profile-sticky-bar::after {
    border: none;
}
li[style="background-color: #196127"] {
    background-color: #25ea25 !important;
}
li[style="background-color: #239a3b"] {
    background-color: #28cc28 !important;
}
rect[fill="#196127"] {
    fill: #25ea25;
}
rect[fill="#239a3b"] {
    fill: #28cc28;
}
span.lang {
    color: white !important;
}
}
