css:
@-moz-document url-prefix("http://wololo.net/talk/") {
  .table-primary thead th {
    background-color: #444 !important;
  }
  
  .search-box form input, .pagination span a, li.pagination span a, .separator:before, .separator i, .content a, .topstyle i {
    background-color: #333 !important;
  }
  
  .subheader, .table-primary tbody td, .btn-default, .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus, .content blockquote, .codebox, .dropdown-menu li:hover {
    background: #222 !important;
  }
  
  body, html, .nav-icon table, .sub-breadcrumb, .table-primary tbody tr:nth-child(2n+1) td, .table-primary tbody tr:nth-child(2n+1) th, .well, footer #footerContent, .pagination>li>a, .pagination>li>span, .dropdown-menu {
    background: #111 !important;
  }
  
  .submenu {
    background: rgba(17,17,17, 0.97);
  }
  
  header, #content-forum, .form-control {
    background: #000 !important;
  }
  
  #wrap {
    background: none;
  }
  
  
  /* colors */
  a:visited {
    color: #999;
  }
  
  .topstyle i {
    color: #666;
  }
  
  .btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open>.dropdown-toggle.btn-default, .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus, .pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus, .separator i, .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, h3 {
    color: #aaa;
  }
  
  .form-control, .input-icon.left i, .postbody .content, .content a {
    color: #bbb !important;
  }

  a:link, .search-box form input, body, .pagination>li>a, .pagination>li>span, .user-profile-output, .postlink:hover, .dropdown-menu>li>a {
    color: #ccc;
  }
  
  
  /* gradients */
  .tabs-nav, .crumbs {
   background: -moz-linear-gradient(center top , #000 0%, #111 100%) repeat scroll 0 0 transparent !important;
  }

  
  /* borders */
  .topstyle i {
    border-color: #444 !important;
  }
  
  .search-box form input, .table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td, .panel-default, .btn-default, .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus, .user-profile-tab, .user-profile-row, .codebox, footer .navList li a {
    border-color: #333 !important;
  }
  
  .submenu, .subheader, footer, .form-control, .pagination>li>a, .pagination>li>span, h3, .pagination span a:hover, .content div, .content blockquote, blockquote, .dropdown-menu, .dropdown-menu li {
    border-color: #222 !important;
  }
  
  .nav-icon table, .tabs-nav, .crumbs, .well {
    border-color: #111 !important;
  }
  
  .postlink, .postlink:hover {
    border-color: #aaa !important;
  }
  
  #wrap, .avatar-frame img {
    border: none;
  }
  
  
  /* box shadows */
  .table-bordered {
    box-shadow: 0 3px 0 0 #333;
  }
  
  .well {
    box-shadow: 0 3px 0 0 #222;
  }
  
  .tabs-nav, .crumbs {
    box-shadow: 0 1px 0 #111;
  }
  
  #content-forum {
    box-shadow: 0 0 14px #000;
  }
  
  #wrap {
    box-shadow: 0 0 4px rgba(0,0,0,0.5);
  }
  
  
  /* text shadows */
  .sub-crumb, .sub-crumb li, .dropdown-menu li:hover {
    text-shadow: none;
  }
}
