@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.svg-logo,
.logo .png-logo,
.logo:hover .opacity-switch .preload-container {
    background: url('http://musicfeedsthesoul.rocks/images/asd4.png')!important;
    /* background-size: contain !important; */
    
    width: 405px !important;
    /* float: left !important; */
}
.logo:hover .opacity-switch .png-logo {
    opacity: 1;
}
.logo {
    width: 405px !important;
}
.opacity-switch .preload-container {
    display: none;
}
.follow-text {
    color: #A38AAD!important;
}
/** New Post Button Color **/

.compose-button {
    fill: #ffffff!important;
}
/** New Post Button Stroke Color **/

path {
    stroke: rgba(255, 255, 255, 0)!important;
}
.tab.iconic:nth-child(1) > .tab_anchor::before,
.tab.iconic:nth-child(2) > .tab_anchor::before,
.tab.iconic:nth-child(3) > .tab_anchor::before,
.tab.iconic:nth-child(4) > .tab_anchor::before,
.tab.iconic:nth-child(5) > .tab_anchor::before,
.tab.iconic:nth-child(6) > .tab_anchor::before {
    content: none!important;
}
.l-header-container--refresh .tab_bar .tab_nav_account .tab_anchor i:before,
.l-header-container--refresh .tab_bar .tab_nav_account .tab_anchor .icon_arrow_carrot_down {
    display: none;
}
.l-header-container--refresh .l-header {
    min-width: 1200px !important;
    width: 100% !important;
    max-width: 1200px !important;
    height: 55px !important;
}
/* negative moves to the left */

.tab_bar .tab_home .tab_anchor {
    background-position: 5px 0px
}
.tab_bar .tab_discover .tab_anchor {
    background-position: -47px 0px
}
.tab_bar .tab_inbox .tab_anchor {
    background-position: -102px 0px
}
.tab_bar .tab_messaging .tab_anchor {
    background-position: -163px 0px
}
.tab_bar .tab_activity .tab_anchor {
    background-position: -190px 0px
}
/* XKit Logo Goes Here */

.tab_bar .tab_nav_account .tab_anchor {
    background-position: -311px 0px
}
.tab_bar .iconic .tab_anchor {
    background-image: url('http://musicfeedsthesoul.rocks/images/ryomasqdashcontrolmale2.png') !important;
    background-size: 353px 35px;
    background-repeat: no-repeat;
    cursor: pointer;
    width: 29px;
}
/* XKit */

#xkit-control a.tab_anchor,
#new-xkit-control > a,
#new-xkit-control.halloween > a {
    background: url('http://musicfeedsthesoul.rocks/images/ryomasqdashcontrolmale2.png') !important;
    background-position: -250px 0px !important;
}

.xkit-reply-button-pn:hover {
    background-color: #ff8f8f  !important;
}


body {
    background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/bg1gradualtransparent8.png') repeat center center fixed;
    -webkit-background-size: contain;
    -moz-background-size: contain;
    -o-background-size: contain;
    background-size: contain;

}

body,
a,
.tab_bar .iconic .tab_anchor, .xkit-reply-button-pn.xkit-notes-new-style-fix  {
    cursor: url('http://musicfeedsthesoul.rocks/images/17456.png'), progress !important;
}
#dashboard_index,
#dashboard_edit_post,
#dashboard_submissions,
#dashboard_drafts,
#dashboard_post_queue,
#dashboard_followers,
#dashboard_members,
#dashboard_messages_inbox,
#tagged_actions_display,
#settings_actions_tumblelog,
#dashboard_useraction_followers,
#activity_actions_index,
#search_actions_search,
#dashboard_posts_likes,
#dashboard_group_members,
#dashboard_useraction_following,
.identity {
    background: url(http://musicfeedsthesoul.rocks/images/tumblrbg1rymsk-1a.png) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    /* background-size: 100% 100%; */
    
    width: 100%;
    height: 100%;
}
.identity-refresh .l-container {
    padding-top: 80px;
}
#content {  
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    /* background-image: none !important; */
    background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/bg1gradualtransparent8.png') top left no-repeat!important;
}
* {
    text-shadow: none !important;
    color: #641214 !important;
}
