div.l-content.l-content.l-content {
    background-color: transparent !important; }

/* l-content { background-image: none !important; } */

DIV.l-header-container {
    background: rgba(163, 49, 77, 1);
    background: -moz-linear-gradient(left, rgba(163, 49, 77, 1) 0%, rgba(255, 218, 153, 1) 31%, rgba(255, 218, 153, 1) 45%, rgba(209, 166, 163, 1) 57%, rgba(143, 71, 66, 1) 100%);
    background: -webkit-gradient(left top, right top, color-stop(0%, rgba(163, 49, 77, 1)), color-stop(31%, rgba(255, 218, 153, 1)), color-stop(45%, rgba(255, 218, 153, 1)), color-stop(57%, rgba(209, 166, 163, 1)), color-stop(100%, rgba(143, 71, 66, 1)));
    background: -webkit-linear-gradient(left, rgba(163, 49, 77, 1) 0%, rgba(255, 218, 153, 1) 31%, rgba(255, 218, 153, 1) 45%, rgba(209, 166, 163, 1) 57%, rgba(143, 71, 66, 1) 100%);
    background: -o-linear-gradient(left, rgba(163, 49, 77, 1) 0%, rgba(255, 218, 153, 1) 31%, rgba(255, 218, 153, 1) 45%, rgba(209, 166, 163, 1) 57%, rgba(143, 71, 66, 1) 100%);
    background: -ms-linear-gradient(left, rgba(163, 49, 77, 1) 0%, rgba(255, 218, 153, 1) 31%, rgba(255, 218, 153, 1) 45%, rgba(209, 166, 163, 1) 57%, rgba(143, 71, 66, 1) 100%);
    background: linear-gradient(to right, rgba(163, 49, 77, 1) 0%, rgba(255, 218, 153, 1) 31%, rgba(255, 218, 153, 1) 45%, rgba(209, 166, 163, 1) 57%, rgba(143, 71, 66, 1) 100%);
   /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#a3314d', endColorstr='#8f4742', GradientType=1); */
}
#right_column {
    width: 250px;
    margin-right: 80px !important;
     padding: 20px 0 !important; 
    /* background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/bg1gradualtransparent8.png') top left no-repeat!important; */
    background: none !important;
}
#right_column:after,
#right_column,
#left_column {
   padding: 20px 0 !important; 
   /* background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/bg1gradualtransparent8.png') top left no-repeat!important; */
    background: none !important;
}

div.l-content.clearfix { background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/bg1gradualtransparent8.png') top left no-repeat!important; }

#right_column:after,
/* #right_column, */
/* #left_column, */
.post_content,
.post_tags:after,
.section.first.divider,
blockquote pre,
#tumblr_radar {
    background: none !important;
}
#right_column a#dashboard_switch_blog_menu_current {
    background: #f9f9f9 !important;
    opacity: 0.90 !important;
    border: 0px !important;
}
#right_column:hover {
    display: block;
    border-bottom: none !important;
    border-bottom: 0px !important;
  /*  background: none!important; */
   /* background-image: none !important; */
    border: none !important;
}
/* .l-header-container--refresh .l-header{max-width:1100px} */

div.l-header .selection_nipple {
    border-bottom: none !important
}
.tab-post-type .post-type-icon {
    background-image: url(http://musicfeedsthesoul.rocks/wp-content/uploads/menu1-ryo-masq.png);
}
.glass {
    background: #986662 !important;
    opacity: .8;
    box-shadow: 2px 2px 15px 0 rgba(241, 218, 224, 0.62)
}
.post-type-name-span {
    text-shadow: none !important;
    color: #ffeedd !important;
}
.l-header > .compose-button:hover {
    opacity: 1
}
.tab.iconic > .tab_anchor > I::before,
.tab.iconic > .tab_anchor::before {
    color: #afafaf!important
}
DIV.tab.iconic .tab_notice {
    border-color: #fff
}
DIV.tab.iconic .tab_notice .tab_notice_value {
    color: #fff
}
div.l-header .tab .tab_notice,
.popover_blogs .tab_notice,
