    background-color: #ffda99;
}
DIV.tab.iconic .tab_notice {
    border-color: #ff8f8f !important;
}
.compose-button {
    fill: #ffda99 !important;
}
.tab_bar .tab_anchor {
    padding: 0 14px
}
.tab-bar-container .tab_notice {
    right: 34px;
    left: auto;
    top: -4px;
}
.popover_header {
    background: #fff2f2;
    border-bottom: 1px solid #efdadd
}
LI.popover_header {
    background-color: #FFB4E0;
    color: #CF358E
}
LI.popover_menu_item:hover,
LI.popover_menu_item.keyboard_focus {
    background-color: #ffdff1
}
.popover_menu_item a {
    color: #6d6c6c!important
}
.pop-menu {
    color: #777
}
.pop-menu .item-option:hover {
    background-color: #ffebf0
}
.blog_menu .selected_blog #open_blog_link {
    color: rgba(255, 255, 255, 0.60)
}
