    position: absolute;
    top: 48px!important;
    left: 422px!important;
    width: 80%;
    max-width: 290px;
    color: #8f4742 !important;
}
.ui_search.active .search_form_field:before,
.ui_search.selected .search_form_field:before {
    color: #8f4742 !important;
}
.result_link:hover:before {
    color: #000000 !important;
}
#search_query {
    background-color: #8f4742;
    box-shadow: 2px 2px 7px rgba(232, 225, 225, 0.81) inset;
    border: 1px solid #f9dee4;
}
.ui_search .search_form_field:before {
    color: #8f4742 !important;
}
.search_controls .search_control_button,
.search_controls .search_control_link {
    color: #8f4742 !important;
}
.search_control.post_layout .icon:hover {
    color: #8f4742 !important;
}
.search_query {
    color: #8f4742 !important;
}
.popover_menu_item.keyboard_focus,
.popover_menu_item:hover {
    background: #fff5f7 !important;
}
.l-container.l-container--flex .l-content {
    border-radius: 0;
    box-shadow: none;
    border: 0;
    background: none!important;
    padding: 0!important
}
.search_sub_header .search_control.related_searches .search_control_link,
.search_sub_header .search_control.related_searches {
    color: #8f4742 !important;
}
.search_sub_header .search_term_heading {
    color: #8f4742 !important;
}
.ui_search {
    max-width: 302px !important;
    padding-left: 20px !important;
    color: #8f4742 !important;
}
.post_brick {
    box-shadow: 1px 1px 1px 0 #eadbdb
}
.new_post_label {
    background: none !important;
}
.post.new_post {
    background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/tumblrryomaskicons.png') center center no-repeat !important;
}
a.new_post_label > i {
    height: 90px;
    margin-top: 5px;
}
.new_post_label {
    padding: 71px 1px 8px 0 !important;
    font-size: 0pt !important;
    text-transform: lowercase;
}
.new_post_label
/*_icon*/

> I {
    background: url('http://musicfeedsthesoul.rocks/wp-content/uploads/transparent.png') center center no-repeat !important;
    z-index: -1;
}
.new_post_label > I {
    color: transparent !important;
    transition: background-position .08s linear !important;
}
#new_post_label_text > I {
    background-position: 2px -5px !important;
}
#new_post_label_photo > I {
    background-position: -73px -5px !important;
}
#new_post_label_quote > I {
    background-position: -146px -5px !important;
}
#new_post_label_link > I {
    background-position: -220px -5px !important;
}
#new_post_label_chat > I {
    background-position: -297px -5px !important;
}
#new_post_label_audio > I {
    background-position: -373px -5px !important;
}
#new_post_label_video > I {
    background-position: -450px -5px !important;
}
.post {
    background: #fdf0ee !important;
}
.post_avatar {
    background-color: rgba(0, 0, 0, 0) !important;
}
.post_avatar {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background: none !important;
    background-color: rgba(0, 0, 0, 0) !important;
}
.post_avatar .post_avatar_link {
    border-radius: 0px !important;
}
.post_avatar_link {
    opacity: 0.80 !important;
    filter: alpha(opacity=80)!important;
    background-color: rgba(0, 0, 0, 0) !important;
}
.post_avatar_link:hover {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background-color: rgba(0, 0, 0, 0) !important;
}
.post_avatar:before {
    position: absolute;
    content: url(http://musicfeedsthesoul.rocks/wp-content/uploads/Frame-sampleee.png);
    top: 0 !important;
    bottom: 0 !important;
    right: 0;
    left: 0;
    z-index: 1;
}
.post .post_footer {
    border-top: none !important;
}
.messaging-inbox .inbox-conversations .inbox-conversation:hover {
