css:
@-moz-document domain("news.ycombinator.com") {
.comhead {
    font: 13px/1.5 'helvetica neue', helvetica, arial !important;
    margin-left: 3px !important;
}
.comment {
    display: block !important;
    font: 13px/1.5 'helvetica neue', helvetica, arial !important;
    text-align: justify !important;
    width: 98% !important;
}
.comment pre {
    overflow: visible !important;
}
.subtext {
    font-size: 10px !important;
    line-height: 1 !important;
}
.subtext a:hover {
    border-bottom: 1px solid !important;
    text-decoration: none !important;
}
.subtext a:nth-of-type(2) {
    color: #BB5500 !important;
}
.title {
    font-size: 19px !important;
    font-weight: 400 !important;
}
.title .comhead {
    font-size: 12px !important;
}
.title a:hover {
    border-bottom: 1px solid !important;
}
.title a:visited {
    color: #CCC !important;
}
.votearrow {
    margin-top: 7px !important;
}
.yclinks {
    color: transparent !important;
}
.yclinks a {
    font: 13px 'helvetica neue', helvetica, arial !important;
    padding: 0 4px !important;
}
[align="right"].title {
    color: #999 !important;
    padding: 0 4px 0 0 !important;
}
[align="right"].title + td {
    padding: 1px 5px 0 0 !important;
    position: relative !important;
    width: 20px !important;
}
[id^="up_"] img {
    padding: 3px !important;
}
a {
    color: orange !important;
}
a.hnuser {
    color: #525252 !important;
}
a.togg {
    color: #525252 !important;
}
a:link {
    color: #ff944d !important;
    text-decoration: none !important;
}
a:visited {
    color: #b36633 !important;
    text-decoration: none !important;
}
a[href^="reply?id"] {
    background-color: rgba(100, 100, 100, 0.05) !important;
    border-radius: 3px !important;
    color: #666 !important;
    float: right !important;
    font-size: 12px !important;
    padding: 3px 15px !important;
    text-decoration: none !important;
}
a[href^="user?id"] {
    font-weight: 500 !important;
    padding: 0 !important;
}
body {
    background: #fff !important;
    margin: 0 !important;
    padding: 0 !important;
}
body > center > table {
    background-color: transparent !important;
    position: relative !important;
    width: 990px !important;
}
/* header */
/* position td */
/* vote td */
/* search */

font {
    font: 13px/1.5 'helvetica neue', helvetica, arial !important;
}
font[color="#000000"] {
    color: #222 !important;
}
form[action*="hnsearch.com"] {
    color: transparent !important;
}
input,
textarea {
    background-color: #FFF !important;
    border: 1px solid #DDD !important;
    font: 13px 'helvetica neue', helvetica, arial !important;
    padding: 10px !important;
}
input:focus,
textarea:focus {
    border: 1px solid #DDD !important;
    outline: none !important;
}
input[name="q"] {
    color: #666 !important;
    left: 150px !important;
    position: absolute !important;
    top: 12px !important;
    width: 190px !important;
}
input[type="submit"],
input[type="reset"],
input[type="button"] {
    background-color: rgba(100, 100, 100, 0.1) !important;
    border: none !important;
    box-shadow: none !important;
    color: #444 !important;
}
span.age a {
    color: #525252 !important;
    opacity: 0.3 !important;
}
span.sitestr {
    color: #d4d4d4 !important;
}
td > center > form {
    display: none !important;
}
td > center > span {
    display: none !important;
}
td,
.pagetop {
    font: 13px/1.5 'helvetica neue', helvetica, arial !important;
}
td.default {
    background-color: rgba(0, 0, 0, 0.03) !important;
    padding: 1rem !important;
}
td.default div {
    color: #634863 !important;
}
td.subtext a {
    color: #999999 !important;
}
td[bgcolor="#ff6600"] {
    background-color: transparent !important;
    padding: 13px 0 10px 0 !important;
}
td[bgcolor="#ff6600"] * {
    vertical-align: middle !important;
}
td[bgcolor="#ff6600"] .pagetop a:not([href="news"]) {
    display: inline-block !important;
    padding: 4px !important;
}
td[bgcolor="#ff6600"] a {
    color: #666 !important;
}
td[bgcolor="#ff6600"] b a {
    color: #444 !important;
    display: inline-block !important;
    font-size: 20px !important;
    font-weight: normal !important;
    letter-spacing: -1px !important;
    margin-top: -7px !important;
    width: 109px !important;
}
td[bgcolor="#ff6600"] font {
    color: #999 !important;
}
td[bgcolor="#ff6600"] img {
    height: 16px !important;
    opacity: 0.7 !important;
    width: 16px !important;
}
td[bgcolor="#ff6600"] td:nth-of-type(2) .pagetop {
    color: transparent !important;
}
td[bgcolor="#ff6600"] td:nth-of-type(2) .pagetop b {
    color: #666 !important;
}
tr > td > a > img {
    margin-top: -7px !important;
}
tr[style="height:5px"] {
    height: 25px !important;
}
}
