css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://slickdeals.net/f/") {
	.post {
		padding-bottom: .3em;
		margin-bottom:  .3em;
		border-bottom: none !important;
	}
	
	.postTextTop { margin-bottom: 0 }
	.postTextBottom { margin-top: .2em }
	
	/*[[default-avatars]]*/
/*[[user-details]]*/
  
	.quotebox_content {
		padding-top: .2em;
		padding-bottom: .2em;
	}
/*[[quotes]]*/	
}

style_setting_options.value:
.quotebox_content:not(:hover) {
	max-height: 3.3em;
	overflow: hidden;
	text-overflow: ellipsis;
}
.quotebox_content:not(:hover) .quotebox_header { display: none }
